using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;

namespace namesOfAllah.Models
{
    public class Names
    {
        public Dictionary<string, Name> ListOfNames = null;
        public Names()
        {
            ListOfNames = new Dictionary<string, Name>();

            ListOfNames["Первый, Последний, Высочайший, Ближайший"] = new Name() { One_Name = "Первый, Последний, Высочайший, Ближайший", Description = "Всевышний Аллах сказал: Он — Первый и Послед­ний, Высочайший и Бли­жайший. Сура 57 «Железо»,аят 3. Эти четыре благословенных имени были ясно и всесторонне разъяснены Пророком(да благословит его Аллах и приветствует) в одной из молитв: «О Аллах, Ты — Первый, и нет ничего прежде Тебя, и Ты — Последний, и нет ничего после Тебя. Ты — Высочайший, и нет никого выше Тебя, и Ты — Ближайший, и нет никого ближе Тебя» [Муслим, т. 4, с. 2084]. Он указал на величественные значения каждого имени, отвергнув всё, что противоречит и противопоставляется им.Задумайся же над этими славными значениями, указывающими на то, что только Великий Господь обладает абсолютным совершенством и полностью объемлет время (первые два имени) и пространство (последниедва имени). Первый означает, что всё остальное некогда не существовало, а потом возникло. Это обязывает рабов замечать милость Господа во всех духовных и материальных благах, ибо только Он создаёт причины для их обретения. Последний означает, что Аллах — цель, к которой стремятся творения, обоготворяя Его, желая приблизиться к Нему и испытывая страх перед Ним. Высочайший указывает на величие божественных качеств и на то, что перед Его величием меркнет всё сущее и любые качества, какими бы славными они ни были. Ближайший указывает на то, что Ему ведомы любые тайны, сокровенные мысли и намерения. Он доподлинно знает обо всём в мельчайших подробностях. Это имя также свидетельствует о совершенстве Его близости к рабам. Имена Высочайший и Ближайший не противоречат друг другу, ибо ничьи качества не подобны качествам Аллаха." };

            ListOfNames["Возвышенный, Всевышний, Превознёсшийся"] = new Name() { One_Name = "Возвышенный, Всевышний, Превознёсшийся", Description = "Всевышний Аллах сказал: Не тяготит Его оберегание их. Он — Возвышенный, Великий. Сура 2 «Корова», аят 255. Славь имя Господа твоего Всевышнего. Сура 87 «Всевышний», аят 1. Он — Ведающий сокро­венное и явное, Большой, Превознёсшийся. Сура 13 «Гром», аят 9. Эти имена объединяют все проявления возвышенности, присущие Аллаху.Его высокое положение объясняется вознесением на Трон, и Он находится над рабами Своей божественной сущностью. Его высокое положение также объясняется тем, что Ему присущи возвышенные и величественные качества, которые никоим образом не похожи на качества творений. Более того, все творения вместе не способны всесторонне постичь даже одно из значений какого либо божественного качества. По этому поводу Всевышний сказал: «Они не способны объять Его своим знанием» (сура 20 «Та,ха »,аят 110), и это ещё раз доказывает, что ничьи качества не подобны качествам Всевышнего Аллаха. Его высокое положение проявляется в Его абсолютной власти, ибо Он — Единственный, Всемогущий. Своим могуществом и возвышенностью над всеми Он покоряет творения. Их судьбы — в Его Руках, и происходит только то, что Он пожелает, и ничто не способно помешать этому. А то, что Ему не угодно, не может произойти, и если все твари объединятся, чтобы сотворить то, что Ему не угодно, они не сумеют сделать этого. Если же они объединятся, чтобы помешать тому, что Он предрешил, то не сумеют предотвратить это. Его власть совершенна, и Его воля неизменно исполняется, и все творения испытывают в Нём всестороннюю крайнюю нужду." };

            ListOfNames["Великий"] = new Name() {One_Name = "Великий", Description = "Коран гласит: Не тяготит Его оберегание их.Он — Возвышенный, Великий. Сура 2 «Корова», аят 255. Аллах велик, и все божественные качества обязывают к почитанию и возвеличиванию Его.Ни одно творение не способно возвеличить Его должным образом, как Он того заслуживает.Он достоин похвалы, на которую способен только Он и которая недоступна Его рабам. Следует знать, что присущие Аллаху значения величия делятся на две категории. 1.Он обладает всеми качествами совершенства, и эти качества присущи Ему в самой совершенной, величественной и всеобъемлющей форме.Его знание безгранично, а могущество непререкаемо. Ему присущи гордость и величие,одним из проявлений которого является то, что небеса и земля в Пригоршне Милостивого не больше горчичного зёрнышка.Об этом говорили Ибн ‘Аббас и другие комментаторы, а Всевышний сказал: «Не ценили они Аллаха должным образом, а ведь вся земля в День воскресения будет всего лишь Пригорш­ней Его, а небеса будут свёрнуты Его Десницей» (сура 39 «Толпы», аят 67); «Воистину, Аллах удер­живает небеса и землю, чтобы они не сдвинулись. А если они сдвинутся, то никто другой после Не­го их уже не удержит» (сура 35 «Творец», аят 41); «Небеса готовы разверзнуться сверху[от величия Аллаха]» (сура 42 «Совет», аят 5). В «Сахихе» сообщается со слов Пророка , что Аллах сказал: «Гордость — Моя накидка, а величие — Мой изар, и Я подвергну мучениям того, кто попытается отнять их у Меня» [Муслим, т. 4, с. 2023; Абу Давуд, т. 4, с. 59; Ибн Маджа, т. 2, с. 1397; Ахмад, т. 2, с. 376]. Таким образом, гордость и величие — два божественных качества.Их невозможно оценить по достоинству, равно как и невозможно постичь всю глубину их смысла. 2. Ни одно из творений не достойно того, чтобы его возвеличивали и почитали так же, как Аллаха. Он заслуживает того, чтобы рабы возвеличивали Его в душе, языком и телом.Для этого следует проявить усердие в познании Его, возлюбить Его и унизиться перед Ним, смириться пред Его величавостью и убояться Его. Для этого надлежит устами прославлять Его, а телом — благодарить Его и поклоняться Ему.Чтобы почитать Аллаха, следует надлежащим образом бояться Его в делах, повиноваться Ему, не зная ослушания, поминать Его без устали, не забывать Его и быть признательным Ему, не допуская неблагодарности.Чтобы почитать Его, необходимо уважать Его запреты и предписания, касающиеся временных промежутков, мест и поступков. Всевышний сказал: «Вот так! И если кто почитает обрядовые знамения Аллаха, то это исходит от богобоязненности в сердцах» (сура 22 «Хадж», аят 32); «Вот так! Кто почитает святыни Аллаха, тот поступает во благо себе перед своим Господом» (сура 22 «Хадж», аят 30). Наконец, чтобы почитать Его, следует не возражать против того, что Он сотворил или узаконил." };

            ListOfNames["Славный"] = new Name() { One_Name = "Славный", Description = "Это — Тот, Кто обладает великой славой. Под славой подразумевается величие и необъятность качеств. Что же касается качеств Аллаха, то все они величественны и достославны. Он — Знающий, и Его знание совершенно. Он — Милосердный, и Его милосердие объемлет всё сущее. Он — Могущественный, и для Него нет ничего невозможного. Он — Выдержанный и Мудрый, а Его выдержка и мудрость безупречны. Такая характеристика справедлива по отношению ко всем божественным именам и качествам. Они достигли предела великолепия и славы, в них нет ни малейшего изъяна или недостатка. Всевышний Аллах сказал: Да пребудут над вами ми­лость и благословение Ал­лаха, о обитатели дома! Воистину, Он — Досто­хвальный, Славный. Сура 11 «Худ», аят 73" };

            ListOfNames["Большой"] = new Name() { One_Name = "Большой", Description = "Пречистому и Всевышнему Аллаху присущи слава, гордость и величие. Он больше, величавее, славнее и превыше всего сущего. Сердца Его возлюбленных и избранников преисполнены уважения и почтения к Нему. Они покорны Ему и смиренны перед Его величием, как сказал об этом Всевышний: Это потому, что, когда призывали одного Аллаха, вы не веровали. Если же к Нему приобщали сото­варищей, вы верили. Ре­шение принимает толь­ко Аллах, Возвышенный, Большой. Сура 40 «Прощающий», аят 12" };
            ListOfNames["Слышащий"] = new Name() { One_Name = "Слышащий", Description = "Всевышний сказал: Аллах — Слышащий, Ви­дящий. Сура 4 «Женщины», аят 134. Божественный слух и зрение часто упоминаются вместе. Каждое из этих качеств подразумевает обладание соответствующими внешними и внутренними способностями. Слышащий — Тот, Кто объемлет слухом всё, что можно услышать. Он слышит любые тайные и явные звуки, возникающие в высшем и низшем мирах. Он слышит их все без исключения, не запутываясь в них, несмотря на их многочисленность. Он слышит голоса на любых языках, близкие и отдалённые, приглушённые и громкие. Все они воспринимаются Им совершенно одинаково: Скрываете вы свои речи или произносите их вслух, прячетесь вы ночью или открыто передвигаетесь днём — все вы равны[перед Аллахом]. Сура 13 «Гром», аят 10. Аллах уже услышал слова женщины, которая всту­пила с тобой в пререкания относительно своего мужа и пожаловалась Аллаху. Аллах слышал ваш спор, ведь Аллах — Слышащий, Видящий. Сура 58 «Препирающаяся», аят 1 ‘Аиша говорила: «Благословен Тот, Чей слух объемлет любые звуки! Когда пререкавшаяся женщина пришла и стала жаловаться Посланнику Аллаха (да благословит его Аллах и приветствует), я находилась в углу комнаты и не могла расслышать некоторые из её слов. Аллах же ниспослал аят : “Аллах уже услышал слова женщины, которая вступила с тобой в пре­рекания относительно своего мужа”». Божественный слух бывает двух видов: а) имеется в виду, что Аллах слышит любые тайные и явные звуки, а также громкие и приглушённые голоса, различая их совершенным образом; б) имеется в виду, что Он внимает тем, кто просит Его, поклоняется Ему и обращается к Нему с молитвами. Он отвечает на их просьбы, вознаграждая их за это. Всевышний сказал: «Воистину, мой Господь внимает мольбе» (сура 14 «Ибрахим»,аят 39). Когда молящийся говорит: «Аллах внимает тому, кто восхваляет Его»[30], — имеется в виду, что Он отвечает на молитвы." };

            ListOfNames["Видящий"] = new Name() { One_Name = "Видящий", Description = "Это — Тот, Чьё зрение объемлет всё сущее в любых уголках небес и земли, даже самые крошечные творения. Он видит чёрного муравья на скале в тёмную ночь, различая все его внешние очертания и внутренние органы; от Него не скрыто даже движение пищи по его мелким органам. Он видит ток жидкости внутри ветвей деревьев и любых других растений, независимо от их размеров. Он различает сосуды муравьёв, пчёл, комаров и даже более мелких существ. Пречист Тот, Чьё величие изумляет умы, Чьи качества необъятны, а величие и проницательность совершенны. Он ведает сокровенное и явное, близкое и далёкое. Он замечает даже беглые взгляды и видит любые изменения, происходящие в сердцах. Всевышний сказал: «…Который видит те­бя, когда ты выстаиваешь намазы по ночам и двига­ешься среди падающих ниц. Воистину, Он — Слы­шащий, Знающий» (сура 26 «Поэты», аяты 218–220); «Он знает о предательском взгляде и том, что скрыва­ют сердца» (сура 40 «Прощающий», аят 19); «Аллах же — Свидетель всякой вещи!» (сура 85 «Созвездия Зодиака»,аят 9), то есть Он знает обо всём, что происходит во Вселенной, и объемлет зрением и слухом всё сущее." };

            ListOfNames["Знающий, Ведающий"] = new Name() { One_Name = "Знающий, Ведающий", Description = "Всевышний сказал: Он — Одолевающий и на­ ходится над Своими раба­ми.Он — Мудрый, Веда­ющий. Сура 6 «Скот», аят 18. Воистину, Аллах ведает о всякой вещи. Сура 8 «Трофеи», аят 75. Знающий — Тот, Чьё знание объемлет всё сущее: необходимое, невозможное и возможное. Ему известно о Себе Самом, Его пресвятых качествах и великих атрибутах. Все они есть необходимость, отсутствие которой невозможно и невероятно. Наряду с этим, Ему известно о том, что невозможно, и том, как бы это выглядело, если бы стало возможно. В Коране сказано: «Если бы на[небесах и земле] были иные божества на­ ряду с Аллахом, то[небеса и земля] разрушились бы» (сура 21 «Пророки», аят 22); «Аллах не взял Себе сына, и нет наряду с Ним другого бога. В противном случае каждый бог унёс бы с собой то, что сотворил, и одни из них возвысились бы над другими» (сура 23 «Верующие», аят 91). Эти и похожие аяты указывают на то, что Аллах ведает о том, что невозможно, и даже сообщает нам о том, что могло бы произойти, если бы это невозможное стало реальностью. Наконец, помимо всего прочего, Аллах ведает о возможном, то есть том, что существует во Вселенной, и том, чего пока нет, поскольку этого требовала божественная мудрость, но что в принципе может возникнуть. Его знания объемлют высший и низший миры, объемлют пространство и время. Ему известно всё сокрытое и явное, очевидное и сокровенное. Всевышний сказал: «Воистину, Аллах веда­ет о всякой вещи» (сура 8 «Трофеи», аят 75). Текстов о всеобъемлющем знании Аллаха очень много; всех их нельзя сосчитать и перечислить. Ничто не скроется от Него ни на земле, ни на небе, будь то крошечная крупица, или нечто большее, или нечто меньшее. Он ничего не оставляет без внимания и ничего не забывает. Все необъятные и разносторонние познания, которыми обладают творения, ничто в сравнении со знанием Аллаха. Точно так же всё могущество творений не идёт ни в какое сравнение с могуществом Господа. Он — Тот, Кто наделил их знаниями о том, чего они не ведали, и наделил их всевозможными способностями. Ему известно обо всём, что происходит на небесах и на земле, а также обо всех творениях, проживающих в этих мирах, — о них самих, их качествах, поступках и состояниях. Он ведает то, что произошло в прошлом, и то, чему предстоит произойти в далёком будущем. Он знает то, чего никогда не произойдёт, и то, как это выглядело бы, если бы всё - таки произошло. Ему известно обо всём, что происходит с людьми с того момента, как они появляются на свет, что постигает их после смерти и что ожидает их после того, как они будут воскрешены. Ни один из их добрых и злых поступков не сокрыт от Него, и в Обители вечности они получат воздаяние за свои деяния и узнают все мельчайшие подробности о самих себе. Одним словом, Аллах — Тот, Чьи знания охватывают всё зримое и незримое, тайное и явное, необходимое, невероятное и возможное. Он ведает о высшем и низшем мирах, знает о прошлом, настоящем и будущем и ничего не оставляет незамеченным." };

            ListOfNames["Достохвальный"] = new Name() { One_Name = "Достохвальный", Description = "  Всевышний сказал: О люди! Вы нуждаетесь в Аллахе, тогда как Ал­лах — Богатый, Досто­хвальный. Сура 35 «Творец», аят 15. Ибн аль-Каййим указывал на два проявления этого качества. Во-первых, все существа воздают Ему хвалу. Всевышний Аллах заслуживает всяческой похвалы, которую возносят Ему первые и последние обитатели небес и земли. Он достоин похвалы, которую возносят Ему в этом мире и в Последней жизни, и похвалы, которую только можно воздать на протяжении долгих веков. Она наполняет высший и низший миры, её невозможно ни сосчитать, ни перечислить. Аллах достоин её по праву, ведь именно Он создал творения, наделил их всяческими милостями и проливает на них зримые и незримые блага — как мирские, так и духовные. Он избавляет их от бед и несчастий. Любые милости, которыми пользуются рабы, ниспосланы от Аллаха, и только Он оберегает от зла и неприятностей. Посему Он достоин того, чтобы творения восхваляли и благодарили Его в любое время, каждое мгновение. Во-вторых, Господь достоин похвалы за то, что обладает прекрасными именами и совершенными, возвышенными качествами. Его атрибуты славны и прекрасны. Он обладает всеми качествами совершенства в их наилучших, самых величественных проявлениях. Каждое из Его качеств в отдельности заслуживает самой совершенной похвалы. Что же можно говорить о Том, Кто обладает всеми пречистыми качествами вместе?! Восхваления достойны Сам Аллах, Его атрибуты и деяния. Эти деяния преисполнены либо милости и добра, либо справедливости и мудрости, и потому их полагается хвалить наилучшим образом. Помимо того, Аллах заслуживает похвалы за то, что творит и издаёт законы, регулирующие всё происходящее во Вселенной, а также законы Шариата и справедливого возмездия в этом мире и в Последней жизни. Если говорить подробно о том, какой должна быть хвала Аллаху, и обо всём, за что Он заслуживает похвалы, то для этого не хватит ни слов, ни чернил." };

            ListOfNames["Могущественный, Всемогущий, Могучий, Способный, Всемогущий, Всесильный, Крепкий, Несокрушимый"] = new Name() { One_Name = "Могущественный, Всемогущий, Могучий, Способный, Всемогущий, Всесильный, Крепкий, Несокрушимый", Description = " Эти великие имена имеют похожий смысл, указывая на то, что Всевышний обладает совершенным могуществом, великой силой и безграничной мощью. В Коране сказано: «Могущество целиком принадлежит Аллаху» (сура 10 «Юнус», аят 65); «Воистину, твой Господь — Всесильный, Могущественный!» (сура 11 «Худ», аят 66). Таким образом, все три проявления могущества самым полным образом относятся к Великому Аллаху. Первое — сила. На это качество указывают такие имена, как Аль-Кави (Всесильный), Аль-Матин (Крепкий). Оно не может быть приписано творениям, какими бы сильными они ни были. Коран гласит: «Воистину, Аллах — Наделяющий уделом, Обладающий силой, Крепкий» (сура 51 «Рассеивающие прах», аят 58); «Ал­лах — Всемогущий. Аллах — Прощающий, Милосерд­ный» (сура 60 «Испытуемая», аят 7); «Скажи: “Он влас­тен наслать на вас мучения сверху или из-под ваших ног, или привести вас в замешательство, разделив на группировки и дав одним из вас вкусить жестокость других”» (сура 6 «Скот», аят 65); «Воистину, Аллах спо­собен на всякую вещь» (сура 18 «Пещера», аят 45); «Во­истину, богобоязненные пребудут в Райских садах и среди рек на седалище истины возле Всемогущего Властелина» (сура 54 «Луна», аяты 54–55). Второе — отсутствие всякой нужды. Аллах Самодостаточен, Он ни в ком и ни в чём не нуждается. Рабы не способны причинить Ему какой-либо вред или принести Ему какую-либо пользу. Напротив, только Он причиняет вред и приносит пользу, дарует блага одним и лишает других. Третье — власть. Аллах господствует над всеми творениями; все они подвластны Ему, подчинены Его величию, зависимы от Его воли. Он распоряжается судьбами всех существ, и они не могут ни шевельнутся, ни распорядиться тем, чем владеют, вопреки Его воле. Происходит только то, что пожелает Аллах, и то, что не угодно Ему, не может произойти. Нет силы и мощи ни у кого, кроме Него.Свидетельством Его силы и могущества было сотворение небес и земли, а также всего, что между ними, всего за шесть дней. Он дарует творениям жизнь, а потом умерщвляет их, а потом они будут воскрешены и вернутся к их Господу: «Сотворение и воскрешение всех вас подобно сотворению и воскрешению одного человека» (сура 31 «Лукман», аят 28); «Он — Тот, Кто создаёт тво­рения в первый раз, а затем воссоздаёт их, и сделать это для Него ещё легче» (сура 30 «Ромеи», аят 27). Другим свидетельством Его могущества является то, что ты видишь землю иссохшей, но стóит Ему излить с неба воду, как она приходит в движение и набухает, а потом на ней произрастают всевозможные пары растений. О Его могуществе свидетельствует участь неверующих и несправедливых народов, которые были подвергнуты различным наказаниям, ставшим поучительным уроком для остальных. Их ухищрения и уловки не принесли им пользы. Ни богатство, ни многочисленные армии, ни крепости нисколько не защитили их от Божьей кары, когда та постигла их. Напротив, всё это ещё больше отяготило их в тот момент, когда приблизилась их погибель. Колоссальная сила, которую обрели эти народы, и их изумительные изобретения были результатом того, что Аллах наделил их определёнными способностями и обучил тому, чего они не знали. Но вся эта сила, эти способности и достижения не принесли им никакой пользы, не уберегли их от наказания и погибели, и это стало ещё одним знамением Аллаха. Они пытались спастись от лютой кары, но свершилось Божье веление, ибо всё сущее на небесах и на земле подчинено Его воле. На совершенство могущества и власти Аллаха также указывает сотворение Им деяний рабов, их богоугодных поступков и грехов. Аллах сотворил не только рабов, но и их деяния. Таким образом, поступки и действия людей сотворены и предопределены Господом, но совершаются самими людьми. Между этими истинами нет противоречий, ибо Аллах создал их способности и волю. Тот, Кто создал причины, также является создателем того, что вызывает эти причины. В Коране сказано: «Аллах сотворил вас и то, что вы делаете» (сура 37 «Выстроившиеся в ряды», аят 96). Ещё одним проявлением Его могущества является упомянутое в Коране обещание помогать праведникам и приближённым рабам (аулийа), несмотря на их малочисленность и слабую оснащённость, в борьбе с врагами, превосходящими их числом и умением. Всевышний сказал: «Сколько малочисленных отрядов победило многочисленные отряды по воле Аллаха!» (сура 2 «Корова», аят 249). Благодаря Своему могуществу и по милости Своей Аллах приготовил для обитателей Ада и Рая разные виды наказания и вечного удовольствия, которые не прервутся и не прекратятся. Благодаря Своему могуществу Господь сотворил всё сущее, управляет Своими творениями, придаёт им соразмерность и совершенный облик, оживляет мёртвых и умерщвляет живых, а потом воскресит рабов для того, чтобы они получили воздаяние, вознаградит праведников за их благодеяния и покарает грешников за их прегрешения. Он изменяет состояние сердец и направляет их туда, куда пожелает: «Когда Он желает чего-либо, то стоит Ему сказать: “Будь!” — как это сбы­вается» (сура 36 «Йа, син», аят 82); «Где бы вы ни были, Аллах приведёт всех вас вместе. Воистину, Аллах спо­собен на всякую вещь» (сура 2 «Корова», аят 148)." };

            ListOfNames["Богатый"] = new Name() { One_Name = "Богатый", Description = "Всевышний сказал: Он дарует богатство и на­деляет собственностью. Сура 53 «Звезда», аят 48. О люди! Вы нуждаетесь в Аллахе, тогда как Ал­лах — Богатый, Досто­хвальный. Сура 35 «Творец», аят 15. Он богат и абсолютно ни в чём не нуждается, ибо Он обладает совершенством, и все Его качества совершенны и далеки от малейших недостатков. Бог не может нуждаться в чём-либо, ибо Его богатство и самодостаточность являются необходимостью. Таким же образом, Бог непременно должен быть Добродетельным, Щедрым, Добрым, Милосердным, Великодушным. Творения не способны прожить без Его заботы даже мгновение ока. Их появление на свет и существование невозможны без Его участия. Они нуждаются в Нём для удовлетворения своих нужд и потребностей. Проявлением Его безмерного богатства является обладание Им сокровищницами небес и земли, а также сокровищницами великой милости. Он неустанно оказывает милость Его творениям: каждый миг они пользуются Его благами. Он одаряет их днём и ночью, щедро и не скупясь. Совершенство богатства и щедрости Аллаха проявляется и в Его повелении взывать к Нему. Он обещал Своим рабам отвечать на их молитвы и удовлетворять их просьбы. Он наделяет их благами, которые они просят, и многим из того, чего они даже не просят. Его богатство настолько совершенно, что если все творения, от первого до последнего, соберутся в одном месте и попросят Его о любых благах, и Он одарит их тем, что они попросят, и всем, что только придёт им на ум, то от этого Его богатство не уменьшится даже на малую толику. Свидетельством безграничности Его богатства являются удовольствия и нескончаемые радости, которыми Он щедро одарит обитателей Рая. Это будут бесконечные дары, которых не видывал взор, о которых не слышали уши, которые даже не приходили на ум человеку. Ещё одним проявлением Его абсолютного богатства является отсутствие у Него жены и детей. Никто не разделяет с Ним божественной власти, и Он не нуж- дается в покровителе, избавляющем от унижения. Он богат и самодостаточен благодаря Своим совершенным качествам, и Он — Тот, Кто избавляет творения от нужды. Одним словом, Аллах самодостаточен и обладает всесторонним и абсолютным богатством, вследствие чего Он ни в чём не нуждается. Он дарует достаток всем творениям в самом широком смысле и одаряет особым достатком Своих избранников, наполняя их сердца божественным знанием и верой." };

            ListOfNames["Мудрый"] = new Name() { One_Name = "Мудрый", Description = "Всевышний сказал: Он — Одолевающий и нахо­дится над Своими рабами. Он — Мудрый, Ведающий. Сура 6 «Скот», аят 18. Это значит, что Он обладает безупречной мудростью и совершенной властью. Мудрый — Тот, Кто обладает всесторонними знаниями о сути событий и их исходе, последствиях, Кто заслуживает щедрой похвалы, обладает совершенным могуществом и необъятным милосердием. Он всё расставляет по местам, творит только то, что заслуживает быть, и отдаёт только подобающие приказы. В Его мудрости нельзя усомниться, и её нельзя подвергнуть критике. Следует знать, что божественная мудрость проявляется в двух формах. Первая — мудрость в творении. Аллах создал всё сущее с истиной и ради неё. Истина была целью появления творений на свет. Он установил во Вселенной прекрасный порядок и придал каждому существу надлежащий вид. Более того, Он позаботился о каждом органе живых существ и каждой части Его творений, и ни в одном из них нельзя усмотреть несовершенство или изъян. И если даже лучшие умы всех времён и народов объединятся, чтобы придумать нечто подобное творению Милостивого или хотя бы нечто близкое к этому прекрасному порядку и изумительному совершенству, у них ничего не выйдет. Они не способны на такое, и самые мудрые и рассудительные из мужей довольствуются лишь тем, что постигают некоторые из Божьих законов. Они могут видеть лишь часть того изящества и совершенства, которое заложено в творении Аллаха. Всё это вытекает с полной очевидностью из Его величия и совершенства Его качеств, из Его многочисленных творений и велений. Поэтому Он бросил вызов рабам, приказав им внимательно наблюдать за Его творением, выискивая в нём изъяны и недостатки. Он же сообщил, что человек скорее устанет наблюдать за Божьими созданиями, чем найдёт в них то, к чему можно придраться. Вторая — мудрость в законах и повелениях. Всевышний установил законы, ниспослал Писания и отправил посланников, чтобы рабы познали Его и поклонялись Ему. Может ли быть более мудрая цель? Может ли быть большая милость? Познание Всевышнего Аллаха, поклонение Ему одному, не приобщая сотоварищей к Нему, искреннее служение Ему, возможность благодарить и восхвалять Его — лучшее и самое славное из того, чем Аллах наделил Своих рабов. Это — наивысшее счастье и радость для человеческих сердец и душ. Вместе с тем это — единственный способ обрести вечное счастье и бесконечное блаженство. Эта великая цель — основа всякого благополучия и наивысшее удовольствие. Ради неё было создано всё сущее, ради неё были сотворены Рай и Ад, и ради неё будет вершиться справедливое возмездие. И если бы она была единственной целью ниспослания Божьих приказов и законов, то этого было бы вполне достаточно. Шариат Аллаха и Его религия преисполнены всякого блага. Его повествования наполняют сердца знаниями, убеждённостью и верой. Правильные убеждения выправляют кривизну человеческих душ, воплощаются в прекрасные нравственные качества и праведные деяния, помогают следовать прямым путём. Приказы и запреты Аллаха исполнены высшей мудрости, они служат исправлению духовного и материального мира, ибо Господь приказывает только то, что приносит одну только пользу или приносит больше пользы, чем вреда. А Его запреты распространяются только на то, что приносит один только вред либо приносит больше вреда, чем пользы. Мудрость мусульманского Шариата не ограничивается тем, что он нацелен на оздоровление душ, исправление нравов и поступков, а также следование прямым путём. Напротив, Шариат содержит законы, необходимые для приведения в порядок и мирских дел. Более того, подлинного благополучия на земле можно достичь, только следуя предписаниям истинной религии, принесённой Пророком Мухаммадом (да благословит его Аллах и приветствует). Это очевидно для каждого благоразумного человека, ибо последователи Мухаммада (да благословит его Аллах и приветствует) жили в полном благополучии, пока они руководствовались основными и второстепенными предписаниями этой религии, прислушивались ко всем её наставлениям и указаниям. Когда же они отклонились от этого пути и отказались от выполнения многих из этих канонов, когда они перестали руководствоваться высоким учением ислама, их мирская жизнь тоже пришла в упадок. Посмотри на другие народы, обладающие могучей силой и добившиеся изумительных успехов в цивилизационном развитии. Они лишены живительного духа веры, её милости и справедливости, и поэтому своими действиями они приносят больше вреда, чем пользы. Их учёные, правители и политики не способны предотвратить распространения зла, которое несёт с собой их развитие, и они не смогут справиться с такой задачей, пока не исправят своего положения. Вот почему божественная мудрость требовала, чтобы принесённая Мухаммадом (да благословит его Аллах и приветствует) религия и Священный Коран были ярчайшими свидетельствами его правдивости и справедливости его учения. Одним словом, Мудрый — Тот, Чья мудрость проявляется в Его творениях и велениях, каждое из которых совершенно и прекрасно. Он обладает тремя формами власти, которыми не обладает никто другой: вершит судьбу рабов по законам Шариата, предопределения и справедливого возмездия. Следует различать между законами предопределения и Шариата. Первые связаны с тем, что Господь предопределил и сотворил. В соответствии с ними происходит только то, что пожелал Аллах, а то, что не угодно Ему, не происходит. А законы Шариата связаны с тем, что Аллах приказал. Творения обязательно подчиняются либо одной группе этих законов, либо им обеим. Тот, кто совершает богоугодные поступки, подчиняется как законам предопределения, так и законам Шариата. Тот же, кто поступает иначе, попадает под законы предопределения. Его судьба была предопределена и предрешена, но он не следовал законам Шариата, отказавшись от того, что угодно Аллаху и нравится Ему. Таким образом, добро и зло, благодеяния и грехи — всё это происходит строго в соответствии с Божьим предопределением. А те из этих поступков, которые угодны Аллаху и нравятся Ему, также соответствуют законам Его Шариата и всему, что вытекает из них." };

            ListOfNames["Выдержанный"] = new Name() { One_Name = "Выдержанный", Description = "Всевышний сказал: Знайте, что Аллаху из­вестно о том, что в ва­ших душах. Остерегайтесь Его и знайте, что Аллах — Прощающий, Выдержан­ный. Сура 2 «Корова», аят 235. Выдержанный — Тот, Кто одаряет творения зримыми и незримыми благами, несмотря на их ослушание и многочисленные ошибки. Он не спешит наказывать грешников за ослушание и предоставляет им отсрочку, чтобы они могли раскаяться и вернуться на прямой путь. Аллах обладает совершенной выдержкой, которая распространяется даже на неверующих, нечестивцев и грешников. Он предоставляет им шанс покаяться и не торопится с их наказанием. Если бы Он захотел, то подверг бы их лютой каре сразу после того, как они совершили свой грех, потому что грехи навлекают на человека скорое возмездие в его разных проявлениях. Однако выдержка Пречистого Господа требует того, чтобы им была предоставлена отсрочка. В Коране сказано: Если бы Аллах стал на­казывать людей за то, что они приобрета­ют, то Он не оставил бы на поверхности [земли] ни единого живого сущест­ва, но Он предоставля­ ет им отсрочку до назна­ченного срока. Их срок настанет, и Аллах видит Своих рабов. Сура 35 «Творец», аят 45. Если бы Аллах стал нака­зывать людей за их несправедливость, то не оста­вил бы на земле ни одного живого существа. Одна­ко Он предоставляет им отсрочку до назначенного срока. Когда же наступит их срок, они не смогут от­далить или приблизить его даже на час. Сура 16 «Пчёлы», аят 61" };

            ListOfNames["Снисходительный, Прощающий, Всепрощающий"] = new Name() { One_Name = "Снисходительный, Прощающий, Всепрощающий", Description = "Всевышний сказал: Воистину, Аллах — Снисходительный, Прощаю­щий. Сура 22 «Хадж», аят 60. Снисходительный, Прощающий — Тот, Чьим неотъемлемым качеством во все времена остаётся снисходительность и всепрощение. Все существа нуждаются в Его снисходительности и прощении, милости и великодушии. Он обещал простить и помиловать тех, кто своими деяниями заслужит такое отношение. Всевышний сказал: Воистину, Я прощаю тех, кто раскаялся, уверовал, стал поступать праведно, а потом последовал пря­мым путём. Сура 20 «Та, ха», аят 82. Снисходительный — Тот, Кто обладает необъятным прощением. Он отпускает рабам грехи, особенно если они совершают поступки, помогающие им заслужить прощение, — например, просят об этом и раскаиваются, укрепляют веру и совершают праведные дела. Пречистый Аллах принимает покаяния и прощает прегрешения. Он — Снисходительный. Он любит прощение и любит, когда люди стремятся заслужить Его прощение. Он любит, когда они совершают богоугодные поступки и делают добро Его рабам. Его прощение настолько совершенно, что Он милует даже тех, кто совершил великие преступления, если они покаялись и вернулись на прямой путь. Проявлением Его всепрощения является то, что обращение в ислам смывает любые большие и малые грехи, совершённые прежде. Всевышний сказал: Скажи Моим рабам, ко­торые излишествовали во вред самим себе: «Не от­чаивайтесь в милости Ал­лаха. Воистину, Аллах про­ щает грехи полностью, ибо Он — Прощающий, Милосердный». Сура 39 «Толпы», аят 53. В хадисе сообщается, что Аллах сказал: «О сын Адама, если ты явишься ко Мне с грехами величиной с Землю и предстанешь предо Мной, никого не приобщая в сотоварищи ко Мне, то Я дарую тебе столько же прощения» [Тирмизи, т. 4, с. 122; хадис хороший согласно аль-Альбани: Джами‘ ас-сагир, т. 5, с. 548]. В Коране также сказано: «Воистину, твой Господь обладает необъятным прощением» (сура 53 «Звезда», аят 32). Аллах распахнул врата для обретения прощения путём покаяния и молитв о прощении, укрепления веры и совершения праведных деяний, доброго и снисходительного отношения к рабам Аллаха, стремления к Его милости и надежды на Него. Всё это — лишь некоторые из поступков, приближающих нас к Его великому прощению." };

            ListOfNames["Принимающий покаяния"] = new Name() { One_Name = "Принимающий покаяния", Description = "Всевышний сказал: Неужели они не знают, что Аллах принимает по­каяния от Своих рабов и принимает пожертвова­ния, что Аллах — Прини­мающий покаяния, Милосердный? Сура 9 «Покаяние», аят 104. Принимающий покаяния — Тот, Кто не перестаёт принимать покаяния и прощать грехи кающимся. Если человек обращается к Аллаху с искренним покаянием, то Он непременно принимает его покаяние. Он прощает кающихся вначале, когда помогает им принести покаяние и направляет их сердца к Нему. Он прощает их после их покаяния, когда принимает его и отпускает им прегрешения. Таким образом, данное качество Господа проявляется в двух формах: а) Он вселяет в сердца рабов желание вернуться к Нему и покаяться, выполнив все обязательные условия, то есть прекратив грешить, искренне пожалев о содеянном, твёрдо решив не возвращаться к этому и совершив вместо него добрые дела; б) Он принимает искренние покаяния рабов, внимая их просьбам, и стирает совершённые ими ранее грехи" };

            ListOfNames["Наблюдающий"] = new Name() { One_Name = "Наблюдающий", Description = "  Он — Тот, Кто ведает обо всём, что кроется в сердцах, и сохраняет всё, что приобретает человек: «Воистину, Аллах наблюдает за вами» (сура 4 «Женщины», аят 1). Наблюдающий — Тот, Кто оберегает Свои творения, располагает их в самом прекрасном порядке и управляет ими совершенным образом." };

            ListOfNames["Свидетель"] = new Name() { One_Name = "Свидетель", Description = "Свидетель — Тот, Кто видит всё сущее, слышит любые громкие и тихие звуки, видит все большие и маленькие существа; Кто объемлет знанием всё сущее и свидетельствует за и против Своих рабов обо всём, что они совершили. Шейх ‘Абд-ар-Рахман ас-Са‘ди писал [Хакк аль-вадых аль-мубин, с. 58–59]: Ар-Ракиб (Наблюдающий) и Аш-Шахид (Свидетель) — имена близкие по смыслу, каждое из них указывает на то, что Аллах слышит любые звуки и видит всё сущее. Ему известно всё явное и сокрытое. Он читает мысли творений и следит за их взорами, не говоря уже об их очевидных поступках. Всевышний сказал: «Воистину, Аллах наблюда­ет за вами» (4:1); «Аллах — Свидетель всякой ве­щи» (58:6). Вот почему осознание того, что Аллах наблюдает за человеком, является одним из самых достойных поступков, совершаемых сердцем. Это есть форма поклонения Аллаху посредством Его имён Ар-Ракиб (Наблюдающий) и Аш-Шахид (Свидетель). Если раб понимает, что Аллах знает о каждом из его явных и сокрытых поступков, если он вспоминает об этом в любом состоянии, то он оберегает свою душу от любых мыслей и раздумий, ненавистных Аллаху, а также избегает любых слов и поступков, вызывающих Его гнев. Он достигает ступени ихсана, поклоняясь Аллаху так, словно он видит Его, и если даже он не видит Его, то Аллах видит его. Таким образом, если Аллах наблюдает за любыми мелкими и сокрытыми вещами, ведая о сокровенных тайнах и намерениях, то Ему тем более известно обо всём явном и очевидном, то есть о поступках, которые рабы совершают телом." };


            ListOfNames["Хранитель"] = new Name() { One_Name = "Хранитель", Description = "Всевышний сказал: Воистину, Господь мой — Хранитель всякой вещи. Сура 11 «Худ», аят 57. Это прекрасное имя имеет два значения. Первое говорит о том, что Аллах сохраняет все добрые и злые деяния, праведные поступки и грехи, совершённые Его рабами. Ему известно обо всех поступках, совершённых ими явно и тайно, и все они записаны в Хранимой скрижали. Кроме того, Он приставил к людям ангелов — благородных писцов, которым ведомо об их поступках. Таким образом, из этого прекрасного имени следует, что Аллах обладает всесторонним знанием о внешнем и духовном состоянии Его рабов. Всё это записано в Хранимой скрижали и свитках, находящихся в руках ангелов. Он ведает о степени тяжести и превосходства человеческих поступков, их совершенстве и недостатках, а также размерах той награды и того наказания, которое они заслужили. А потом, когда придёт время, Он воздаст каждому из них либо по милости, либо по справедливости. Второе свидетельствует о том, что Аллах оберегает рабов от всего, что им ненавистно. Подобная забота бывает общей и особой. Общая забота распространяется на все творения: Аллах облегчает им поиски пропитания, оберегает их жилища, наставляет их на путь, ведущий к Нему, и указывает на то, что приносит им пользу. По этому поводу сказано: «Господь наш — Тот, Кто при­дал облик всякой вещи, а затем указал всему путь» (сура 20 «Та, ха», аят 50), то есть указал путь каждому творению к тому, что предписано для него, в чём оно нуждается и без чего оно не в состоянии прожить. Он научил рабов тому, как раздобыть пропитание и как удовлетворять половое влечение. Он оберегает их от многочисленных несчастий и злоключений, и подобная забота распространяется не только на праведников, но и на грешников, а также на животных и прочие творения. Аллах удерживает небеса и землю от сотрясений, поддерживает творения благодаря Своим дарам. К людям Он приставил ангелов- хранителей, которые оберегают их от несчастных случаев по воле Аллаха, и если бы не божественная опека, то с ними непременно приключилось бы много несчастий. Особую заботу Аллах проявляет о Его любимых рабах. Он оберегает их от любых сомнений, искушений и страстей, которые могут навредить их вере и пошатнуть их убеждённость. Он помогает им благополучно избежать всего этого и защищает их от врагов из числа людей и джиннов. Он одаряет их победой над ними, отводя от них козни неверующих. Всевышний сказал: «Во­истину, Аллах оберегает тех, кто уверовал» (сура 22 «Хадж», аят 38). Это касается всего, что может навредить их духовному и мирскому благополучию. И чем сильнее вера раба, тем лучше оберегает Его Аллах по милости Своей. В хадисе сказано: «Помни об Аллахе, и Он защитит тебя» [Тирмизи, т. 4, с. 667; хадис достоверный согласно аль- Альбани: Джами‘ ас-сагир, т. 6, с. 300]. Это значит: выполняй Его приказы, остерегайся нарушать Его запреты, не преступай Его ограничения, и Он непременно защитит тебя и твою веру, твоё имущество и твоих детей, сохранив для тебя все дарованные Им блага." };

            ListOfNames["Проницательный, Добрый"] = new Name() { One_Name = "Проницательный, Добрый", Description = "Всевышний сказал: Аллах добр к Своим рабам и наделяет уделом, кого пожелает. Он — Всесиль­ный, Могущественный. Сура 42 «Совет», аят 19. Взоры не могут постичь Его, а Он постигает взоры. Он — Проницательный [или Добрый], Сведущий. Сура 6 «Скот», аят 103. Проницательный, Добрый — Тот, Кто нежно обращается со Своими рабами во всех вопросах, касающихся их духовного мира и связанных с ними внешне. Он ведёт их и приближает к ним то, что приносит им пользу, хотя они даже не ощущают Его руководства. Всё это — следствие Его знаний, великодушия и милосердия, и поэтому прекрасное имя Аль-Лятыф (Проницательный, Добрый) имеет два значения. Первое указывает на то, что Аллах объемлет знанием всё сущее, ведая о сокровенных тайнах и утаённых помыслах. Ему известно то, что кроется в сердцах, и то, что сокрыто от взоров. Он знает обо всём сущем в мельчайших подробностях. Второе значение свидетельствует о том, что Аллах добр к Его любимым рабам. Он желает довести до конца Своё благодеяние по отношению к ним, одарить их по Своей щедрости, поселить их в вышних горницах, облегчить им путь к наилегчайшему и отдалить их от всего обременительного. Он подвергает их всевозможным нелёгким испытаниям, которые ненавистны им, но даже они приносят им одну только пользу. Это — путь к счастью, ибо даже пророки подвергались испытаниям: их народы причиняли им страдания, и им было велено бороться с ними на пути Аллаха. В истории пророка Юсуфа сообщается о том, какие трудности выпали на его долю, но Аллах был добр к нему и проявил изысканную заботу о нём: исход предписанных ему испытаний оказался благим как в этом мире, так и в Последней жизни. Таким же образом Он подвергает испытанию Своих любимых рабов, чтобы, в конечном итоге, они добились исполнения своих заветных желаний. Люди не способны прочувствовать доброту и великодушие Аллаха, равно как и не способны представить себе их. Сколько желанных земных благ, таких как власть или правление, Аллах отдаляет от человека из сострадания к нему, чтобы они не навредили его религии. Но это огорчает людей, потому что они невежественны и не познали своего Господа. Если бы они знали, что припасено для них, но сокрыто от их взоров, то воздали бы хвалу Аллаху и принялись благодарить Его. Поистине, Он снисходителен и милосерден к Своим рабам, добр и обходителен со Своими любимцами. В одной из дошедших до нас молитв говорится: «О Аллах, пусть то, чем Ты одарил меня и что нравится мне, придаст мне силы для совершения того, что Ты любишь, и пусть то, чего Ты лишил меня и что нравится мне, не мешает мне совершать то, что Ты любишь» [Тирмизи, т. 5, с. 523; хадис хороший согласно ‘Абд-аль-Кадиру аль-Арнауту: Ибн аль-Асир 2, т. 4, с. 341]." };

            ListOfNames["Близкий"] = new Name() { One_Name = "Близкий", Description = "Всевышний сказал: Он сотворил вас из земли и поселил вас на ней. Про­сите прощения у Него, а за­ тем покайтесь перед Ним. Воистину, мой Господь — Близкий, Отзывчивый. Сура 11 «Худ», аят 61. Близкий — одно из имён Аллаха, и Его близость к творениям бывает двух видов. Близость в общем смысле проявляется в Его знании обо всём сущем. В этом смысле Он ближе к человеку, чем его яремная вена. Так же трактуется присутствие Аллаха рядом со всеми творениями. Особая близость проявляется по отношению к тем, кто поклоняется Аллаху, взывает к Нему и любит Его. Из такой близости вытекает любовь к этим людям и поддержка их: Аллах оказывает им поддержку, когда они трудятся и отдыхают, внимает их молитвам, принимает их поклонение и вознаграждает за него. В Благородном Коране сказано: «Если Мои рабы спросят тебя обо Мне, то ведь Я близок и отвечаю на зов молящего­ся, когда он взывает ко Мне» (сура 2 «Корова», аят 186). Если человек осознает истинный смысл всеобщей и особой близости Аллаха, то ему станет ясно, что Его близость к творениям не противоречит тому, что Он находится над Троном. Пречистый Аллах — Тот, Кто возвышен, несмотря на Его близость, и близок, несмотря на Его высокое положение." };

            ListOfNames["Отзывчивый"] = new Name() { One_Name = "Отзывчивый", Description = "Аллах отзывается на молитвы рабов, внимает их просьбам и принимает их поклонение. Это божественное качество проявляется в двух формах. В широком смысле слова Аллах внимает всем, кто обращается к Нему с просьбами или поклоняется Ему. Ваш Господь сказал: «Взы­вайте ко Мне, и Я отвечу вам». Сура 40 «Прощающий», аят 60. Обращаясь к Аллаху с просьбами, люди молят Его одарить их благами и уберечь от зла. Так поступают не только благочестивые праведники, но и грешники, и Аллах внимает молитвам каждого из них, принимая во внимание их положение и руководствуясь Своей мудростью. Это указывает на щедрость нашего Покровителя и то, что Его доброго отношения не лишены ни праведники, ни грешники. Поэтому, если молитва была принята, это не означает, что сам человек ведёт праведный образ жизни, за исключением тех случаев, когда просьбе сопутствуют праведные деяния, свидетельствующие об искренности человека и том, что правда на его стороне. Так было, например, когда пророки просили Аллаха за своих соплеменников или же, наоборот, проклинали их. Аллах внимал их молитвам, и это было подтверждением правдивости того, к чему они призывали, и указывало на их высокое положение перед Господом. Именно поэтому мусульмане и прочие современники Пророка (да благословит его Аллах и приветствует) неоднократно становились свидетелями того, как принимаются его молитвы. Это доказывало истинность его миссии и лишний раз подтверждало его правдивость. То же самое можно сказать о том, как Аллах принимает мольбы многих праведников и приближённых рабов — всё это указывает на их высокое положение перед Ним. Существуют и особые проявления того, как Аллах отзывается на мольбы рабов. Причины на это могут быть самые разные. Например, Аллах внимает мольбе раба, который попал в тяжёлое положение и испытывает сильную печаль: «Кто отвечает на мольбу нуждающегося, ког­да он взывает к Нему?» (сура 27 «Муравьи», аят 62). Че- ловек, попавший в сложную ситуацию, остро нуждается в помощи Аллаха, смиряется пред Ним и не возлагает никаких надежд на творения. В то же время милость Аллаха велика и объемлет все творения в зависимости от того, насколько они нуждаются в ней. И, конечно же, Он не лишает Своей милости того, кто испытывает крайнюю нужду. К обстоятельствам, способствующим скорейшему принятию молитв, также относятся долгое путешествие и попытки приблизиться к Нему посредством того, что угодно Ему, то есть посредством Его имён, качеств и дарованных людям благ. Помимо того, Аллах внимает мольбам больных и угнетённых, постящихся и родителей, молящихся за своих детей. Есть и определённые благословенные часы и обстоятельства, когда Господь не отвергает мольбу, например, по окончании обязательных намазов, перед наступлением рассвета, между азаном и икаматом, во время азана или дождя, в разгар битвы и т.д. Воистину, мой Господь — Близкий, Отзывчивый. Сура 11 «Худ», аят 61" };

            ListOfNames["Любящий, Любимый"] = new Name() { One_Name = "Любящий, Любимый", Description = "Всевышний сказал: Попросите прощения у своего Господа, а затем покайтесь перед Ним. Воистину, мой Господь — Милосердный, Любящий (или Любимый). Сура 11 «Худ», аят 90. Он — Прощающий, Любящий (или Любимый). Сура 85 «Созвездия Зодиака», аят 14. Это прекрасное имя образовано от слова вудд — ود — ‘чистая любовь’. Аллах любит пророков, ангелов, верующих рабов, и они тоже любят Его. Более того, они никого и ничто не любят так сильно, как Аллаха. Любовь избранных рабов к Нему не сравнима с любой другой любовью — ни в основе своей, ни в своих проявлениях, ни по силе любви. Любовь к Аллаху в сердце каждого раба должна опережать и превосходить любовь ко всему остальному. Более того, любовь ко всему остальному должна находиться в зависимости от любви к Нему. Любовь к Аллаху — основа всех праведных поступков, из неё исходят все формы поклонения душой и телом. Это чувство возникает по милости Господа. Человек не приобретает его благодаря своим способностям или своей силе. Аллах помещает любовь к Нему в сердце того раба, которого Он возлюбил. Потом Он помогает ему проникнуться любовью к Нему, после чего вознаграждает его особой любовью. Всё это — лишь результат доброго отношения Аллаха. Он создаёт причины и следствия. Он не ждёт отдачи, а любит бескорыстно — любит тех из Своих рабов, которые благодарны, за то, что они благодарны. Таким образом, пользу от всего этого получают только сами рабы. Благословен же Тот, Кто поместил любовь в сердца правоверных, а потом увеличил и усилил её настолько, что любовь ко всему остальному рядом с ней померкла, любые несчастья стали казаться лёгкими, а самые трудные богоугодные поступки стали доставлять удовольствие. Всё это принесло им множество разных благ, величайшим из которых является любовь к Аллаху, обретение Его довольства и наслаждение близостью к Нему. Таким образом, любовь раба к Господу окружена двумя проявлениями любви Самого Господа. Благодаря первоначальной любви Аллаха в человеке просыпается ответная любовь к Нему. За это Аллах благодарит Своего раба, Его любовь к нему усиливается, и тот попадает в число Божьих избранников. Иными словами, любовь к Господу — величайшая цель существования человека, и лучшим способом обрести её являются частое поминание и восхваление Аллаха, раскаяние перед Ним и сильное упование на Него. Помимо того, необходимо стремление приблизиться к Нему посредством выполнения обязательных и добровольных предписаний, подлинная искренность во всех словах и поступках, а также повиновение Пророку (да благословит его Аллах и приветствует) душой и телом. Всевышний сказал: Скажи: «Если вы лю­бите Аллаха, то следуй­те за мной, и тогда Аллах возлюбит вас». Сура 3 «Семейство ‘Имрана», аят 31" };

            ListOfNames["Признательный, Благодарный"] = new Name() { One_Name = "Признательный, Благодарный", Description = "Всевышний сказал: А если кто доброволь­но совершает доброе дело, то ведь Аллах — Призна­тельный, Знающий. Сура 2 «Корова», аят 158. Если вы одолжите Ал­лаху прекрасный заём, то Он приумножит его для вас и простит вас. Ал­лах — Благодарный, Вы­держанный. Сура 64 «Взаимное обделение», аят 17. Аллах — Признательный, Знающий. Сура 4 «Женщины», аят 147. Всевышний Аллах не даёт пропасть стараниям тех, кто трудится искренне ради Него. Напротив, Он многократно приумножает награду за такие благодеяния. Он не теряет награды тех, кто поступает хорошо. В Коране и Сунне содержатся указания на то, что награда за одно доброе дело приумножается многократно — в десять раз, в семьсот раз, и даже больше. Так Аллах благодарит Своих рабов! Он видит всё, что испытывают ради Него праведники, и с лихвой вознаграждает тех, кто трудится или отказывается от чего-нибудь ради Него. Он вдохновляет верующих на богоугодные поступки, а потом благодарит их за это, одаряя щедрыми благами, которых не видывал взор, о которых не слышали уши и которые даже не приходили на ум человеку. Он никому не обязан всё это делать, но по милости и щедрости Своей Аллах обязал Себя поступать именно так. Никто сверху не приказывает Ему относится к творениям таким образом: Его не спросят за то, что Он совершает, а они будут спрошены. Сура 21 «Пророки», аят 23. Он не обязан вознаграждать покорных рабов и наказывать ослушников. Награда — всего лишь проявление Его милости и добродетели, а наказание — проявление Его справедливости и мудрости. Однако Пречистый Аллах вменил Себе в обязанность определённое отношение к рабам, которое стало Его долгом. Таково обещание Того, Кто не нарушает обещаний. В Коране сказано: Ваш Господь предписал Себе быть Милосердным, и если кто из вас сотво­рит зло по своему неве­жеству, а затем раскается и станет совершать пра­ведные деяния, то ведь Он — Прощающий, Ми­лосердный. Сура 6 «Скот», аят 54. Нашим долгом было помогать верующим. Сура 30 «Ромеи», аят 47. Приверженцы Сунны считают, что Аллах ничего не должен рабам, и всё, что названо долгом Аллаха, было вменено Им Себе в обязанность. Вот почему ни один поступок, совершённый искренне ради Аллаха и в соответствии с предписаниями Пророка (да благословит его Аллах и приветствует), не пропадает у Него. Это и есть два основных условия принятия деяний. Таким образом, если на долю раба выпадает благо или ему удаётся избежать какого-либо несчастья, то это есть милость и великодушный дар Всевышнего Аллаха. Если Он оказывает людям милость, то делает им добро; если же Он подвергает их наказанию, то поступает мудро и справедливо. В любом из этих случаев Он заслуживает всяческой похвалы за Своё решение!" };

            ListOfNames["Господин, Самодостаточный"] = new Name() { One_Name = "Господин, Самодостаточный", Description = "Всевышний сказал: Скажи: «Он — Аллах Еди­ный, Аллах Самодостаточный. Сура 112 «Очищение веры», аяты 1–2. Пророк (да благословит его Аллах и приветствует) сказал: «Господин — это Всеблагой и Всевышний Аллах» [Абу Давуд, т. 4, с. 254; Ахмад, т. 3, с. 241; там же, т. 4, с. 25; хадис достоверный согласно аль-Арнауту: Фатх Аль-Маджид, с. 613]. Слово سيد�ال’ господин’ означает также ‘хозяин’, ‘владелец’, ‘глава’, ‘муж’. Так называют славных, достойных, благородных, выдержанных людей, помогающих другим в трудную минуту. Великий и Могучий Аллах — Господин, правящий творениями и покровительствующий им. Его господство истинно и пóлно, а все творения — всего лишь Его рабы. Это не означает того, что отдельных людей нельзя назвать господами, однако господство Всеблагого и Всевышнего Творца отличается от господства слабых творений. Слово الصمد’ самодостаточный’ обладает множеством значений, соответствующих различным интерпретациям этого прекрасного имени. Это — Тот, к Которому устремляются все творения, унижаясь перед Ним и испытывая бесконечную нужду в Нём. Это — Тот, Кто обладает совершенными знаниями и мудростью, сдержанностью и могуществом, величием и милосердием. Одним словом, Тот, Чьи качества совершенны и безупречны, к Кому устремляются творения для удовлетворения всех своих нужд. Он — Господин, Чьё главенство совершенно; Зна- ющий, Чьё знание совершенно; Выдержанный, Чья выдержка совершенна; Богатый, Чьё богатство совершенно; Могущественный, Чьё могущество совершенно; Славный, Чья слава совершенна; Великий, Чьё величие совершенно; Мудрый, Чья мудрость совершенна. Он — Тот, Чьи проявления славы и главенства безупречны. Он — Великий и Могучий Аллах. Этим качеством не обладает никто другой. Нет никого равного Ему, и нет никого подобного Ему. Пречист Аллах, Единственный, Всемогущий!" };

            ListOfNames["Одолевающий, Всемогущий"] = new Name() { One_Name = "Одолевающий, Всемогущий", Description = "Всевышний сказал: Скажи: «Аллах — Творец всякой вещи. Он — Единст­венный, Всемогущий». Сура 13 «Гром», аят 16. В тот день, когда они поя­вятся [на поверхности земли после воскрешения], ничто о них не скроется от Аллаха. Кому же при­надлежит власть сегодня?  Аллаху, Единственному, Всемогущему. Сура 40 «Прощающий», аят 16. Он — Одолевающий и нахо­дится над Своими рабами. Он — Мудрый, Ведающий. Сура 6 «Скот», аят 18. Одолевающий, Всемогущий — Тот, Кому подвластно всё сущее и подчинены все творения. Пред Его могуществом и волей преклоняется всё, что есть на небесах и земле. Всё движется и покоится только с Его дозволения. Происходит только то, что угодно Ему, а то, что не угодно Ему, не может произойти. Все творения беспомощны и нуждаются в Аллахе. Они не способны ни помочь себе, ни навредить. Способность подчинять подразумевает обладание Им такими атрибутами, как жизнь, могущество и способность на всё, поскольку подчинение творений и власть над ними не были бы полными, ес- ли бы Творец не обладал этими атрибутами. Таким образом, именно эти три качества делают власть полной и совершенной" };

            ListOfNames["Могучий, Выправляющий, Подчиняющий"] = new Name() { One_Name = "Могучий, Выправляющий, Подчиняющий", Description = "Всевышний сказал: Он — Аллах, и нет бо­жества, кроме Него, Ца­ря, Святого, Пречистого, Оберегающего, Храните­ля, Могущественного, Мо­гучего. Сура 59 «Сбор», аят 23. Это прекрасное имя несёт в себе три значения. 1. Аллах поддерживает слабых и выправляет сердца тех, кто унижается ради Него. Он помогает тем, кто надломился, избавляет от нужды нуждающихся и облегчает участь тех, кто сильно стеснён обстоятельствами. Он оказывает поддержку тем, кого постигло несчастье, помогая им проявить стойкость и терпение, и, если они справляются со своими обязанностями, Он возмещает их потери величайшей наградой. Его особой поддержки удостаиваются те, кто покорился Его величию и могуществу. Он выправляет сердца любящих Его, щедро одаряя их всевозможными благами, знаниями и верой. Если человек смиряется пред Ним, то помощь и поддержка Аллаха близка. О ней просит молящийся, когда говорит: «О Аллах, выправи моё положение», то есть приведи в порядок мои дела и убереги меня от всего неприятного. 2. Ему подчинено всё сущее, и все творения смиряются пред Ним, покоряются Его воле. 3. Он возвышен над всеми творениями. Таким образом, это прекрасное имя включает в себя значения имён Снисходительный, Всемогущий и Возвышенный. Иногда оно может указывать на четвёртое значение — возвышающийся над любыми пороками и недостатками, находящийся превыше того, чтобы иметь равных, соперников или партнёров в обладании божественными особенностями и правами." };

            ListOfNames["Требующий отчёта"] = new Name() { One_Name = "Требующий отчёта", Description = "Всевышний сказал: Но довольно того, что Ал­лах ведёт счёт. Сура 4 «Женщины», аят 6 Он один принимает реше­ние, и Он — Самый быст­рый в расчёте. Сура 6 «Скот», аят 62. Это прекрасное имя включает в себя несколько значений. Во-первых, оно указывает на то, что Аллах обеспе- чивает рабов всем, в чём они нуждаются для обретения мирских и духовных благ, а также избавления от вреда. Во-вторых, в более узком смысле оно означает, что Аллах проявляет особую заботу о тех рабах, которые блюдут благочестие и уповают на Него, приводя в порядок их духовный мир и земные дела. В-третьих, Аллах сохраняет добрые и злые деяния Его рабов, чтобы призвать их к ответу. И тогда праведники получат добро, а грешникам будет воздано злом за зло. Всевышний сказал: «О Пророк! Тебе и твоим верующим последователям довольно Аллаха» (сура 8 «Трофеи», аят 64), то есть Он защитит и тебя, и твоих последователей. Степень такой защиты зависит от того, насколько исправно он душой и телом выполняет приказы Посланника (да благословит его Аллах и приветствует) и поклоняется Всевышнему" };

            ListOfNames["Наставляющий"] = new Name() { One_Name = "Наставляющий", Description = "Коран гласит: Довольно того, что твой Господь наставляет на пря­мой путь и помогает. Сура 25 «Различение», аят 31. Воистину, Аллах настав­ляет верующих на прямой путь. Сура 22 «Хадж», аят 54. Наставляющий — Тот, Кто указывает Своим рабам на всё, что может принести им пользу и уберечь их от вреда; Кто обучает их тому, чего они не знают, и наставляет их на прямой путь; Кто внушает им богобоязненность и делает их сердца стремящимися к Нему и покорными Его воле. Под наставлением подразумевается доброе руководство, поучение. Руководство Аллаха для людей имеет четыре формы. 1. Руководство, данное всем, кто несёт ответственность за свои поступки. Здесь имеются в виду разум, естество (фитра) и необходимые знания, которые Аллах даровал всем людям в той или иной степени. Всевышний сказал: «Господь наш — Тот, Кто придал облик всякой вещи, а затем указал всему путь» (сура 20 «Та, ха», аят 50). 2. Руководство, данное людям через пророков, ниспосланное в Коране и т. д. Оно имеется в виду в аяте: «Мы создали среди них имамов, которые вели остальных по Нашему повелению прямым путём» (сура 32 «Земной поклон», аят 24). 3. Особое руководство (хидайат ат-тауфик), посредством которого Аллах наставляет тех, кто следует прямым путём. Оно упоминается в Его словах: «Тем, кто следует прямым путём, Он уве­личивает их приверженность прямому пути» (сура 47 «Мухаммад», аят 17); «Сердце того, кто уве­ровал в Аллаха, Он наставляет на прямой путь» (сура 64 «Взаимное обделение», аят 11); «Воис­тину, тех, которые уверовали и совершали пра­ведные деяния, Господь поведёт верным путём за их веру» (сура 10 «Юнус», аят 9); «А тех, кото­рые борются ради Нас, Мы непременно поведём Нашими путями» (сура 29 «Паук», аят 69). 4. Руководство в Последней жизни, указывающее путь к Раю. Оно подчёркивается в аятах: «Он ни­когда не сделает тщетными деяния тех, кто был убит на пути Аллаха. Он поведёт их прямым путём, исправит их положение» (сура 47 «Мухаммад», аяты 4–5); «Мы исторгнем из их сердец зло­бу, и под ними будут протекать реки. Они скажут: “Хвала Аллаху, Который привёл нас к этому”» (сура 7 «Ограды», аят 43). Эти четыре формы связаны друг с другом, и если человек лишён первого руководства, то он не сможет получить второе. Более того, он даже не несёт ответственности за свои поступки. Кто лишён второго руководства, тот не сумеет получить третье и четвёртое руководства. Следовательно, четвёртое руководство получит только тот, кто получил предыдущие три, а третье — только тот, кому были дарованы первые два. С другой стороны, если человек получил первое руководство, это не значит, что он обязательно получит последующие. Человек не в состоянии наставить других людей на прямой путь, но он может помолиться за них и указать им на него. Об этом говорится в словах Всевышнего: «Воистину, ты указываешь на прямой путь» (сура 42 «Совет», аят 52); «Мы созда­ли среди них предводителей, которые вели остальных по Нашему повелению прямым путём» (сура 32 «Земной поклон», аят 24); «У каждого народа есть наставник» (сура 13 «Гром», аят 7), то есть тот, кто призывает на прямой путь. Об остальных формах наставления говорится в аяте: «Воистину, ты не сможешь наставить на прямой путь тех, кого возлюбил» (сура 28 «Рассказ», аят 56). Наставляющий — Тот, Чьи слова и деяния есть верное руководство. Он выводит на прямой путь тех, кто пребывает в растерянности и заблуждении. Он разъясняет им правую стезю, обучает их тому, как нужно идти по ней, и вдохновляет на это. Изданные им вселенские законы, посредством которых создаются творения и уп- равляется Вселенная, есть сущая истина, ибо они преисполнены мудрости, красоты и совершенства. Его слова, произнесённые в Писаниях и устами посланников, содержат правдивые повествования и абсолютно справедливые предписания (веления и запреты). Ничья речь не может быть правдивее и прекраснее слов Аллаха! Коран гласит: «Слово твоего Господа исполнено правды и справедливости!» (сура 6 «Скот», аят 115), и это относится к Его повелениям и запретам. Это — величайшие и самые славные наставления рабам. Более того, ничто иное не может сделать человека праведным, и если кто либо пытается встать на прямой путь, обходя их, то Аллах вводит его в заблуждение. Не будет праведным тот, кто не следует Божьему руководству. Оно позволяет обрести правильные познания (рушд ‘ильми), когда человеку становится ясна истина, когда он узнаёт ответы на основные и второстепенные вопросы, а также понимает, что приносит ему пользу, а что вредит его мирскому и духовному благополучию. Оно же позволяет творить праведные дела (рушд ‘амали), ибо оно облагораживает души, очищает сердца и указывает на наилучшие поступки и нравственные качества. Оно вдохновляет на всё прекрасное, а также предостерегает от всего низкого и скверного. Тот, кто прислушивается к этому руководству, следует прямым путём; а тот, кто отворачивается от него, впадает в заблуждение. Аллах отправил к людям посланников и ниспослал Писания, содержащее абсолютное руководство, и они не смогут оправдаться перед Аллахом собственным неведением. Скольких же заблудших Господь вывел на прямой путь по милости Своей! Скольким пребывавшим в растерянности рабам Он указал на правую стезю! И в первую очередь — тем, кто уповал на Него и от всей души молил Его о верном руководстве, зная, что никто другой не способен вывести на прямой путь. Таким образом, руководство, которого Великий и Могучий Аллах лишил нечестивцев и неверующих, является третьим и четвёртым руководствами. Иными словами, это — вдохновение, благодаря которому люди следуют прямым путём, и награда в Последней жизни, то есть в Раю. По этому поводу в Коране сказано: «Ал­лах не ведёт прямым путём несправедливых людей» (сура 2 «Корова», аят 258); «Это потому, что они пред­почли мирскую жизнь Последней жизни. Воистину, Аллах не ведёт прямым путём неверующих людей» (сура 16 «Пчёлы», аят 107). В аятах также сообщается, что Пророк (да благословит его Аллах и приветствует) и остальные люди не способны наставлять на прямой путь. Здесь имеется в виду, что они не способны наделить рабов разумом, вдохновить их на следование прямым путём или ввести в Рай. Однако это не относится к мольбе за людей и указанию на правую стезю. Всевышний сказал: «Вести их прямым путём — не твоя обязанность, ибо Аллах ведёт прямым путём, кого пожелает» (сура 2 «Корова», аят 272). Я молю Аллаха вдохновить нас на совершение того, что Он любит и что угодно Ему. Только Он заслуживает обращения за помощью и упования, и нет силы и могущества ни у кого, кроме Аллаха." };

            ListOfNames["Справедливый Судья"] = new Name() { One_Name = "Справедливый Судья", Description = "Всевышний сказал: Потерпите, пока Аллах не рассудит между на­ми, ибо Он — Наилучший из судей. Сура 7 «Ограды», аят 87. Слово твоего Господа ис­полнено правды и справедливости! Никто не из­менит Его Слов. Сура 6 «Скот», аят 115. Воистину, Аллах повеле­вает блюсти справедливость, делать добро и ода­ривать родственников. Сура 16 «Пчёлы», аят 90. Пророк же сказал: «Поистине, Аллах — Справедливый Судья, и только Аллах принимает решения» [Абу Давуд, т. 4, с. 289; Насаи, т. 8, с. 226; хадис хороший согласно аль-Арнауту: Фатх Аль-Маджид, с. 517]. В другом аяте сказано: «Неуже­ли я пожелаю иного судью, помимо Аллаха, в то время как Он ниспослал вам Писание, подробно разъяснив [его]?» (сура 6 «Скот», аят 114). Пречистый Аллах справедливо и беспристрастно разрешает все разногласия между рабами как в этом мире, так и в Последней жизни. Он никому не причиняет ни малейшей несправедливости и не возлагает на людей ответственности за чужие грехи, наказывая рабов только за совершённые ими прегрешения. Он возмещает им попранные другими права, и поэтому каждый, чьи права были ущемлены, непременно получит всё, что ему полагалось. Его решения и предопределение справедливы. Его деяния справедливы и соответствуют мудрым законам Вселенной. В них нет даже примеси несправедливости, и, как мы уже отмечали, все они исполнены либо милости и сострадания, либо справедливости и мудрости. Что же касается разного рода мучений и унижений, которым подвергаются неверующие ослушники в этом мире, а также унизительной кары, приготовленной для них в Последней жизни, то всё это они заслужили своими деяниями. Поистине, Аллах наказывает только за грехи и только после того, как истина будет ясна рабам. Все Его слова справедливы. Его приказы являют собой абсолютное благо или приносят больше пользы, чем вреда, а Его запреты предостерегают только от того, что является абсолютным злом или приносит больше вреда, чем пользы. Права рабов не будут ущемлены ни тогда, когда Аллах будет вершить суд между ними, ни тогда, когда их деяния будут положены на Весы. Коран гласит: В День воскресения Мы установим справед­ ливые Весы, и ни с кем не поступят несправед­ливо. Если найдётся не­что весом с горчичное зёрнышко, Мы принесём его. Довольно того, что Мы ведём счёт! Сура 21 «Пророки», аят 47 Справедливый Судья — Тот, Чьим неотъемлемым качеством является справедливость, Чьи деяния, слова и решения абсолютно беспристрастны. Этот смысл вытекает из аята: «Воистину, мой Господь — на прямом пути» (сура 11 «Худ», аят 56). Его речи правдивы, а деяния исполнены либо справедливости, либо милости. Каждый из Его поступков правильный, а каждое из Его решений, разрешающее споры между рабами, справедливо. Его вердикты нисколько не ущемляют права рабов, и то же самое можно сказать о Божьих законах возмездия, награде и наказании." };

            ListOfNames["Святой Пречистый, Дарующий мир и благополучие"] = new Name() { One_Name = "Святой Пречистый, Дарующий мир и благополучие", Description = "Всевышний сказал: Он — Аллах, и нет божест­ва, кроме Него, Царя, Свя­того, Пречистого. Сура 59 «Сбор», аят 23. Эти два имени близки по смыслу. Первое из них образовано от глагола قدس’ почитать и возвеличивать кого-либо, отвергая наличие у него любых недостатков’. Второе имя образовано от слова سالمة’ благополучие’, ‘отсутствие недостатков’. Аллах не имеет подобных Себе среди творений. Он лишён любых недостатков и всего, что несовместимо с Его совершенством. Его свято почитают и возвеличивают, отвергая от Него всякое несовершенство. Нет никого, подобного Ему, и нет у Него пороков и изъянов. Среди Его качеств нет такого, которое бы противоречило высшему идеалу. Все эти проявления несовершенства полагается отрицать при описании Аллаха. Таким образом, Он лишён любого недостатка. Он превыше того, чтобы иметь подобных Ему или похожих на Него. У Него нет ни равных, ни противников. Среди Его качеств нет ни одного ущербного, ибо это самые безупречные, величественные и безмерные качества. Отрицание несовершенства будет неполным, если не признать обладание Аллахом такими качествами, как величественность и величие. Отрицание несовершенства — не самоцель. Оно необходимо для того, чтобы защитить совершенство Аллаха от неверных интерпретаций. Так, во времена невежества люди думали о Нём неподобающим образом. Если же раб восхваляет своего Господа атрибутами Пречистый, Святой или Всевышний, то он фактически подчёркивает отсутствие у Него любых пороков и Его абсолютное совершенство. Разъясняя смысл имени Ас-Салям (Пречистый), имам Ибн аль-Каййим сказал [Бадаи‘ аль-фаваид, т. 2, с. 150–152; там же, т. 2, с. 135–137; текст приводится с незначительными изменениями]: Аллах имеет больше прав на обладание этим именем, чем все остальные, потому что Он лишён любых пороков и недостатков. Он — Пречистый в полном смысле этого слова, тогда как творения могут обладать этим качеством лишь в ограниченном смысле. Сущность Аллаха пречиста и избавлена от любых изъянов и недостатков, которые только могут прийти на ум. Его качества также пречисты и безупречны. Его деяния пречисты и далеки от пороков, недостатков, зла и несправедливости. Ни одно из них нельзя назвать немудрым. Он — Пречистый в самом полном смысле этого слова и в самом широком его понимании. Следовательно, Он больше заслуживает того, чтобы носить это прекрасное имя, чем кто-либо другой. Такова истинная сущность отрицания всякого несовершенства, которое может быть приписано Ему, разъяснённая Аллахом и Его Посланником. Он не имеет ни супруги, ни сына, ни равного, ни подобного Себе, Он не имеет сотоварищей. И если задуматься над каждым из Его совершенных качеств в отдельности, то станет ясно, что оно избавлено от противоположных качеств несовершенства. Так, Его жизнь не имеет конца и не связана с дремотой или сном. Его самодостаточность и мощь несовместимы с усталостью и утомлением. Его знание несовместимо с неведением, забывчивостью, а также потребностью в напоминании и раздумьях. Его воля несовместима с отсутствием мудрости или пользы. Его слова не имеют ничего общего с ложью и несправедливостью; напротив, они исполнены правдивости и справедливости. Его богатство несовместимо с нуждой в ком-либо. Напротив, всё сущее испытывает нужду в Нём, тогда как Он вообще не нуждается в Своих творениях. Его власть настолько совершенна, что никто не посягает на неё. Никто не разделяет с Ним эту власть, не помогает Ему в управлении и даже не способен заступиться перед Ним без Его позволения. Его божественность далека от того, чтобы кто-либо разделял её с Ним. Он — Аллах, и нет достойного поклонения, кроме Него. Его сдержанность, снисходительность и прощение не являются проявлением нужды в чём-либо или слабости, лести или угодливости. В отличие от творений, эти Его качества есть проявление Его щедрости, доброты и великодушия. Его кара и возмездие, крепкая хватка и скорое наказание не имеют ниче-го общего с угнетением, вымещением зла, жестокостью и беспощадностью. Напротив, это — всего лишь проявление Его мудрости и справедливости. Такими действиями Аллах расставляет всё по местам, и Он заслуживает за это такой же похвалы, как и за Свою доброту, награду или милость. Более того, если бы грешники вместо наказания получали награду, то это противоречило бы Его мудрости и могуществу. Подвергая наказанию тех, кто того заслуживает, Аллах проявляет справедливость и являет Своё могущество. Он далёк от того, что думают о Нём Его невежественные враги, и никогда не поступает вопреки божественной мудрости. Его предопределение не является развлечением, несправедливостью или деспотизмом, и неправильно полагать, что оно противоречит Его безграничной мудрости. Его Шариат и религия очищены от противоречий, неясностей и путаницы. В них нет того, что вредит интересам рабов, что несовместимо с добрым и снисходительным отношением к ним или противоречит божественной мудрости. Напротив, Его Шариат целиком преисполнен мудрости, милосердия, блага и справедливости. Его дары не являются чем-то преподносимым взамен или ради ублажения кого-либо. Если же Он лишает кого-либо, то это не является следствием Его скупости или страха перед обеднением. Нет, напротив, Он одаряет рабов только по щедрости Своей (не вместо их добродетели и не из-за нужды в них) и лишает их благ только в силу Своей справедливости и мудрости, а не потому, что Он скуп или беден. Он вознёсся на Трон и находится над ним, но не нуждается в нём для того, чтобы опираться на него или сидеть на нём. Наоборот, это Трон и ангелы, которые несут его, нуждаются в Аллахе. Он не ощущает потребности в Троне, этих ангелах и всех остальных творениях. Он поднялся на него и находится над ним, но это не значит, что Он окружён или ограничен им. Он не нуждается ни в Троне, ни в чём-либо ином, и ничто не способно объять Его — пречист Он и велик! Он существовал до того, как был создан Трон, и нисколько не нуждался в нём. Он — Богатый, Достохвальный. Однако Он вознёсся на Трон и господствует над творениями, ибо это подчёркивает Его царствие и власть. Однако Он нисколько не нуждается ни в Троне, ни во всём остальном. Каждую ночь Он спускается на ближайшее небо, но это никоим образом не противоречит Его высокому положению и самодостаточности. Его совершенство избавлено от всего того, что ошибочно думают о нём те, кто лишает его качества всякого смысла или уподобляет их качествам творений. Он превыше того, чтобы оказаться под чем-либо или быть ограниченным чем-либо. Аллах превыше всего, что несовместимо с Его совершенством. Его богатство, слух и зрение не имеют ничего общего с тем, что предполагают мушаббихиты и измышляют те, кто лишают божественные качества всякого смысла. Он приближает к Себе творения не потому, что он слаб, подобно им самим. Напротив, это является чистым проявлением Его милости, благодетели и добра. Всевышний сказал: «Скажи: “Хвала Аллаху, Который не взял Себе сына, Который ни с кем не делит власть, Которому не нужен защитник от унижения”» (сура 17 «Ночной перенос», аят 111). Он не сказал, что у Него вообще нет приближённых (вали), но среди его приближённых нет таких, в ком Он нуждается для того, чтобы избежать слабости и унижения. Его любовь к возлюбленным и приближённым лишена таких проявлений любви творений друг к другу, как потребность в возлюбленном, заискивание перед ним или извлечение пользы от близости с ним. Она далека от всего, что наговаривают те, кто лишают Его качества всякого смысла. Его Рука, Лик и другие качества также не имеют ничего общего с измышлениями мушаббихитов и тех, кто лишает божественные качества всякого смысла. Призадумайся же над тем, как прекрасное имя Ас-Салям (Пречистый) подчёркивает отсутствие у Всеблагого и Всевышнего Аллаха любых недостатков. И сколько людей, знающих это имя, не имеют представления обо всех этих секретах и значениях! О Аллах, Ты — Единственный, к Кому обращаются с мольбой!" };

            ListOfNames["Добродетельный, Дарующий"] = new Name() { One_Name = "Добродетельный, Дарующий", Description = "Всевышний сказал: Мы взывали к Нему прежде. Воистину, Он — Добродетельный, Мило­сердный. Сура 52 «Гора», аят 28 Господь наш! Не укло­няй наши сердца в сторо­ну после того, как Ты на­ставил нас на прямой путь, и даруй нам милость от Себя, ведь Ты — Дарующий! Сура 3 «Семейство ‘Имрана», аят 8. Добродетельный, Дарующий — Тот, Чья доброта и щедрость объемлют всё сущее. Он — Прекрасный Покровитель, Чья доброта бесконечна, Чьи дары неисчислимы. Добродетель — Его неотъемлемое качество, а его следствие — все зримые и незримые блага, в которых творения не перестают нуждаться даже на мгновение ока. Добродетель Всевышнего Аллаха бывает всеобщей и особой. Первый вид упоминается в аятах: «Господь наш! Ты объял всякую вещь милостью и знанием» (сура 40 «Прощающий», аят 7); «Моя милость объемлет вся­кую вещь» (сура 7 «Ограды», аят 156); «Все блага, кото­рые вы имеете, — от Аллаха» (сура 16 «Пчёлы», аят 53). В этом смысле Аллах делает добро праведникам и грешникам, обитателям небес и жителям земли, тем, кто несёт ответственность за свои деяния, и всем остальным. Особая добродетель Аллаха — это милость, оказываемая богобоязненным рабам. В Коране сказано: Моя милость объемлет всякую вещь. Я предпишу её для тех, которые бу­дут богобоязненны, станут выплачивать закят и уве­руют в Наши знамения, которые последуют за по­сланником, неграмотным пророком. Сура 7 «Ограды», аяты 156–157. Воистину, милость Аллаха близка к творящим добро. Сура 7 «Ограды», аят 56. Пророк Сулейман во время мольбы сказал: «Введи меня по Своей милости в число Твоих праведных рабов» (сура 27 «Муравьи», аят 19). Это — особая милость, о которой молили пророки и их последователи. Она подразумевает наделение верой и знаниями, праведностью и благополучием, вечным счастьем и преуспеянием. Это — величайшая цель, к которой стремятся избранные творения. Пречистый Аллах обладает щедростью, то есть Его милость велика, а дары изобильны. Его щедрость тоже делится на два вида: всеобщая и особая. Первая распространяется на всё сущее; все творения наслаждаются Его милостью и всевозможными благами. Особой щедрости удостаиваются те, кто взывает к Аллаху устами и всем своим состоянием подчёркивает свою нужду в Нём. Это могут быть как праведники, так и грешники, как мусульмане, так и неверующие. Аллах одаряет каждого, кто взывает к Нему, удовлетворяя их просьбы, потому что Он — Добродетельный, Милосердный. «Все блага, которые вы имеете, — от Аллаха. И если вас касается бе­да, то вы громко зовёте Его на помощь» (сура 16 «Пчёлы», аят 53). Проявлением Его безграничной щедрости являются уготованные для Его возлюбленных райские блага, которых не видывал взор, о которых не слышали уши, которые даже не приходили на ум человеку." };

            ListOfNames["Милостивый, Милосердный, Великодушный, Самый великодушный, Сострадательный"] = new Name() { One_Name = "Милостивый, Милосердный, Великодушный, Самый великодушный, Сострадательный", Description = "Всевышний сказал: Хвала Аллаху, Господу ми­ров, Милостивому, Милосердному. Сура 1 «Открывающая Коран», аяты 2–3. Кто благодарен, тот благо­дарен во благо себе. А ес­ли кто непризнателен, то ведь мой Господь — Бо­гатый, Великодушный. Сура 27 «Муравьи», аят 40. Аллах предостерегает вас от Самого Себя. Аллах со­страдателен к рабам. Сура 3 «Семейство ‘Имрана», аят 30. Шейх ‘Абд-ар-Рахман ибн Насыр ас-Са‘ди сказал [Тафсир Са‘ди, т. 5, с. 621]: Милостивый, Милосердный, Добродетельный, Великодушный, Щедрый, Сострадательный, Дарующий — эти имена обладают схожим смыслом, и каждое из них свидетельствует о том, что Господь обладает милостью, добротой, щедростью и великодушием. Его милосердие безгранично, Его дары щедры, а Его милость объемлет всё сущее в соответствии с божественной мудростью. Но значительная и самая славная часть её предназначена только для правоверных, поскольку Он сказал: «Моя милость объемлет всё сущее. Я предпишу её для тех, которые будут богобоязненны» (сура 7 «Ограды», аят 156). Все блага и щедроты — результат Его милости, щедрости и великодушия, все прелести мирской и будущей жизней — тоже результат Его милости. Комментируя слова Всевышнего «Читай, ведь твой Гос­подь — Самый великодушный. Он научил посредст­вом письменной трости — научил человека тому, чего тот не знал» (сура 96 «Сгусток», аяты 3–5), Ибн Таймийя сказал [Маджму‘ аль-фатава, т. 16, с. 293–296; текст приводится с незначительными изменениями]: Аллах сообщил, что великодушие является одним из Его качеств, и назвал Себя Самым великодушным после того, как сообщил о сотворении [мира и человека — Пер.]. Тем самым Господь дал понять, что оказывает милость творениям и позволяет им достичь славных целей. Всевышний также сказал: «…Который сотворил [всё сущее] и всему придал соразмер­ность, Который предопределил судьбу [творений] и указал путь» (сура 87 «Всевышний», аяты 2–3); «Господь наш — Тот, Кто придал об­лик всякой вещи, а затем указал всему путь» (сура 20 «Та, ха», аят 50); «…Который сотворил меня и ведёт прямым путём» (сура 26 «Поэты», аят 78). Сотворение подразумевает начало, а великодушие — конец. В суре «Аль-Фатиха» вначале называется имя Господь миров, а потом — имена Милостивый, Милосердный. Слово «великодушие» объединяет в себе достоинства и добродетели. Оно не просто указывает на щедрость — щедрость лишь дополняет его смысл. И как доброе отношение к другим есть совершенство добродетели, так и великодушие есть изобилие щедрости. <…> Пречистый Аллах сообщил, что Он — Самый великодушный. Это слово употреблено в форме имени прилагательного в превосходной степени и с определённым артиклем, что указывает на то, что только Аллах заслуживает такой атрибут. Если бы перед ним не было определённого артикля, как если бы аят звучал أكرم وربك, то это не подчёркивало бы ограничение его смысла. Употребление определённого артикля указывает на то, что это имя относится только к Аллаху. Причём Аллах не сказал, что Он великодушнее кого-либо или чего-либо. Он употребил атрибут в самом широком смысле, ничем не ограничив его смысл. Это значит, что Его великодушие проявляется в наивысшей степени: оно не имеет предела и лишено всяких недостатков." };

            ListOfNames["Судия, Раскрывающий, Дарующий победу"] = new Name() { One_Name = "Судия, Раскрывающий, Дарующий победу", Description = "Всевышний сказал: Скажи: «Наш Господь со­берёт всех нас, а потом рассудит между нами по справедливости, ибо Он — Раскрывающий (или Судия), Знающий». Сура 34 «Сава», аят 26. Слово فاحت фатих означает ‘судья’. Слово تاحّف фаттах имеет схожий, но усиленный смысл. Это — Добродетельный и Щедрый Судья, и это качество проявляется, с одной стороны, в законах религии и справедливого возмездия, а с другой — в законах предопределения. Установив законы религии, Аллах устами посланников узаконил всё, в чём нуждаются люди, — в том числе для того, чтобы следовать прямым путём. Верша законы возмездия, Аллах разрешает споры между Его пророками и их противниками, между Его возлюбленными и их врагами. Он спасает пророков и их последователей, оказывая им милость, и подвергает на- казанию их врагов, унижая их. А в День воскресения Он вынесет приговор в отношении творений, воздав всем творениям за их деяния. Установив законы предопределения, Аллах предопределил всё доброе и злое, хорошее и дурное, что произойдёт с Его рабами, — всё, что они получат и чего лишатся. Всевышний сказал: «Никто не удержит милость, которую Аллах открывает людям. А то, что Он удер­живает, никто не может ниспослать после Него. Он — Могущественный, Мудрый» (сура 35 «Творец», аят 2). Всевышний Господь — Знающий Судия, раскрывающий перед покорными рабами сокровищницы Его щедрости и великодушия, а перед Его врагами — то, что противно этому. И всё потому, что Он руководствуется либо милостью, либо справедливостью." };

            ListOfNames["Кормилец, Наделяющий уделом"] = new Name() { One_Name = "Кормилец, Наделяющий уделом", Description = "Слово رزاق раззак является формой с усилением смысла от слова رازق разик, что указывает на щедрость и изобилие. Ар-Раззак (Наделяющий уделом) — одно из имён Пречистого Аллаха. Коран гласит: Воистину, Аллах — Наде­ляющий уделом. Сура 51 «Рассеивающие прах», аят 58. Нет на земле ни едино­го живого существа, кото­рого Аллах не обеспечи­вал бы пропитанием. Сура 11 «Худ», аят 6. Пророк (да благословит его Аллах и приветствует) же сказал: «Поистине, Аллах — Устанавливающий цены, Удерживающий, Простирающий, Наделяющий уделом» [Абу Давуд, т. 3, с. 272; Тирмизи, т. 3, с. 596; Ибн Маджа, т. 2, с. 741; Ахмад, т. 3, с. 156, 286; Дарими, т. 2, с. 165; хадис достоверный согласно аль-Альбани: Сахих Тирмизи, т. 2, с. 32; Сахих Ибн Маджа, т. 2, с. 15. Приведена версия Абу Давуда]. Ниспосылаемый рабам удел бывает всеобщим и особым. Всеобщий удел получают все творения. Это — то, в чём они нуждаются для существования и продолжения жизни. Аллах облегчил им поиски пропитания и позволил их организмам усваивать его. Он доставляет все необходимые вещества к каждому из органов, независимо от его величины. Всё это относится к праведникам и грешникам, мусульманам и неверующим. Более того, это относится ко всем людям, джиннам, ангелам и животным. С другой стороны, в отношении творений, несущих ответственность за свои деяния, общий удел может быть дозволенным, за который рабы не несут ответственности, и запрещённым. Этот последний тоже именуется уделом и даром. Иными словами, мы называем заработок уделом от Аллаха, независимо от того, каким способом он был добыт: дозволенным или запрещённым. Абсолютно всё считается уделом от Него. Особым уделом называются те блага, которые приносят пользу не только в этом мире, но и в Последней жизни. Люди получают такой удел через Посланника (да благословит его АЛлах и приветствует), и его можно разделить на два вида. К первому относится удел для сердец: истинные знания и вера. Человеческие сердца испытывают безграничную нужду в познании истины, поклонении Аллаху и обожествлении Его. Благодаря этим качествам души обогащаются и перестают быть ущербными. Ко второму виду относится удел для плоти — дозволенные блага, за которые люди не несут ответственности. Удел, которым Аллах отмечает верующих, о котором они молят Его, касается как материальных, так и духовных благ. Поэтому, когда раб просит своего Господа об уделе, ему следует иметь в виду оба вида благ. Прося об уделе, мы просим всё то, что способно привести в порядок сердце, то есть знания, верное руководство и веру, подразумевающую праведные поступки и высокие нравственные качества. Вместе с тем мы просим то, что может привести в порядок наши тела, то есть здоровую и дозволенную (халяль) пищу, которая не обременит нас и не повлечёт за собой неприятных последствий." };

            ListOfNames["Живой, Вседержитель"] = new Name() { One_Name = "Живой, Вседержитель", Description = "Пречистый Аллах сказал: Аллах — нет божества, кроме Него, Живого, Все­держителя. Сура 2 «Корова», аят 255. Алиф. Лам. Мим. Аллах — нет божества, кроме Него, Живого, Все­держителя. Сура 3 «Семейство ‘Имрана», аяты 1–2. Лица смирятся перед Жи­вым, Вседержителем, и разочарование постигнет тех, кто понесёт [бремя] несправедливости. Сура 20 «Та, ха », аят 111. Эти два прекрасных имени идеально подходят друг другу, и поэтому Аллах упомянул их вместе сразу в нескольких аятах Корана. Объясняется это тем, что они указывают на все качества совершенства. Живой — Тот, Чья жизнь совершенна, и это указывает на все качества, связанные с божественной сущностью, такие как знания и могущество, сила и воля, величие и возвышенность. Вседержитель — Тот, Чьё господство (кайюмийя) совершенно. Это качество имеет два значения: а) самодостаточность, величие и отсутствие всякой нужды в творениях; б) поддержание земли, небес и всех обитающих на них творений. Аллах сотворил их, наделив всем необходимым для благополучного существования. Он нисколько не нуждается в них, но они всесторонне нуждаются в Нём. Живой Вседержитель — Тот, Кто обладает всеми качествами совершенства и творит, что пожелает." };

            ListOfNames["Свет небес и земли"] = new Name() { One_Name = "Свет небес и земли", Description = "Всевышний сказал: Аллах — Свет небес и зем­ли. Его свет [в душе верующего] подобен нише, в которой находится све­тильник. Светильник за­ключён в стекло, а стек­ло подобно жемчужной звезде. Он возжигается от благословенного олив­кового дерева, которое не [освещается солнцем] только с востока или толь­ко с запада. Его масло го­тово светиться даже без соприкосновения с огнём. Один свет поверх другого! Аллах направляет к Свое­му свету, кого пожелает. Сура 24 «Свет», аят 35. А Пророк (да благословит его Аллах и приветствует) говорил: «О Аллах, хвала Тебе одному. Ты — свет небес, земли и тех, кто [обитает] в них…» [Бухари, цит. по: Фатх Аль-Бари, т. 13, с. 464; Муслим, т. 1, с. 532]. В другом хадисе говорится: «Поистине, Могучий и Великий Аллах не спит, и не подобает Ему спать. Он поднимает и опускает весы[34]. Совершённые ночью деяния возносят к Нему прежде, чем деяния, совершённые днём, а деяния, совершённые днём, возносят к Нему прежде, чем деяния, совершённые ночью. Его покрывало из света, и если Он приподнимет его, то сияние Его Лика сожжёт все творения, на которые попадёт Его взор» [Муслим, т. 1, с. 161]. Шейх ‘Абд-ар-Рахман ибн Насыр ас-Са‘ди писал: Одним из великих имён и качеств Аллаха является свет. Аллах, обладающий величием и великодушием, также обладает светом и сиянием. Если приподнять покрывало с Его Лика, то сияние его сожжёт все творения, на которые попадёт Его взор. Он освещает все миры. Свет Его Лика уничтожает мрак. Благодаря нему освещены Трон, Престол, семь небес и вся Вселенная. Божественный свет бывает материальным и духовным. Благодаря первому освещены все миры. Духовный свет освещает сердца и души посредством Корана и Сунны, которые принёс Мухаммад (да благословит его Аллах и приветствует). Изучение их и совершение праведных деяний в соответствии с ними освещает сердца, слух и взоры. Этот свет не покидает раба ни в этом мире, ни в Последней жизни. Аллах направляет к Своему свету, кого пожелает. Он назвал Себя Светом небес и земли, а также нарёк светом Своё Писание, Своего Посланника и Своё откровение. <…> Ибн аль-Каййим предостерегал от обольщения, в которое впали представители суфизма, не различающие между светом [божественных] качеств с одной стороны и светом веры и знания с другой стороны. Они стали поклоняться, не различая между истиной и ложью, не имея истинных познаний, и свет поклонения замерцал в их сердцах, ведь поклонение всегда освещает сердца. Они приняли его за свет божественной сущности, стали терять сознание и говорить ужасные слова. Всё это было следствием невежества, обольщения и заблуждения. Но обладающие знанием и верой, различающие между ложью и заблуждением, знают разницу между светом божественной сущности и качеств и между сотворённым светом — как материальным, так и духовным. Они признают, что свет качеств Создателя не отделяется от Его сущности и не передаётся творениям. Аллах бесконечно далёк от всего, что говорят о нём несправедливые люди! В то же время сотворённый свет характеризует творения в зависимости от породивших его причин и вложенного в него смысла. Так, Аллах освещает сердце верующего, когда его вера достигает совершенства, и тот начинает постигать сущность вещей, отличая истину ото лжи. Этот свет становится основным компонентом жизни раба, придавая ему силы для постижения знаний и совершения праведных деяний. Помимо того, он избавляется от сомнений, вредящих его знаниям и убеждённости, и страстей, порождаемых небрежностью и темнотой. Его сердце, слова и поступки наполняются светом, который окружает его со всех сторон. В то же время неверующие, лицемеры, те, которые возражают [против истины] или беспечно отворачиваются от неё, ступают ощупью во мраке, и чем больше причин, обрекающих их на такое положение, тем гуще окружающий их мрак. И только Аллах способен наставить на прямой путь!" };

            ListOfNames["Господь"] = new Name() { One_Name = "Господь", Description = "Всевышний сказал: «Скажи: “Неужели я стану искать другого господа помимо Аллаха, в то время как Он — Господь всякой вещи ?”» (сура 6 «Скот», аят 164). Господь — Тот, Кто заботится обо всех рабах, управляя их судьбами и одаряя их всевозможными благами.Особую заботу Аллах проявляет о Своих избранниках, приводя в порядок их сердца, души и нравственный облик.Поэтому в своих молитвах они часто называют Его этим славным именем, надеясь на Его особую заботу." };

            ListOfNames["Аллах"] = new Name() { One_Name = "Аллах", Description = "Аллах — Тот, Кто заслуживает обожествления и поклонения. Все творения обязаны обожествлять Его и поклоняться Ему, потому что Он обладает божественными качествами, то есть качествами совершенства." };

            ListOfNames["Царь, Властелин, Обладающий властью"] = new Name() { One_Name = "Царь, Властелин, Обладающий властью", Description = "Всевышний сказал: Превыше всего Аллах, Ис­тинный Царь! Нет божест­ва, кроме Него, Господа благородного Трона. Сура 23 «Верующие», аят 116. …на седалище истины возле Всемогущего Влас­телина. Сура 54 «Луна», аят 55. Скажи: «О Аллах, Вла­дыка царства! Ты дару­ешь власть, кому пожела­ешь, и отнимаешь власть, у кого пожелаешь. Ты воз­величиваешь, кого поже­лаешь, и унижаешь, кого пожелаешь. Всё благо — в Твоей Руке. Воистину, Ты способен на всякую вещь». Сура 3 «Семейство ‘Имрана», аят 26. Он — Тот, Кто обладает властью. Это качество проявляется в величии, гордости, могуществе и правлении. Он единолично творит и повелевает, вознаграждает и наказывает. Ему принадлежат высший и низший миры, и все творения являются Его рабами и нуждаются в Нём. Он — Истинный Господь, Истинный Царь, Истинный Бог. Он сотворил творения благодаря Его господству, подчинил их благодаря Его власти и обратил в рабов благодаря тому, что достоин поклонения. Задумайся же над той славой и тем величием, которые самым удивительным и прекрасным образом заключены в этих трёх именах. Господь людей, Царь людей, Бог людей — эти три выражения включают в себя все принципы веры и значения Его прекрасных имён. Господь — Всемогущий, Творец, Создатель, Дарующий облик, Живой, Вседержитель, Знающий, Слышащий, Видящий, Добродетельный, Наделяющий благами, Щедрый, Одаряющий и Лишающий, Вредящий и Приносящий пользу, Выдвигающий вперёд и Отбрасывающий назад. Он вводит в заблуждение и наставляет на прямой путь, кого пожелает, наделяет счастьем и делает несчастным, кого пожелает, одаряет могуществом и унижает, кого пожелает. Господство Аллаха имеет и другие значения, указывающие на соответствующие им прекрасные имена, которых Он достоин. Царь — Повелевающий и Запрещающий, Дарующий могущество и Унижающий. Он управляет делами Его рабов, как Ему угодно, и распоряжается ими, как пожелает. Значения этого качества указывают на такие прекрасные имена, как Выправляющий, Гордый, Справедливый Судья, Судия, Понижающий и Повышающий, Дарующий могущество и Унижающий, Великий, Величественный, Большой, Требующий отчёта, Славный, Покровитель, Превознёсшийся, Обладающий властью, Справедливый, Собирающий и др. Бог — Тот, Кто обладает всеми совершенными качествами и славными атрибутами. Это имя объединяет все остальные прекрасные имена, и поэтому утверждение о том, что имя «Аллах» произошло от слова اإلل) Бог), вполне справедливо. Этого мнения придерживался Сибавейхи и большинство его учеников, и достоверно, что имя «Аллах» объединяет в себе все значения прекрасных имён и возвышенных качеств. Таким образом, упомянутые имена включают в себя значения всех прекрасных имён Аллаха, и тот, кто ищет защиты посредством этих трёх имён, действительно заслуживает того, чтобы его защитили и уберегли от наущений шайтана, исчезающего при поминании Господа. И коли Аллах — наш Единственный Господь, Царь и Бог, то в трудные минуты нам не следует устремляться ни к кому, кроме Него, и никто не сможет защитить нас от Него. И нет у нас другого бога, достойного поклонения, кроме Него. Посему никто другой не заслуживает того, чтобы к нему взывали и на него надеялись, чтобы его боялись и любили. Никто другой не заслуживает унижения, смирения и упования. Надежды и страха, мольбы и упования заслуживает только тот, кто заботится о тебе, устраивает твои дела и покровительствует тебе, либо тот, кому ты приходишься истинным рабом, либо тот, кто является твоим богом, без кого тебе не прожить даже мгновения ока, в ком ты нуждаешься больше, чем в жизни и душе. Аллах — Единственный Господь, и нет на свете другого господа. Он — Истинный Царь людей, и все они приходятся Ему рабами. Он — Истинный Бог, Бог людей, и никто иной не заслуживает их поклонения. И коли Он — Господь людей, их Царь и Бог, то им не подобает искать защиты и помощи у кого-либо иного. Им не должно прибегать к покровительству кого-либо, кроме их Покровителя, ведь им достаточно Его помощи и поддержки. Он устраивает все их дела благодаря Его господству, власти и божественности. Как может раб во время несчастий и при приближении врага не искать защиты у своего Господа, Царя и Бога?!" };

            ListOfNames["Единственный, Единый"] = new Name() { One_Name = "Единственный, Единый", Description = "Всевышний сказал: Скажи: «Он — Аллах Еди­ный». Сура 112 «Очищение веры», аят 1. Скажи: «Аллах — Творец всякой вещи. Он — Единст­венный, Всемогущий». Сура 13 «Гром», аят 16. Единственный, Единый — Тот, Кто обладает всеми качествами совершенства и не разделяет их с другими. Рабы обязаны признавать Его единство в душе, на словах и на деле. Для этого они должны верить в Его абсолютное совершенство и посвящать обряды поклонения только Ему одному. Единый — Тот, Кто единичен в обладании всяким совершенством, славой и величием, красотой и похвальностью, мудростью и милосердием, а также другими качествами совершенства. Он не имеет равных и подобных Себе в этом, и нет никого, кто соответствовал бы Ему в этом в той или иной форме. Он один в своём существовании и господстве. Его знание и могущество, величие и слава, красота и похвальность, мудрость, милосердие и остальные качества уникальны, и в каждом из них Он отличается высшей степенью совершенства. Проявлением Его единственности является то, что Он самодостаточен. Это значит, что Он — Совершенный Господь, Великий Господин, обладающий всеми атрибутами совершенства. И каждый из них присущ Ему в самой совершенной форме, и даже часть этих атрибутов творения не способны ни постичь своими сердцами, ни выразить словами." };

            ListOfNames["Гордый"] = new Name() { One_Name = "Гордый", Description = "Всевышний сказал: «Он — Аллах, и нет божества, кро­ме Него, Царя, Святого, Пречистого, Оберегающего, Хранителя, Могущественного, Могучего, Гордого. Пре­чист Аллах и далёк от того, что они приобщают в сото­варищи» (сура 59 «Сбор», аят 23). Гордый — Тот, Кто по причине Своего величия и Своей гордости превозносится над злом, недостатками и пороками." };

            ListOfNames["Творец, Созидатель, Создатель, Дарующий облик"] = new Name() { One_Name = "Творец, Созидатель, Создатель, Дарующий облик", Description = "Всевышний сказал: Он — Аллах, Творец, Создатель, Дарующий облик. У Него — самые прекрасные имена. Сура 59 «Сбор», аят 24. Воистину, твой Господь — Созидатель, Знающий. Сура 15 «Хиджр», аят 86. Он — Тот, Кто творит всё сущее, придаёт творениям соразмерность и одаряет их обликом по Своей мудрости, заслуживая тем самым похвалу, Кто всегда обладал и всегда будет обладать этим великим качеством." };

            ListOfNames["Оберегающий, Дарующий безопасность"] = new Name() { One_Name = "Оберегающий, Дарующий безопасность", Description = "Это — Тот, Кто восхваляет Себя, возвещая о Своих качествах совершенства и совершенстве Своего величия и Своей красоты, Кто отправляет посланников и ниспосылает Писания со знамениями и доказательствами, Кто подтверждает правдивость Своих посланников посредством чудес и доводов, свидетельствующих об их правдивости и достоверности всего, что они проповедуют." };

            ListOfNames["Хранитель"] = new Name() { One_Name = "Хранитель", Description = "Это — Тот, Кто ведает о самых скрытых событиях и тайных помыслах, Чьи знания охватывают всё сущее. Аль-Багави писал: «Это — Тот, Кто свидетельствует о Его рабах на основании их деяний. Это мнение высказали Ибн ‘Аббас, Муджахид и др. Так называют того, кто наблюдает за чем-то » [Тафсир Багави, т. 4, с. 326]." };

            ListOfNames["Всеобъемлющий"] = new Name() { One_Name = "Всеобъемлющий", Description = "Всевышний сказал: Аллаху принадлежит то, что на небесах, и то, что на земле. Аллах объемлет всякую вещь. Сура 4 «Женщины», аят 126. Но если вы будете терпе­ливы и богобоязненны, то их козни не причинят вам никакого вреда. Воис­тину, Аллах объемлет всё, что они совершают. Сура 3 «Семейство ‘Имрана», аят 120. Всеобъемлющий — Тот, Чьи знания, могущество, милость и власть распространяются на всё сущее. Он ведает обо всём, что можно узнать; Он видит всё, что можно увидеть; Он слышит всё, что можно услышать. Его могуществу подвластно всё сущее, а Его милость объемлет всех обитателей земли и небес. Все творения смиряются перед Ним, и все существа подчиняются Ему." };

            ListOfNames["Дарующий пропитание, Наблюдающий"] = new Name() { One_Name = "Дарующий пропитание, Наблюдающий", Description = "Всевышний сказал: «Аллах наблюдает за всякой вещью» (сура 4 «Женщины», аят 85). Дарующий пропитание, Наблюдающий — Тот, Кто одаряет всякую тварь всем, что необходимо для её существования, Кто наделяет творения уделом и распределяет его, как пожелает, — мудрым и похвальным образом. Ар-Рагиб аль-Асфахани писал [Муфрадат, с. 414]: Словом القوت’ пропитание’ (мн. ч. أقوات (обозначают ‘то, что необходимо для поддержания жиз- ни’. Всевышний сказал: «…распределил на ней пропитание для страждущих» (сура 41 «Разъяснены», аят 10). Выражение وته قوات означает قاته يق ‘накормил его тем, что необходимо для поддержания жизни’. Выражение قيتهِي أقاته означает ‘наделил его тем, что необходимо для поддержания жизни’. В хадисе сказано: «С человека довольно греха, если он не заботится о тех, кто находится на его иждивении». Всевышний сказал: «Аллах наблю­дает за всякой вещью» (4:85). Существует мнение, что это означает, что Аллах властен над всякой вещью или наблюдает за всякой вещью. Суть этого сводится к тому, что Он заботится о всякой твари, оберегая её и наделяя тем, что необходимо для существования. В словаре «Камус аль-мухит» говорится, что مقيت — это «тот, кто оберегает какую-либо вещь, наблюдает за ней и властен над ней, кто наделяет каждого тем, что необходимо для существования» [с. 202]. Ибн ‘Аббас сказал: «Тот, Кто властен над всякой вещью, Кто воздаёт каждому по заслугам». Муджахид сказал: «Тот, Кто наблюдает за всякой вещью». Катада сказал: «Тот, Кто оберегает всякую вещь». Существует мнение, что это — Тот, Кто наделяет каждую живую тварь тем, что необходимо для её существования (см. [Тафсир Багави, т. 1, с. 457]). Ибн Кясир, комментируя слова Всевышнего «Ал­лах наблюдает за всякой вещью» (сура 4 «Женщины», аят 85), писал [Тафсир Ибн Кясир, т. 1, с. 531; текст приводится с незначительными изменениями]: Это значит, что Он оберегает всякую вещь. Муджа- хид сказал: «Тот, Кто наблюдает за всякой вещью». В одной из версий, переданной от его имени, говорится: «Тот, Кто требует отчёта от всякой вещи». Существуют мнения, что это означает ‘властный над каждой вещью’, ‘дарующий удел’, ‘наделяющий каждого человека по труду" };

            ListOfNames["Попечитель и Хранитель"] = new Name() { One_Name = "Попечитель и Хранитель", Description = "Всевышний сказал: Аллах — Творец всякой вещи. Он — Попечитель и Хранитель всякой вещи. Сура 39 «Толпы», аят 62. Попечитель и Хранитель — Тот, Кто управляет делами Своих творений благодаря Своему знанию, абсолютному могуществу и безграничной мудрости; Кто покровительствует Своим возлюбленным рабам, облегчает им путь ко всему лёгкому и отдаляет их от всего тяжкого; Кто устраивает их дела. И того, кто вверяет Ему свою судьбу, Он избавляет от любых трудностей: Аллах — Покровитель тех, которые уверовали. Он выводит их из мраков к свету. Сура 2 «Корова», аят 257" };

            ListOfNames["Обладающий величием и великодушием"] = new Name() { One_Name = "Обладающий величием и великодушием", Description = "Это — Тот, Кто обладает величием, гордостью, милосер- дием и щедростью; Кто делает добро всем творениям в целом и правоверным в частности; Кто оказывает милость Своим возлюбленным избранникам, которые почитают, возвеличивают и любят Его. Всевышний сказал: Благословенно имя Гос­пода твоего, Обладающе­го величием и великоду­шием! Сура 55 «Милостивый», аят 78" };

            ListOfNames["Собирающий людей"] = new Name() { One_Name = "Собирающий людей", Description = "Всевышний сказал: Господь наш! Ты соберёшь людей в День, в котором нет сомнения. Воистину, Аллах не нарушает обеща­ния. Сура 3 «Семейство ‘Имрана», аят 9. Собирающий людей — Тот, Кто соберёт людей в День, в котором невозможно усомниться, Кто соберёт их деяния и дарованные им блага, не оставив без внимания ни малого, ни большого; Кто благодаря Своему безграничному могуществу и всеобъемлющему знанию соберёт истлевшие тела первых и последних людей." };

            ListOfNames["Первосоздатель небес и земли"] = new Name() { One_Name = "Первосоздатель небес и земли", Description = "Всевышний сказал: Он — Первосоздатель небес и земли. Ког­да Он принимает реше­ние, то стоит Ему сказать: «Будь!», как это сбы­вается. Сура 2 «Корова», аят 117. Он — Тот, Кто создал небеса и землю, которые никогда прежде не существовали, придал им совершенный и удивительный вид и установил на них восхитительный и точный порядок. Всевышний сказал: Он — Тот, Кто создаёт творения в первый раз, а затем воссоздаёт их . Сура 30 «Ромеи», аят 27 Он создал людей в первый раз, чтобы испытать их и увидеть, чьи деяния будут лучше, а потом Он воссоздаст их, чтобы вознаградить добром тех, кто творил добро, и наказать тех, кто поступал дурно. С другой стороны, Он создаёт творения одно за другим, постоянно возобновляя процесс сотворения. Всевышний сказал: Воистину, твой Господь вершит то, что пожелает. Сура 11 «Худ», аят 107 Он вершит то, что поже­лает. Сура 85 «Созвездия Зодиака», аят 16. Его могущество и способности совершенны, а Его воля неотвратима. Он совершает всё, что пожелает, и никто не может помешать или воспротивиться этому. Он не нуждается в соратниках и помощниках, и стоит Ему сказать: «Будь!» — как желаемое сбывается. Он способен совершить всё, что угодно, однако Его желания подчинены Его божественной мудрости и похвальны. Ему присуще совершенное могущество, и любое Его желание беспрекословно исполняется, но всё, что Он совершил и совершает, обязательно преисполнено мудрости." };

            ListOfNames["Достаточный"] = new Name() { One_Name = "Достаточный", Description = "Всевышний сказал: Разве Аллаха не достаточ­ но для Его раба? Сура 39 «Толпы», аят 36. Достаточный — Тот, Кто обеспечивает рабов всем, что им необходимо и в чём они нуждаются, Кто особым образом помогает тем, кто верует в Него, уповает на Него и просит Его о помощи в религиозных и мирских делах." };

            ListOfNames["Объемлющий"] = new Name() { One_Name = "Объемлющий", Description = "Всевышний сказал: Аллах же обещает вам прощение от Него и ми­лость. Аллах — Объемлю­щий, Знающий. Сура 2 «Корова», аят 268. Объемлющий — Тот, Кто обладает всеобъемлющими качествами и атрибутами. Никто не способен восхвалить Его так, как Он того заслуживает, кроме Него Самого. Его величие, власть и царство необъятны, Его милость и добродетель велики, а Его щедрость и великодушие безграничны." };

            ListOfNames["Истинный"] = new Name() { One_Name = "Истинный", Description = "Это — Тот, Чьи качества и божественная сущность истинны. Его существование есть необходимость, и Его качества и атрибуты могут быть только совершенными. Ничто не может просуществовать без Него. Величие, красота, совершенство и доброта присущи Ему изначально и будут присущи Ему всегда. Его слова — истина, Его деяния — истина, встреча с Ним и Его посланниками — истина. Его Писания — истина, Его религия — истина, поклонение Ему одному и непризнание наряду с Ним никаких сотоварищей — тоже истина, и всё, что связано с Ним, есть истина. Это потому, что Аллах есть Истина, а то, чему они поклоняются помимо Него, есть ложь, а также потому, что Аллах — Воз­вышенный, Большой. Сура 22 «Хадж», аят 62. Скажи: «Истина — от вашего Господа. Кто хочет, пусть верует, а кто не хо­чет, пусть не верует». Сура 18 «Пещера», аят 29. Таков Аллах, ваш Истин­ный Господь! Что может быть за истиной, кроме заблуждения? Сура 10 «Юнус», аят 32 Скажи: «Явилась истина, и сгинула ложь. Воистину, ложь обречена на погибель». Сура 17 «Ночной перенос», аят 81. В тот день Аллах воз­даст им в полной мере по их истинному счёту, и они узнают, что Аллах есть Явная Истина. Сура 24 «Свет», аят 25. Его великие качества и Его деяния истинны. Поклонение Ему истинно, как истинны Его обещания и угрозы. Его расчёт будет беспристрастен, и ни с кем не поступят несправедливо." };

            ListOfNames["Прекрасный"] = new Name() { One_Name = "Прекрасный", Description = "Пророк (да благословит его Аллах и приветствует) сказал: «Поистине, Аллах прекрасен и любит прекрасное» [Муслим, т. 1, с. 93]. Это значит, что прекрасна Его божественная сущность, и прекрасны Его имена, качества и деяния. Ни одно творение не способно выразить словами хотя бы часть Его божественной красоты. Даже обитатели Рая — несмотря на то что они пребудут среди вечных благ, удовольствий, радостей и отрад, которые невозможно оценить должным образом, — забудут об этих удовольствиях, когда увидят своего Господа и станут наслаждаться Его красотой. Все остальные радости померкнут рядом с этим, и они пожелают, чтобы такое состояние продолжалось вечно. Красота и свет Аллаха сделают их ещё более красивыми. Их сердца наполнятся неугасающим желанием ещё раз взглянуть на Господа, а день добавки доставит им радость, от которой их души будут готовы воспарить. Его имена тоже прекрасны. Более того, это — самые лучшие и красивые из всех существующих имён. Всевышний сказал: У Аллаха — самые пре­красные имена. Посе­му взывайте к Нему по­ средством их. Сура 7 «Ограды», аят 180. Знаешь ли ты другого с таким именем? Сура 19 «Марьям», аят 65. Все они указывают на то, что Он заслуживает наилучшей похвалы и обладает абсолютной славой и совершенством. Среди Его имён нет таких, которые указывают не только на совершенство, но и на недостатки. Его качества тоже прекрасны, ибо все они совершенны и заслуживают похвалы и восхваления. Это — всеобъемлющие атрибуты, обладающие самым широким смыслом, касающиеся великого множества вещей. В особенности это относится к таким качествам, как милосердие, доброта, щедрость или великодушие. Его деяния тоже прекрасны — все без исключения, потому что они исполнены либо доброты и милости, за которые Его надлежит восхвалять и благодарить, либо справедливости. И за эту справедливость Его также полагается восхвалять, ведь она исполнена мудрости и заслуживает похвалы. Он ничего не совершает ради забавы, по неразумению или понапрасну. Он никого не обижает и не угнетает. Напротив, все Его поступки преисполнены доброты, верного руководства, милосердия, праведности и справедливости. «Воистину, мой Господь — на прямом пути» (сура 11 «Худ», аят 56). Благодаря Его совершенству, которое выше всякой похвалы, все Его деяния совершенны, а предписания — прекрасны. Его творения замечательны, а свершения — безупречны. «Таково творение Аллаха, Который выполнил всё в со­вершенстве» (сура 27 «Муравьи», аят 88); «…Который превосходно создал всё, что сотворил» (сура 32 «Земной поклон», аят 7); «Чьи решения могут быть луч­ше решений Аллаха для людей убеждённых?» (сура 5 «Трапеза», аят 50). Вселенная содержит много прекрасного, и источником её красоты является Всевышний Аллах. Он облачил её в благолепие и наделил красотой. Аллах же ещё более красив, ведь тот, кто наделяет красотой, больше достоин этого атрибута. Он — источник всякой красоты в этом мире и Последней жизни — как духовной, так и внешней. В особенности это относится к безграничной красоте мужчин и женщин, которые попадут в Рай. Если бы кисть одной из чернооких гурий открылась этому миру, то её свет затмил бы солнце, подобно тому как свет солнца затмевает звёзды. Разве Тот, Кто одарил их такой красотой и совершенством, не за- служивает того, чтобы обладать красотой, которой нет равных?! Это — ясный и приемлемый логический аргумент по данному важному вопросу, а также по остальным вопросам, связанным с божественными атрибутами. Всевышний сказал: «Описание Аллаха — самое возвышенное» (сура 16 «Пчёлы», аят 60). Поэтому, если творение наделено каким-либо совершенным качеством, это не означает, что оно имеет определённые недостатки, потому что Аллах, который наделил его этим качеством, имеет на него больше прав. Между Ним и творениями нет никакой зависимости, Его сущность и качества никоим образом не зависят от сущности и качеств Его творений. Тот, Кто одарил людей слухом, зрением, жизнью, знанием, могуществом и красотой, имеет больше прав на обладание этими атрибутами. Разве может кто-либо описать всё благолепие Аллаха, если самый осведомлённый о Нём среди людей (да благословит его Аллах и приветствует) говорил: «Не сосчитать мне всей хвалы Тебе. Ты заслуживаешь той хвалы, которой Сам восхваляешь Себя» [Муслим, т. 1, с. 352]. Пророк (да благословит его Аллах и приветствует) также сказал: «Его покрывало из света, и если Он приподнимет его, то сияние Его Лика сожжёт все творения, на которые попадёт Его взор» [Муслим, т. 1, с. 161]. Пречист Аллах и бесконечно далёк от того, что наговаривают на Него несправедливые грешники, отрицающие Его совершенство! И довольно с них того убытка, что они не способны постичь Его и упиваться Его любовью! В достоверном хадисе сообщается, что Пророк (да благословит его Аллах и приветствует) сказал: «Никто не способен стерпеть столько оскорблений, сколько слышит Аллах. Они приписывают Ему сына, а Он одаряет их благополучием и уделом» [Бухари, цит. по: Фатх Аль-Бари, т. 10, с. 511; Муслим, т. 4, с. 2160]. В «Сахихе» аль-Бухари приводится и другой хадис: «Сын Адама считает Меня лживым, хотя ему не следует делать этого, и он оскорбляет Меня, хотя ему не следует поступать так. Что касается его обвинений во лжи, то он говорит: “Он не вернёт меня [к жизни] подобно тому, как создал меня [в первый раз]”. Что же касается его оскорблений, то он говорит, будто у Меня есть сын. Я — Единый, Самодостаточный, Тот, Кто не родил и не был рождён, Кому нет равных» [Бухари, цит. по: Фатх Аль-Бари, т. 8, с. 168, 739]. Всевышний Аллах щедро наделяет благами всех рабов — как покорных, так и ослушников. Однако ослушники не перестают противиться Ему, обвинять во лжи Его и Его посланников, пытаясь потушить свет Его религии. Аллах терпеливо сносит всё то, что они говорят и совершают. Они совершают одно злодеяние вслед за другим, а Он не перестаёт одарять их благами. Его терпение совершенно, потому что оно исходит от совершенной мощи, самодостаточности, милости и доброты. Благословен же Милосердный Господь, Которому нет подобных, Который любит тех, кто терпелив, и помогает им во всех начинаниях." };

            ListOfNames["Мягкий, Добрый"] = new Name() { One_Name = "Мягкий, Добрый", Description = "Это имя встречается в достоверном хадисе: «Воистину, Аллах добр и любит доброту. Он позволяет добиться благодаря доброте того, чего не добиться суровостью или чем-либо иным» [Муслим, т. 4, с. 2004]. Деяния Всевышнего Аллаха исполнены доброты и мягкости. Он создал творения постепенно, одно за другим, руководствуясь божественной мудростью и добротой, хотя Ему было по силам создать всю Вселенную разом, за один миг. Если задуматься над тем, как Аллах последовательно создавал творения и ниспосылал законы, то можно открыть для себя много удивительного. Тот, кто проявляет терпение, поступая спокойно и степенно, руководствуясь законами сотворения Вселенной и следуя путём Пророка (да благословит его Аллах и приветствует), — а это есть его прямой путь, — легко добивается успеха. В особенности это относится к тем, кто наставляет людей, призывая их к добру и запрещая им предосудительное, потому что они нуждаются в доброте и кротости. И если люди обижают человека своими скверными речами, а он оберегает свой язык от брани, отвечая им добрым и кротким образом, то ему удаётся отвести от себя оскорбления лучше, чем тому, кто отвечает им такими же злыми словами и поступками. Помимо того, он обретает душевный покой, и умиротворение, набирается уравновешенности и выдержки. Могучий и Великий Аллах помогает рабам, если молят Его о спасении. Передают со слов Анаса ибн Малика, что однажды в пятницу, когда Посланник Аллаха (да благословит его Аллах и приветствует) читал проповедь, в мечеть вошёл мужчина и сказал: «О Посланник Аллаха! Скотина погибла, и мы лишились средств существования. Помолись же Аллаху, чтобы Он ниспослал дождь». Тогда Посланник Аллаха (да благословит его Аллах и приветствует) поднял руки и сказал: «О Аллах, напои нас! О Аллах, напои нас! О Аллах, напои нас!» [Бухари, цит. по: Фатх Аль-Бари, т. 2, с. 507; Муслим, т. 2, с. 612]. Могучий и Великий Аллах помогает рабам во время несчастий и трудностей. Он оказывает поддержку всем творениям, когда им приходится нелегко, когда они попадают в беду и печалятся. Он кормит голодных и одевает раздетых, избавляя их от скорби и оказывая им помощь тогда, когда они сильно нуждаются в ней. Он внимает мольбам тех, кто опечален и взывает к Нему в состоянии глубокой скорби и полной беспомощности. Он спасает тех, кто молит о спасении. Упоминаний о том, как Аллах избавляет от печали, спасает от беды и облегчает тяготы, в Коране и Сунне очень много." };

            ListOfNames["Стыдливый, Покрывающий, Защищающий"] = new Name() { One_Name = "Стыдливый, Покрывающий, Защищающий", Description = "Это имя упомянуто в словах Посланника (да благословит его Аллах и приветствует) : «Поистине, Аллах — Стыдливый. Он испытывает стыд перед рабом, если тот воздевает руки к Нему, и они опускаются пустые» [Абу Давуд, т. 2, с. 78; Тирмизи, т. 5, с. 556; хадис достоверный согласно аль-Альбани: Сахих Ибн Маджа, т. 2, с. 331; Сахих Тирмизи, т. 3, с. 179]. Пророк (да благословит его Аллах и приветствует) также сказал: «Поистине, Могучий и Великий Аллах — Выдержанный, Стыдливый, Покрывающий. Он любит, когда стыдятся и прикрываются. И если кто-либо из вас купается, пусть прикроется» [Абу Давуд, т. 4, с. 40; Насаи, т. 1, с. 200; Байхаки, т. 1, с. 198; Ахмад, т. 4, с. 224; хадис достоверный согласно аль-Альбани: Ирва аль-галиль, т. 7, с. 367; Сахих Насаи, т. 1, с. 87]. Милость, великодушие, совершенство и выдержка Аллаха настолько велики, что Он позволяет рабам открыто совершать грехи, несмотря на то что они сильно нуждаются в Нём. Более того, даже для совершения грехов они пользуются благами своего Господа. Он настолько богат, что не нуждается ни в одном из творений. Вместе с тем, Он великодушен и стыдится позорить, срамить и наказывать рабов. Он покрывает их гре- хи, создавая соответствующие причины, проявляет снисходительность к ним и прощает. Он добивается любви Своих рабов, одаряя их благами, а они вызывают Его гнев своим ослушанием. Он делает им добро каждое мгновение, но в ответ к Нему поднимаются их дурные дела. Грехи и скверные поступки беспрестанно восходят к Великодушному Царю. Всевышний стыдится подвергнуть мучениям того, кто состарился, будучи мусульманином, и стыдится отвергнуть мольбу того, кто воздевает к Нему руки. Он призывает рабов взывать к Нему и обещает принимать их мольбы. Он — Стыдливый и Покрывающий, и Он любит тех, кто стыдится и покрывает свои и чужие недостатки. Он покрывает в этом мире и в Последней жизни того, кто покрывает мусульманина, и поэтому нежелательно, чтобы раб рассказывал другим о своих грехах. Напротив, ему следует покаяться втайне, не открывая постыдных секретов другим. Поистине, Аллаху ненавистен тот, кто засыпает, совершив грех, а наутро рассказывает о том, что Аллах покрыл. Всевышний сказал: Воистину, тем, которые любят, чтобы о верующих распространялась мер­зость, уготованы мучи­тельные страдания в этом мире и в Последней жизни. Сура 24 «Свет», аят 19. Всё это — одно из значений имени Аль-Халим (Выдержанный). Он проявляет сдержанность даже в отношении неверующих, нечестивцев и ослушников, равно как не спешит наказывать тех, кто поступает несправедливо. Он предоставляет им отсрочку, чтобы они покаялись, но не оставляет их без возмездия, если они продолжают бесчинствовать, не возвращаясь на прямой путь." };

            ListOfNames["Бог"] = new Name() { One_Name = "Бог", Description = "Это — Тот, Кто обладает всеми совершенными качествами и славными атрибутами. Это имя объединяет все остальные прекрасные имена, и справедливо суждение о том, что имя «Аллах» произошло от слова 'al-ilah' и что оно объединяет в себе все значения прекрасных имён и возвышенных качеств. А лучше всего об этом известно Аллаху. Всевышний сказал: Воистину, Аллах — Единст­венный Бог. Он пречист и далёк от того, чтобы у Него был сын. Ему при­ надлежит то, что на не­бесах, и то, что на земле. Довольно того, что Ал­лах является Попечителем и Хранителем! Сура 4 «Женщины», аят 171." };

            ListOfNames["Удерживающий, Сжимающий, Простирающий, Одаряющий"] = new Name() { One_Name = "Удерживающий, Сжимающий, Простирающий, Одаряющий", Description = "Всевышний Аллах сказал: «Аллах удерживает и щедро одаряет, и к Нему вы будете возвращены» (сура 2 «Корова», аят 245). Пророк, мир ему и благословение Аллаха, сказал: «Поистине, Аллах — Устанавливающий цены, Удерживающий, Простирающий, Наделяющий уделом» (Ахмад, Абу Давуд, ат-Тирмизи и Ибн Маджа). Он также сказал: «Кому Аллах желает добра, того Он наделяет пониманием религии.Аллах одаряет, а я делю» (ал - Бухари). Он также сказал: «Поистине, Великий и Могучий Аллах не спит, и не подобает Ему спать. Он поднимает и опускает весы.Совершенные ночью деяния возносят к Нему прежде, чем деяния, совершенные днем, а деяния, совершенные днем, возносят к Нему прежде, чем деяния, совершенные ночью» (Муслим). Всевышний сказал: «Скажи: “О Аллах, Владыка царства!Ты даруешь власть, кому пожелаешь, и отнимаешь власть, у кого пожелаешь.Ты возвеличиваешь, кого пожелаешь, и унижаешь, кого пожелаешь.Все благо — в Твоей Руке.Воистину, Ты способен на всякую вещь”» (сура 3 «Семейство Имрана», аят 26). Пророк, мир ему и благословение Аллаха, сказал: «Поистине, Аллах возвышает посредством этого Писания одних людей и низвергает других» [Бухари, цит. по: Фатх Аль-Бари, т. 1, с. 255; Муслим, т. 1, с. 414]. А после завершения намаза, повернувшись к людям, Пророк, мир ему и благословение Аллаха, говорил: «Нет божества, кроме Аллаха, у Которого нет сотоварища. Ему принадлежит владычество, Ему надлежит хвала, и Он способен на всякую вещь. О Аллах, никто не лишит того, что Ты даровал, и никто не дарует того, чего Ты лишил, и бесполезно перед Тобой могущество могущественного» [Бухари, цит. по: Фатх Аль-Бари, т. 2, с. 507; Муслим, № 593]. Эти прекрасные атрибуты являются именами с противоположным смыслом, которые надлежит употреблять для восхваления Аллаха только вместе, потому что абсолютное совершенство выражается лишь в сочетании этих качеств. С одной стороны, Аллах удерживает пропитание и души, с другой — Он щедро дарует удел, простирает милость и отпускает души. Он возвышает тех, кто руководствуется знаниями и требованиями веры, и унижает их врагов. Он наделяет  могуществом тех, кто покорен Ему, и это могущество подлинное, ибо тот, кто покорен Аллаху, величественен, даже если у него нет богатства и помощников. Он унижает тех, кто ослушается Его и враждует с Ним, и в земном мире, и в Последней жизни. Поэтому сердце грешника наполнено унижением, если даже внешне он выглядит величественно и не ощущает своего истинного положения из-за того, что он погряз в страстях. Таким образом, истинное величие можно обрести, только повинуясь Аллаху, тогда как ослушание Его обрекает человека на унижение и бесчестие. «Никто не окажет почтения тому, кого унизит Аллах» (сура 22 «Хадж», аят 18); «Если кто жаждет мо­гущества, то ведь могущество целиком принадлежит Аллаху» (сура 35 «Творец», аят 10); «Могущество при­ суще Аллаху, Его Посланнику и верующим»  (сура 63 «Лицемеры», аят 8). Всевышний Аллах — Одаряющий и Лишающий. Никто не одарит тем, чего лишил Аллах, и никто не лишит того, чем наделил Он. Все эти деяния подчинены божественной справедливости и мудрости, все они заслуживают похвалы. Если Аллах унижает или лишает кого-либо, того требует сама божественная мудрость, и никто не сможет привести довод против Него. Если же Он возвышает кого-либо, щедро одаряя его благами, то это является сущим проявлением Его милости. Раб обязан признавать мудрость Аллаха. Он должен признавать Его милость и благодарить Его языком, сердцем и телом. Аллах — Единственный, Кто вершит такие дела, и все они происходят сообразно Его предопределению. Он создал причины как для возвышения рабов  и оказания им  милости,  так  и для их унижения. Кто совершает поступки-причины, тот удостаивается их следствия, и каждому облегчается путь к тому, ради чего он был создан. Для тех, кому суждено стать счастливыми, облегчаются поступки, совершаемые счастливыми людьми, а для тех, кому суждено стать несчастными, облегчаются поступки, совершаемые несчастными рабами. Это обязывает рабов веровать в Единственного Аллаха, полагаться на Него в обретении всего желанного и усердствовать в совершении полезных поступков, ибо они есть предмет мудрости Аллаха." };
            
            ListOfNames["Выдвигающий вперёд, Отбрасывающий назад"] = new Name() { One_Name = "Выдвигающий вперёд, Отбрасывающий назад", Description = "Последней мольбой, которую читал Пророк, мир ему и благословение Аллаха, после ташаххуда перед произнесением таслима, были слова: «О Аллах! Прости мне то, что я совершил прежде и что оставил на потом, что совершил тайно и что сделал явно, то, в чём я преступил границы, и то, о чём Ты ведаешь лучше меня. Ты — Выдвигающий вперёд, и Ты — От- брасывающий назад. Нет божества, кроме Тебя!» [Муслим, т. 1, с. 535]. Как мы уже отмечали, Выдвигающий вперёд и Отбрасывающий назад — имена, которые не используются для описания Аллаха по отдельности, но только в паре, потому что совершенство выражается в сочетании этих качеств. Всевышний — Тот, Кто выдвигает вперёд и отбрасывает назад, кого пожелает, в соответствии с Его мудростью. Выдвижение вперёд может быть связано с земными, бытовыми вопросами. В этом смысле Аллах помещает одни творения перед другими, позволяет причине предшествовать следствию, а условию — обусловленному. Примеров того, как одни творения предшествуют другим, бесконечно много. С другой стороны, выдвижение вперёд может носить религиозный характер. В этом смысле пророки превосходят остальные творения, одни из пророков превосходят других, одни из людей превосходят других в знаниях, вере, праведных поступках, нравственности и прочих качествах. И, наоборот, одни из них уступают другим в тех или иных качествах, сообразно мудрости Аллаха. Эти два качества и им подобные связаны с божественной сущностью, потому что они присущи Самому Аллаху и характеризуют Его. Вместе с тем они относятся к качествам, связанным с божественными деяниями, потому что выдвижение вперёд и отодвигание назад связаны с сущностью, действиями и качествами творений, но происходят по воле Аллаха и благодаря Его могуществу. Это — правильная классификация  атрибутов Творца: одни из них связаны с божественной сущностью, а другие — с Его деяниями. Эти последние связаны с Его речами и поступками, но характеризуют и Его сущность. Могучий и Великий Аллах сказал: Если Аллах коснётся  тебя бедой, то ни кто не сможет избавить от неё, кроме Него. Если же Он коснётся тебя бла­гом, то ведь Он способен на всякую вещь. Сура 6 «Скот», аят 17). Скажи: «Кто властен помочь вам чем­нибудь перед Аллахом, если Он за­ хочет навредить вам или захочет принести вам пользу?» О нет! Аллах ве­дает о том, что вы совер­шаете. Сура 48 «Победа», аят 11. Вредящий и Приносящий пользу тоже относятся к именам, которые употребляются в сочетании друг с другом. Всевышний Аллах приносит пользу тем из Его рабов, кому пожелает, в вопросах духовной и мирской жизни. Он вредит только тем, кто своими поступками обрекает себя на такое обращение. Его действия подчинены божественной мудрости и установленным Им законам бытия, и они определяются причинами, которые приводят рабов к их следствиям. Всевышний Аллах установил для творений цели и то, чего следует желать в духовных и мирских делах. Он установил причины и пути, ведущие к ним, приказав рабам следовать этими путями. Он облегчил их совершенным образом, и тот, кто встаёт на него, достигает благой цели. Тот же, кто отказывается следовать этим путём или частично отступает от него, кто не остаётся верным ему полностью или делает упущения, тот не достигает желанной цели. Пусть же он не укоряет никого, кроме себя самого, ибо нет у него оправдания перед Аллахом. Он наделил его слухом и зрением, сердцем, силой и способностями. Он указал ему на две стези, разъяснив причины и следствия. Он не мешал ему идти путём, ведущим к добру в духовных и мир ских вопросах. Но человек сам отступил от этой стези, заслужив укора и порицания. Следует знать, что все атрибуты, связанные с божественными деяниями, исходят от трёх качеств: совершенного могущества, неукоснительно исполняемой воли и безграничной мудрости. Все эти качества существуют в Аллахе и характеризуют Его. Они проявляются в том, что Аллах творит во Вселенной, выдвигая вперёд и отбрасывая назад, принося пользу и причиняя вред, одаряя и лишая, унижая и возвышая. При этом нет разницы между тем, что познаётся чувством и разумом, между тем, что имеет отношение к религии и мирской жизни. В этом, а не в том, что предполагают заблуждающиеся последователи каляма, заключается смысл качеств, связанных с божественными деяниями."};

            ListOfNames["Разъясняющий"] = new Name() { One_Name = "Разъясняющий", Description = "Слово املبني мубин является причастием действительного залога от глагола بي ’разъяснять’. Это — тот, кто разъясняет что-либо либо словом, либо поступком. Слова البينة беййина и البيان байан означают соответственно 'ясное указание, воспринимаемое разумом или чувствами' и 'разъяснение'. Речь была названа байан, потому что посредством неё люди раскрывают свои мысли и намерения. В этом значении данное слово использовано в аяте: «Это есть разъяснение людям» (сура 3 «Семейство ‘Имрана», аят 138). Могучий и Великий Аллах разъясняет рабам путь праведности, указывает им на деяния, которые заслуживают награды или наказания. Он разъясняет им то, что следует совершать, и то, чего совершать не следует. Аллах назвал Себя Аль-Мубин (Разъясняющий[37]) в аяте: «В тот день Аллах воздаст им в полной мере по их истинному счёту, и они узнают, что Аллах есть Явная Истина» (сура 24 «Свет», аят 25). Он разъяснил Своим рабам пути праведности и заблуждения, предостерёг их от последних, отправил к ним посланников и ниспослал Писания для того, чтобы им всё стало ясно. Могучий и Великий Аллах сказал: «Воистину, тех, которые скрывают ниспосланные Нами ясные знаме­ния и верное руководство после того, как Мы разъяс­нили это людям в Писании, проклянёт Аллах и про­ клянут проклинающие» (сура 2 «Корова», аят 159). Это суровое предостережение обращено к тем, кто скрывает принесённое посланниками ясное знание, указывающее на правильные цели и верное руководство, приносящее пользу душам, к тем, кто скрывает его после того, как Всевышний Аллах разъяснил его в Писаниях, ниспосланных Его посланникам. Могучий и Великий Аллах сказал: Те, которые лишены зна­ния, говорят: «Почему Аллах не говорит с на­ми? Почему знамение не приходит к нам?» Та­кие же слова произно­сили их предшественни­ки. Их сердца похожи. Мы уже разъяснили знамения людям убеж­дённым! Сура 2 «Корова», аят 118. Так Аллах разъясняет вам знамения, — быть может, вы поразмыслите. Сура 2 «Корова», аят 266 Аллах желает дать вам разъяснения, повести вас путями ваших предшест­венников и принять ваши покаяния, ведь Аллах — Знающий, Мудрый. Сура 4 «Женщины», аят 26. Явились к вам от Аллаха свет и Ясное Писание. Посредством его Аллах ведёт по путям мира тех, кто стремится снискать Его довольство. Он выво­ дит их по Своему соизволению из мраков к свету и наставляет их на прямой путь. Сура 5 «Трапеза», аяты 15–16. Посмотри, как Мы разъ­ясняем им знамения. А за­ тем посмотри, до чего они отвращены [от истины]. Сура 5 «Трапеза», аят 75. Аллах разъясняет вам знамения. Аллах — Знающий, Мудрый. Сура 24 «Свет», аят 18. Аллах разъясняет людям религиозные законы и мудрость предопределения. Он ведает о том, что приносит пользу Его рабам, а Его законы и предопределение преисполнены мудрости. Его мудрость абсолютна, а доводы неопровержимы. Всевышний сказал: «Так Аллах разъясняет вам Свои знамения, — быть может, вы последуете пря­мым путём» (сура 3 «Семейство ‘Имрана», аят 103); «Аллах не вводит в заблуждение людей, которым Он указал прямой путь, пока Он не разъяснит им, чего они должны опасаться. Воистину, Аллах знает о всякой вещи» (сура 9 «Покаяние», аят 115). Он сообщил о Себе и Своём справедливом решении: не вводить людей в заблуждение, пока до них не дойдёт знание о пророческом послании, пока им не предоставят неопровержимых доводов." };

            ListOfNames["Дарующий, Щедрый"] = new Name() { One_Name = "Дарующий, Щедрый", Description = "Это — одно из тех прекрасных имён, которыми Аллаха нарёк Его Посланник, мир ему и благословение Аллаха. Передают со слов Анаса ибн Малика, что Пророк, да благословит его Аллах и приветствует, услышал, как один человек говорил: «О Аллах, поистине, я прошу Тебя посредством того, что Тебе надлежит хвала, и нет божества, кроме Тебя. Один Ты, и нет у Тебя сотоварищей. О Дарующий! О Первосоздатель небес и земли! О Обладающий величием и великодушием! О Живой, Вседержитель! Я молю Тебя о Рае и прошу защитить меня от Ада». Тогда Пророк, да благословит его Аллах и приветствует, сказал: «Он попросил Аллаха посредством величайшего из Его имён, и если Его просят посредством этого имени, то Он отвечает» [Абу Давуд; Тирмизи; Насаи; Ибн Маджа; Ибн Хиббан; Ахмад; аль-Хаким; хадис достоверный согласно аль-Альбани: Сахих Насаи, т. 1, с. 279; Сахих Ибн Маджа, т. 2, с. 329; Салят ан-наби, с. 204]. Ибн аль-Асир в книге «Нихая фи гариб аль-хадис» сказал [Ибн аль-Асир 1, т. 4, с. 365]: Аль-Маннан (Дарующий, Щедрый) — Тот, Кто одаряет благами. Это имя образовано от слова означающего ‘дар’, а не от слова, означающего ‘признательность, благодарность’. В языке часто используется в значении ‘добро, сделанное тому, кто не требует вознаграждение’. В хадисе, переданном аль-Бухари и другими, сообщается, что Пророк,мир ему и благословение Аллаха, сказал: «Поистине, нет среди людей такого, кто ради меня жертвовал бы собой и своим имуществом больше, чем Абу Бакр ибн Абу Кухафа. И если бы мне пришлось выбрать любимого друга из людей, то я выбрал бы Абу Бакра, но любовь в исламе лучше» [Бухари, цит. по: Фатх Аль-Бари, т. 1, с. 558]. Могучий и Великий Аллах — Дарующий, и у Него великие дары. Он одаряет жизнью, разумом, речью, прерасным обликом. Он наделяет благами щедро и обильно. Он сказал: «Если вы станете считать милости Аллаха, то не сможете сосчитать их. Воистину, человек неспра­ведлив и неблагодарен» (сура 14 «Ибрахим», аят 34). Речь идёт о самой большой милости, которую Аллах оказал Своим рабам. Её совершенно справедливо можно назвать первоосновой всех остальных Божьих благ. Это — пришествие Посланника Аллаха, мир ему и благословение Аллаха, посредством которого Аллах спас людей от заблуждения и уберёг от гибели. Всевышний сказал: «Аллах уже ока­зал милость верующим, когда отправил к ним Проро­ка из них самих, который читает им Его аяты, очища­ ет их и обучает их Писанию и мудрости, хотя прежде они находились в очевидном заблуждении» (сура 3 «Семейство ‘Имрана», аят 164). Могучий и Великий Аллах — Тот, Кто сотворил рабов и одарил их уделом, здоровьем и благополучием. Он щедро наделил их зримыми и незримыми благами, и одним из величайших и самых полезных благ, — более того, первоосновой всех благ, — является обращение в ислам и веру. Это лучше всего остального. Слова «Аллах уже оказал милость верующим» означают, «что Аллах соблаговолил верующим, которые поверили Ему. Он — Дарующий, Благоволящий». Слово означает ‘великая милость’. Аль-Асфахани сказал, что это — «весомое благодеяние». Оно бывает двух видов. Первое — когда один делает добро другому. Говорят ’такой-то оказал милость такому-то’ — если он одарил его бесчисленными благами. В этом смысле следует понимать слова Всевышнего: «Аллах уже оказал милость верующим» (сура 3 «Семейство ‘Имрана», аят 164); «Такими вы были прежде, но Аллах оказал вам милость, и посему удостоверяй­тесь. Воистину, Аллах ведает о том, что вы совершае­те» (сура 4 «Женщины», аят 94); «Мы оказали милость Мусе и Харуну» (сура 37 «Выстроившиеся в ряды», аят 114); «Мы и прежде оказывали тебе милость» (сура 20 «Та, ха », аят 37); «Мы пожелали оказать милость тем, кто был унижен на земле, сделать их предводите­лями и наследниками» (сура 28 «Рассказ», аят 5); «Аллах же оказал нам милость и уберёг нас от мучений в Аду» (сура 52 «Гора», аят 27); «Однако Аллах оказы­вает милость тому из Своих рабов, кому пожелает» (сура 14 «Ибрахим», аят 11). Всем этим в действительности может одарить только Всевышний Аллах, и Он наделяет Своих рабов великими щедротами. Хвала Ему до тех пор, пока Он не будет доволен, и хвала Ему после этого! Хвала Ему в этом мире и в Последней жизни! Второе — когда один напоминает другому об оказанной ему милости. Если так поступают люди, то это отвратительно, и поэтому говорят, что попрёк разрушает благодеяние. Всевышний Аллах сказал: «Они попре­кают тебя тем, что обратились в ислам. Скажи: “Не попрекайте меня вашим обращением в ислам. Это Аллах оказал вам милость тем, что привёл вас к вере, если вы правдивы”» (сура 49 «Комнаты», аят 17). Аллах оказал им милость на деле, когда привёл их в ислам. Они же попрекали на словах, ведя недостойные речи. Аллах запретил оказывать милость подобным недостойным образом в Своём Писании: «Не оказывай милости, чтобы получить больше!» (сура 74 «Завернувшийся», аят 6). Комментируя эти слова, Ибн Кясир сказал: «Не гордись перед твоим Господом своими поступками, находя их большими» [Тафсир Ибн Кясир, т. 4, с. 242]. Есть и другие толкования этого аята. Могучий и Великий Аллах сказал: Тем, кто расходует своё имущество на пути Алла­ха и не сопровождает свои пожертвования попрёка­ми и оскорблениями, уготована награда у их Госпо­да. Они не познают страха и не будут опечалены. Доброе слово и прощение лучше милостыни, за ко­торой следует обидный попрёк. Аллах — Богатый, Выдержанный. О те, которые уверова­ли! Не делайте ваши пода­яния тщетными своими попрёками и оскорбле­ниями, подобно тому, кто расходует своё имущест­во ради показухи и не ве­руют при этом в Аллаха и в Последний день. При­тчей о нём является при­тча о гладкой скале, покры­той слоем земли. Но вот выпал ливень и оставил скалу голой. Они не власт­ны ни над чем из того, что приобрели. Аллах не ведёт прямым путём неверую­щих людей. Сура 2 «Корова», аяты 262–264. Посланник Аллаха, мир ему и благословение Аллаха, тоже осудил попрёки за оказанную милость: «С тремя Аллах не будет говорить в День воскресения. Он не посмотрит на них, не очистит их, и их ожидают мучительные страдания». Он повторил это трижды, и Абу Зарр сказал: «Они понесут урон и окажутся в убытке. Кто же они, Посланник Аллаха?» Он ответил: «Тот, чья одежда опускается ниже щиколоток, тот, кто попрекает других в оказанной милости, и тот, кто повышает спрос на свой товар, принося лживые клятвы» [Муслим, т. 1, с. 102]. Слово использовано здесь в значении ‘попрёк в оказанной милости’, и подобный поступок считается недостойным. Если же оно используется в значении ‘дар’, ‘доброе отношение’, ‘щедрость’, то речь идёт о похвальных поступках. Одним словом, Всеблагой и Всевышний Аллах — Дарующий, и нет никого подобного Ему. Он — Слышащий, Видящий, и Его дары велики. Он наделяет жизнью, разумом и даром речи, придаёт прекрасный облик и щедро одаряет благами. Он спас верующих рабов и оказал им милость, отправив к ним посланников и ниспослав им Писания. По Своей милости и щедрости Он вывел их из мрака к свету. Его милость по отношению ко всем творениям проявляется в том, что Он создал их, наделив пропитанием, здоровьем и благополучием. Его дары щедры, несмотря на многочисленность их грехов. О Боже, окажи нам милость, одарив нас верой, даруй нам от всяких благ, убереги нас и избавь от всего дурного, и пусть исход всех дел наших будет благим. Защити нас от бесчестия в этом мире и мучений в Последней жизни, Щедрый, Дарующий, Обладающий величием и великодушием, Живой, Вседержитель, Первосоздатель небес и земли. О Единственный, Единый! О Тот, Кто не родил и не был рождён и Кому нет равных!" };

            ListOfNames["Покровитель"] = new Name() { One_Name = "Покровитель", Description = "  Словом вали можно назвать каждого, кто распоряжается чем-либо либо выполняет какое-либо дело. Оно может означать ‘помощник’, ‘любящий’, ‘друг’, ‘союзник’, ‘зять’, ‘сосед’, ‘последователь’, ‘освободитель из рабства’, ‘покорный’. Верующего называют вали Аллах, что означает ‘приближённый к Аллаху’. Его антонимом является слово ‘враг’. Покровитель — одно из имён Аллаха. Это — Помогающий, управляющий делами Вселенной и творений. Ар-Рагиб аль-Асфахани писал [Муфрадат, с. 533]: Слова означают ‘близость’. Речь может идти о близости в пространстве, родстве, духовной близости, дружбе, помощи или близости убеждений. Слово означает ‘помощь’, ’правление’. Слова вали и мауля употребляются в значении причастий действительного — и страдательного — залогов. Верующего называют вали Аллах, что означает ‘приближённый к Нему’, а Аллаха — вали аль-му’минин, то есть ‘Покровитель верующих’. Покровительство Могучего и Великого Аллаха отличается от покровительства творений, ибо «нет никого по­добного Ему, и Он — Слышащий, Видящий» (сура 42 «Совет», аят 11). Он — Покровитель, Который управляет Вселенной и вершит судьбы творений. Ему принадлежит власть, и Он предоставил Своим творениям всё, что может помочь им в духовных и мирских делах, а также в Последней жизни. Он нарёк Себя этим именем, одним из Его прекрасных имён. Могучий и Великий Аллах сказал: «Неужели они взяли себе покровителей и помощ­ников вместо Него? Аллах является Покровителем. Он оживляет мёртвых, и Он способен на всякую вещь» (сура 42 «Совет», аят 9); «Он — Тот, Кто ниспосылает дождь после того, как они отчаиваются, и распростра­ няет Свою милость. Он — Достохвальный Покрови­тель» (сура 42 «Совет», аят 28). Аллах покровительствует рабам, которые поклоняются и повинуются Ему, пытаясь приблизиться к Нему всеми возможными способами. Он покровительствует всем рабам в целом, управляя ими и воплощая в жизнь предопределение. А верующим рабам Он покровительствует особым образом, выводя их из мрака к свету, проявляя о них добрую заботу, оказывая им поддержку во всех делах, поддерживая их морально и ведя их правым путём. Всевышний сказал: Аллах — Покровитель тех, которые уверовали. Он выводит их из мраков к свету. А покровители и помощники неверую­щих — тагуты; они выво­дят их из света к мракам. Они — обитатели Огня и пребудут там вечно. Сура 2 «Корова», аят 257 Воистину, несправед­ливые — помощни­ки и друзья друг другу, а Аллах — Покровитель богобоязненных. Сура 45 «Коленопреклонённые», аят 19. Могучий и Великий Аллах помогает верующим. Он покровительствует им, оказывая им поддержку и вдохновляя их. Он выводит их из мраков неверия к свету веры. Он уподобил неверие мракам только потому, что мрак — словно покрывало для очей. Он не позволяет постичь вещи и убедиться в их существовании. Неверие подобно покрывалу, которое лишает сердце способности видеть истину веры и знания, несмотря на его правдивость и правдивость ведущих к нему путей. Аллах сообщил Своим рабам о том, что Он покровительствует верующим и помогает им узреть истинность веры, её путей, законов и доводов. Он указывает им на аргументы, которые избавляют их от сомнений, развеивая всё, что приводит к неверию, и рассекая мрак пелены, лишающей зрения сердца. Одним словом, Всевышний Аллах сообщил о том, что покровительствует всем тем, кто верует в Него и Его посланников и подтверждает свою веру, выполняя её обязательные требования и отвергая всё, что несовместимо с ней. Он оказывает им особое покровительство и заботится о них, выводя их из мраков невежества, неверия, ослушания, небреж- ности и отвращения — к свету знания, убеждённости, веры, покорности и полного устремления к Господу. Он освещает их сердца светом откровения и веры, облегчает им путь ко всему лёгкому и отдаляет их от всего трудного. Он приближает к ним то, что приносит им пользу, и отдаляет от них всё то, что может навредить им. Он покровительствует праведникам: «Воистину, мой Покровитель — Аллах, Который ниспослал Писание. Он покровительствует правед­никам» (сура 7 «Ограды», аят 196). Праведны их намерения, и праведны их речи. Они избрали своим покровителем Аллаха, уверовав в Него, и встали на путь богобоязненности. Они не стали искать для себя иных покровителей, не способных помочь или навредить. Поэтому Аллах стал покровительствовать им, смилостивился над ними, помог им обрести добро и благо в мирских и религиозных делах, а также уберёг их от всякого зла. Могучий и Великий Аллах сказал: «Во­истину, Аллах оберегает тех, кто уверовал» (сура 22 «Хадж», аят 38). В отличие от них, неверующие отвернулись от своего Всевышнего Покровителя, и поэтому Он сделал их покровителями тех, кого они пожелали видеть своими покровителями. Он лишил их Своей поддержки, поставив их в зависимость от тех, кто не способен помочь или навредить им. Эти покровители ввели их в заблуждение, обрекли их на несчастье, лишили их полезного знания. Они удержали их от праведных поступков, лишили их вечного счастья и обрекли на бесконечные страдания в Адском пламени. О Боже, опекай нас в числе тех, кого Ты опекаешь! Аллах любит Своих приближённых рабов, помогает им и направляет их. Его приближённые — это те, которые познали Его, прилежно повинуются и искренне поклоняются Ему, избегая грехов и ослушания. И тем, кто враждует с такими приближёнными, Могучий и Великий Аллах объявил войну. Пророк, мир ему и благословение Аллаха, сообщил, что Аллах сказал: «Я объявил войну тому, кто враждует с Моим приближённым! Из всех деяний, посредством которых Мой раб приближается ко Мне, Я больше всего люблю обязательные предписания. А посредством дополнительных предписаний Мой раб продолжает приближаться ко Мне, пока Я не возлюблю его. Если же Я возлюблю его, то стану слухом, которым он слышит, и зрением, которым он видит, и рукой, которой он хватает, и ногой, которой он ступает. Если он попросит Меня о чём-либо, Я непременно одарю его. Если же он станет искать у Меня убежища, Я непременно предоставлю ему его. Что бы Я ни делал, Я никогда не колеблюсь так, как это бывает, когда Я забираю душу верующего. Ему неприятна смерть, а Мне не нравится доставлять ему неприятность» [Бухари, цит. по: Фатх Аль-Бари, т. 11, с. 340]. Смысл этих слов в том, что если человек становится возлюбленным, приближённым рабом Могучего и Великого Аллаха, то Он оберегает его, направляет на прямой путь и вдохновляет на следование этим путём. И тогда он начинает слушать только то, что угодно его Покровителю, и смотрит только на то, что нравится Ему. Он не протягивает руки к тому, что не угодно Аллаху, и не ступает ногой туда, где можно ослушаться Его. Он вдохновлён, направлен и ведóм Покровителем — Могучим и Великим Аллахом. Так истолковали этот хадис многие учёные, — в том числе Ибн Таймийя, — и это суждение опирается на одну из версий данного предания, где сказано: «Он слышит со Мной и смотрит со Мной, хватает со Мной и ступает со Мной» [Бухари, цит. по: Фатх Аль-Бари, т. 11, с. 344]. Это указывает на поддержку, которую Аллах оказывает Своему рабу. Аллах помогает ему, вдохновляет на поступки, которые он совершает этими органами, и оберегает от всего, что ненавистно Ему [Фатх Аль-Бари, т. 11, с. 344]." };

            ListOfNames["Господин, Покровитель"] = new Name() { One_Name = "Господин, Покровитель", Description = "Слово мауля имеет много значений: ‘Господь’, ‘хозяин’, ‘господин’, ‘благодетель’, ‘освободитель из рабства’, ‘помощник’, ‘любящий’, ‘последователь’, ‘сосед’, ‘кузен’, ‘союзник’, ‘зять’, ‘невольник’, ‘опекаемый’. Большинство из перечисленных значений встречаются в хадисах, и поэтому в каждом конкретном случае это слово необходимо понимать в соответствии с контекстом. Таким образом, Могучий и Великий Аллах — Господин, и «нет никого подобного Ему, и Он — Слыша­щий, Видящий» (сура 42 «Совет», аят 11). Он — Господь, Царь, Господин. Он — Тот, на Чью помощь и поддержку можно надеяться, потому что Он владеет всем сущим. Он нарёк Себя этим именем: «Совершайте намаз, вы­ плачивайте закят и крепко держитесь за Аллаха. Он — ваш Покровитель. Как же прекрасен этот Покровитель! Как же прекрасен этот Помощник!» (сура 22 «Хадж», аят 78); «Но если они отвернутся, то знайте, что Ал­лах — ваш Покровитель. Как прекрасен этот Покрови­тель! Как прекрасен этот Помощник!» (сура 8 «Трофеи», аят 40); «Это потому, что Аллах является Покровите­лем верующих, а у неверующих нет покровителя» (сура 47 «Мухаммад», аят 11). Пречистый Аллах покровительствует тем, кто уверовал. Он — их Господин и Помощник, защищающий от врагов. Как прекрасен этот Покровитель! Как прекрасен этот Помощник! Он покровительствует верующим рабам, одаряет их тем, что приносит им пользу, помогает им устраивать их религиозные и мирские дела и защищает их от злых ухищрений преступников и яростных нападок злодеев. И если Аллах покровительствует человеку и защищает его, то ему нечего бояться. Но если Он объявляет войну человеку, тот непременно лишится могущества и не сможет найти для себя опоры. Покровительствуя верующим, Пречистый Аллах устраивает их дела наилучшим образом. Как прекрасен такой Покровитель для тех, кто избрал Его своим Покровителем и достиг с Его помощью своей цели! Как прекрасен такой Помощник для тех, кто избрал Его своим Помощником и с Его помощью избавился от всех неприятностей! Могучий и Великий Аллах сказал: «О нет! Аллах — ваш Покровитель, и Он — Наилучший из помощников» (сура 3 «Семейство ‘Имрана», аят 150). Он сообщил о том, что верующие взывают к Нему со словами: «Ты — наш Покровитель. Помоги же нам одер­жать верх над неверующими людьми» (сура 2 «Корова», аят 286), то есть Ты покровительствуешь нам и защищаешь нас, на Тебя одного мы уповаем и Тебя одного молим о помощи, и нет силы и мощи ни у кого, кроме Тебя. Всевышний сказал: Если вы обе покаетесь пе­ред Аллахом, то ведь ва­ши сердца уже уклонились в сторону. Если же вы ста­нете поддерживать друг друга против него, то ведь ему покровительствует Аллах, а Джибриль и праведные верующие являют­ся его друзьями. Сура 66 «Запрещение», аят 4. Аллах установил для вас путь освобождения от ва­ших клятв. Аллах — ваш Покровитель. Он — Знаю­щий, Мудрый. Сура 66 «Запрещение», аят 2. Когда же Абу Суфьян крикнул мусульманам: «У нас есть Аль-‘Узза, а у вас нет ‘уззы», — Пророк, мир ему и благословение Аллаха, велел своим сподвижникам: «Скажите: “Аллах — наш Покровитель, и нет у вас покровителя”» [Бухари, передал аль-Бара ибн ‘Азиб, цит по: Фатх Аль-Бари, т. 3, с. 20]." };

            ListOfNames["Помощник, Защитник"] = new Name() { One_Name = "Помощник, Защитник", Description = "Слово насыр образовано по модели и употребляется как причастие действительного и страдательного залогов, ибо каждый из тех, кто помогает друг другу, оказывается и помощником и победителем. Глагол означает ‘оказать помощь в борьбе с врагом’. Слово означает ‘тот, на кого можно положиться, кто не предаст своего друга и не лишит его своей поддержки’. Могучий и Великий Аллах — Помощник, и Его помощь не похожа на помощь творений. «Нет никого подобно­го Ему, и Он — Слышащий, Видящий» (сура 42 «Совет», аят 11). Аллах нарёк Себя Помощником и Защитником: Довольно того, что твой Господь наставляет на пря­мой путь и помогает. Сура 25 «Различение», аят 31. Аллаху лучше знать ва­ших врагов. Довольно то­го, что Аллах является Покровителем! Довольно того, что Аллах является Помощником! Сура 4 «Женщины», аят 45. Крепко держитесь за Ал­лаха. Он — ваш Покро­витель. Как же прекрасен этот Покровитель! Как же прекрасен этот Помощ­ник! Сура 22 «Хадж», аят 78. Знайте, что Аллах — ваш Покровитель. Как прекрасен этот Покрови­тель! Как прекрасен этот Помощник! Сура 8 «Трофеи», аят 40. Аллах помогает Своим верующим рабам, оказывая им поддержку: Если Аллах окажет вам поддержку, то никто не одолеет вас. Если же Он лишит вас поддержки, то кто же поможет вам вместо Него? Пусть же на Аллаха уповают веру­ющие. Сура 3 «Семейство ‘Имрана», аят 160. О те, которые уверовали! Если вы поможете Аллаху, то и Он поможет вам и утвердит ваши стопы. Сура 47 «Мухаммад», аят 7. Воистину, Мы окажем помощь Нашим посланни­кам и верующим в мирской жизни и в тот день, когда предстанут свидетели. Сура 40 «Прощающий», аят 51. В тот день верующие воз­радуются помощи Аллаха. Он помо­гает, кому пожелает. Он — Могущественный, Мило­сердный. Сура 30 «Ромеи», аяты 4–5. Аллах непременно по­могает тому, кто по­могает Ему. Воистину, Аллах — Всесильный, Мо­гущественный. Сура 22 «Хадж», аят 40. Нашим долгом было по­могать верующим. Сура 30 «Ромеи», аят 47. Кто полагает, что Ал­лах не окажет [Мухаммаду] помощи в этом ми­ре и в Последней жизни, пусть подвяжет верёв­ку к небу, а затем перере­жет её и посмотрит, уст­ранит ли его хитрость то, что вызывает его ярость. Сура 22 «Хадж», аят 15. Помощь, которую Аллах оказывает рабам, упоминается и в этих, и в других аятах. Он помогает тем, кто помогает Ему. Он защищает таких рабов и направляет их на прямой путь. Рабы же помогают тем, что поддерживают Его верующих рабов, выполняют свои обязанности перед Ним, сохраняют верность своим обязательствам, придерживаются Его законов и отдаляются от всего запретного. Выполняя всё перечисленное, человек помогает своему Господу: «Если вы поможете Аллаху, то и Он поможет вам» (сура 47 «Мухаммад», аят 7); «Будьте помощниками Аллаха» (сура 61 «Ряд», аят 14); «Мы также ниспослали железо, в котором заключается могучая сила и польза для людей, для того, чтобы Аллах узнал тех, кто помога­ет Ему и Его посланникам, хотя и не видит Его воочию. Воистину, Аллах — Всесильный, Могущественный» (сура 57 «Железо», аят 25). Тому, кто помогает Аллаху, повинуясь Ему и сторонясь Его запретов, Он оказывает всемерную помощь. Аллах помогает Своим рабам одержать верх над их противниками и разъясняет им, чего они должны остерегаться, и оказывает им поддержку в этом. Благодаря Его покровительству они обретают добро, а благодаря Его поддержке они избавляются от всякого зла. Пророк, мир ему и благословение Аллаха, во время военных походов говорил: «О Аллах, Ты — моя поддержка, и Ты — мой Помощник. Благодаря Тебе я передвигаюсь, благодаря Тебе нападаю и благодаря Тебе сражаюсь» [Абу Давуд, т. 3, с. 42; Тирмизи, т. 5, с. 572; хадис достоверный согласно аль-Альбани: Сахих Тирмизи, т. 3, с. 183]. Могучий и Великий Аллах помогал Своим верующим рабам в прошлом и помогает им сейчас. Он помогает им в земной жизни, позволяя воочию убедиться в поражении тех, кто причинял им зло. В «Сахихе» аль- Бухари приводится хадис о том, что Всеблагой и Всевышний Аллах сказал: «Я объявил войну тому, кто враждует с Моим приближённым!» [Бухари, цит. по: Фатх Аль-Бари, т. 11, с. 340]. По этой причине Аллах погубил народ Нуха , ‘адитов, самудян, обитателей Расса, соплеменников Лута , жителей Мадьяна и многих других, кто подобно им считал лжецами посланников и противился истине. Всевышний спас только тех из них, кто был верующим, и ни один из них не пострадал. А неверующие были под- вергнуты мукам, и никто из них не сумел избежать наказания. Так же Аллах помог Своему Пророку Мухаммаду, мир ему и благословение Аллаха, и его сподвижникам, позволив ему одолеть противников, считавших его лжецом и враждовавших с ним. Он возвысил Своё Слово и помог Своей религии одержать верх над всеми остальными вероисповеданиями. И люди стали толпами обращаться в религию Аллаха, и ислам широко распространился как на Востоке, так и на Западе. Аллах обещал помочь и оказать поддержку тем, кто помогает Ему, и если человек исповедует и проповедует Его религию, борется с Его врагами и делает это искренне ради Него, то Он придаёт ему силы и помогает одолеть врагов. Таково обещание Великодушного Аллаха, Чья речь самая правдивая, самая прекрасная! Он обещал оказать поддержку тем, кто помогает своему Покровителю словом и делом. Он открывает перед ними пути, ведущие к победе, придавая им стойкости и т. п. Всевышний разъяснил, по каким признакам можно узнать тех, кто помогает Ему, и если человек заявляет, что он помогает Аллаху и Его религии, но не обладает этим качеством, то он лжёт. Всевышний сказал: Аллах непременно по­ могает тому, кто по­ могает Ему. Воистину, Аллах — Всесильный, Мо­гущественный. Если Мы одарим их влас­тью на земле, они бу­дут совершать намаз, выплачивать закят, ве­леть совершать одобряе­мое и запрещать предосу­дительное. А исход всех дел — у Аллаха. Сура 22 «Хадж», аяты 40–41. Это — верный признак тех, кто помогает Аллаху и кому помогает Он. Аллах приказал Своим верующим рабам помогать Ему: «О те, которые уверовали! Будьте помощниками Аллаха» (сура 61 «Ряд», аят 14). Чтобы помогать религии Аллаха, следует, в частности, изучать Его Писание и Сунну Его Посланника, вдохновлять на это других, призывать к одобряемому и запрещать предосудительное." };

            ListOfNames["Исцеляющий"] = new Name() { One_Name = "Исцеляющий", Description = "Пречистый Аллах — Исцеляющий. Передают со слов ‘Аиши , что Пророк, мир ему и благословение Аллаха, читал заклинания одной из своих жён, потирая её правой рукой. При этом он говорил: «О Аллах, Господь людей, удали зло и исцели, ведь Ты — Исцеляющий, и нет излечения, кроме излечения от Тебя, излечения, не оставляющего недуга» [Бухари, цит. по: Фатх Аль-Бари, т. 10, с. 206, 210; Муслим, т. 4, с. 1721; Абу Давуд, т. 4, с. 11]. Когда Сабит аль-Бунани заболел, Анас ска- зал ему: «Не прочесть ли тебе заклинание, которое читал Посланник Аллаха?» Тот ответил: «Конечно, прочти». Он сказал: «О Аллах, Господь людей, удаляющий зло! Исцели, ведь Ты — Исцеляющий, и никто не исцеляет, кроме Тебя, не оставляя недуга» [Бухари, цит. по: Фатх Аль-Бари, т. 10, с. 206]. Могучий и Великий Аллах исцеляет от болезней, пороков и сомнений, и такое исцеление бывает двух видов: духовное, избавляющее от душевных пороков, и материальное, избавляющее от болезней тела. В Писании Аллаха упоминаются оба вида исцеления, которые также разъяснены в Сунне Его Посланника, мир ему и благословение Аллаха. Передают со слов Абу Хурайры , что Пророк, мир ему и благословение Аллаха, сказал: «Аллах не низвёл такой болезни, от которой бы не было ниспослано излечение» [там же]. Вначале поговорим о духовном исцелении. Могучий и Великий Аллах сказал: О люди! К вам от ваше­го Господа явилось назидание, исцеление для того, что в груди, верное руко­водство и милость для ве­рующих. Сура 10 «Юнус», аят 57. Под назиданием подразумеваются содержащиеся в Священном Коране предостережения от мерзостей и поступков, которые обрекают человека на гнев Аллаха и влекут за собой наказание. Назидание содержит повеление и запреты, переданные в форме побуждения и устрашения. Помимо того, в этом Священном Коране — исцеление для того, что в груди, избавление от сомнений и страстей, скверны и гнусности. Таким образом, Коран содержит упоминание о награде и наказании, обещание и предостережение, и эти тексты пробуждают в человеке желание и страх. И если он ощутил желание творить добро и страх перед злом, то эти чувства усилятся в процессе постижения значений этого Писания, и в конечном итоге раб станет отдавать предпочтение тому, что угодно Аллаху, перед тем, что угодно ему самому. А совершение богоугодных поступков станет для него милее, чем удовлетворение собственных страстей. Помимо того, в Коране содержатся доводы и аргументы, которые Аллах разъяснил самым совершенным образом. Они уничтожают всё, что ставит под сомнение истину, и благодаря этому душа достигает высшей степени убеждённости. А вслед за сердцем от недугов избавляются все остальные органы, потому что они приходят в порядок, когда сердце в порядке, и приходят в расстройство, когда оно болеет. Священный Коран — верное руководство и милость для верующих, для тех, кто обрёл веру, подтвердил её правдивость и достиг убеждённости. Всевышний сказал: Мы ниспосылаем в Кора­не то, что является исцеле­нием и милостью для ве­рующих, а беззаконникам он не прибавляет ничего, кроме убытка. Сура 17 «Ночной перенос», аят 82. Скажи: «Он является вер­ным руководством и ис­целением для тех, которые уверовали. А уши неверу­ющих поражены глухотой, и они слепы к нему. Это — те, к которым взывают из­ далека». Сура 41 «Разъяснены», аят 44. Верное руководство — это знание об истине и использование его на практике. Милость — это то, благодаря чему можно обрести добро и заработать награду как при жизни на земле, так и после смерти. И всё это — для тех, кто руководствуется этим Священным Кораном. Верное руководство — это величайшее из средств, а милость — самая совершенная цель и самое заветное желание. Однако верным руководством и милостью Священный Коран является только для правоверных. Они следуют кораническому руководству и заслуживают милость, которая является следствием приверженности прямому пути, и благодаря этому обретают счастье и преуспеяние, выгоду и пользу, радость и успех. Такому успеху действительно можно порадоваться, и Аллах повелел: «Скажи: “Это — милость и милосердие Аллаха”. Пусть они возрадуются этому, ибо это лучше того, что они накапливают» (сура 10 «Юнус», аят 58). Таким образом, Коран приносит исцеление и милость, но не каждому, а только тем, кто уверовал в него, засвидетельствовал правдивость его аятов и стал руководствоваться ими на практике. Что же касается тех, кто поступает несправедливо, отказываясь уверовать в него или выполнять его предписания, то им коранические аяты не прибавляют ничего, кроме убытка. Они лишают их возможности оправдаться собственным неведением. Как уже было отмечено, Коран излечивает болезни не только души, но и тела. Он избавляет его от болей и недугов, и посредством его Могучий и Великий Аллах наставляет верующих на прямой путь: «Скажи: “Он является верным руководством и исцелением для тех, которые уверовали”» (сура 41 «Разъяснены», аят 44). Он указывает им на прямой путь и учит их полезному знанию, благодаря чему они получают совершенное божественное руководство. Он исцеляет их тела и души благодаря тому, что отдаляет их от порочного нрава и дурных поступков, вдохновляя на искреннее покаяние, смывающее грехи и исцеляющее сердца. Что же касается тех, кто не уверовал в Коран, то они глухи к нему и отворачиваются от него. Он ослепляет их, и они не могут узреть прямой путь, чтобы последовать по нему. Он не прибавляет им ничего, кроме заблуждения. Их призывают уверовать, но они не отвечают на этот призыв, словно их зовут издалека, а они не слышат голоса зовущего. Смысл этих слов в том, что люди, не уверовавшие в Коран, не извлекают пользы из его наставлений, не видят его света и не пользуются его благами, потому что они сами закрыли перед собой двери, ведущие к прямому пути, отвернувшись от него и став неверующими. Человек во все времена и в любых условиях убеждается в правдивости этих слов. Одни люди заучивают этот Коран, и он украшает и оживляет их сердца. Он делает великими их самих и всё то, что их окружает. Другие же считают Коран слишком обременительным для их ушей и сердец, и он не прибавляет им ничего, кроме глухоты и слепоты. Их сердца слепы, и они не способны извлечь пользу из этого Писания. Но не Коран изменился — изменились человеческие сердца. Могучий и Великий Аллах исцеляет сердца верую- щих, помогая им одолеть их врагов и Его врагов: «Сра­жайтесь с ними. Аллах накажет их вашими руками, опозорит их и одарит вас победой над ними. Он исце­лит груди верующих людей и удалит гнев из их сер­дец. Аллах прощает, кого пожелает, ибо Он — Знаю­щий, Мудрый» (сура 9 «Покаяние», аяты 14–15). Правоверные всегда гневаются на тех, кто сражается против Аллаха и Его Посланника, мир ему и благословение Аллаха, и стремится затушить Его свет. Поэтому сражения с ними исцеляют сердца правоверных, удаляя из них скорбь и печаль, и это свидетельствует о любви и заботе Аллаха по отношению к верующим. Второй вид — это исцеление тел. Мы уже отмечали, что Коран способен не только излечить сердца и души, но и сокрушить телесные недуги. Передают со слов Абу Са‘ида аль-Худри , что как-то группа сподвижников Пророка, мир ему и благословение Аллаха, проходила мимо поселения арабов, жители которого отказали им в гостеприимстве. В этот момент их старейшину укусил скорпион, и они спросили сподвижников: «Есть ли у вас лекарство, или, может, среди вас есть умеющий читать заклинания?» Те ответили: «Вы не оказали нам гостеприимства, и мы не станем помогать вам, пока вы не назначите для нас награду». Они обещали дать им в награду отару овец, и один из сподвижников принялся читать больному Мать Корана. Он собирал слюну и поплёвывал, и больной выздоровел. Потом они привели с собой овец и сказали: «Мы не возьмём их себе, пока не спросим об этом Пророка, да благословит его Аллах и приветствует». Когда они рассказали ему о происшедшем, он рассмеялся и сказал: «Откуда ты знал, что она есть заклинание? Берите себе овец и отделите одну долю для меня» [Бухари, т. 7, с. 22; там же, т. 6, с. 150; Муслим, т. 4, с. 1727]. Передают, что ‘Аиша рассказывала: «Когда Пророк чувствовал себя плохо, он читал себе суры, посредством которых прибегают к защите Аллаха, поплёвывая при этом. Когда же боли усилились, я стала читать их ему, обтирая его тело его рукой в надежде на её благодать» [Бухари, т. 7, с. 22; там же, т. 6, с. 605; Муслим, т. 4, с. 1723]. Ибн аль-Каййим сказал [Зад аль-ма‘ад, т. 4, с. 177]: Известно, что некоторые речи обладают особенностями и приносят пользу, что доказано на практике. Что после этого можно думать о Слове Господа миров, которое превосходит любые другие речи, подобно тому, как Аллах превосходит творения?! Это — совершенное целительное средство, прекрасная защита, свет, верное руководство, всеобщая милость, и, если бы оно было ниспослано на гору, та превратилась бы в прах из-за его величия и славы. Всевышний сказал: «Мы нис­посылаем в Коране то, что является исцелением и милостью для верующих» (сура 17 «Ночной перенос», аят 82). Предлог мин здесь использован для указания на род вещи, а не указывает на часть его. Это — самое достоверное из двух суждений по данному вопросу. Таким образом, Коран способен исцелять не только души верующим, но и их тела. Могучий и Великий Аллах — Тот, Кто излечивает телесные болезни и недуги: «Твой Господь внушил пчеле: “Воздвигай жилища в горах, на деревьях и в строениях. А потом питайся всевозможными плодами и следуй по путям твоего Господа, которые доступны тебе”. Из брюшков пчёл ис­ ходит питьё разных цветов, которое приносит людям исцеление. Воистину, в этом — знамение для людей размышляющих» (сура 16 «Пчёлы», аяты 68–69). Комментируя слова Всевышнего Аллаха «Из брюшков пчёл исходит питьё разных цветов, которое приносит людям исцеление», Ибн Кясир писал [Тафсир Ибн Кясир, т. 2, с. 576]: Оно имеет беловатый, желтоватый, красноватый и другие приятные оттенки, в зависимости от того, где пасутся и питаются пчёлы. Этот мёд приносит людям исцеление от различных болезней. Некоторые из тех, кто комментировал пророческую медицину, говорили: «Если бы слово шифа ‘исцеление’ было употреблено с определённым артиклем, то мёд исцелял бы все болезни, но оно использовано без артикля, и поэтому мёд годится для лечения холодных болезней. Он относится к жарким средствам, а болезни лечатся средствами, обладающими противоположными качествами». Доказательством того, что слова «приносит людям исцеление» относятся к мёду, служит хадис, переданный в «Сахихах» аль- Бухари и Муслима со слов Абу Са‘ида аль-Худри. В нём сообщается, что один мужчина пришёл к Пророку и сказал: «У моего брата понос». Посланник Аллаха сказал: «Давай ему мёд». Он поил его мёдом, а спустя некоторое время снова пришёл к нему и сказал: «Я поил его мёдом, но от этого его боли только усилились». Так продолжалось три раза. В четвёртый раз он снова пришёл, и Пророк сказал: «Давай ему мёд». Мужчина ответил: «Я поил его мёдом, но от этого его болезнь только усилилась». Посланник Аллаха сказал: «Правду сказал Аллах, а живот твоего брата солгал!» Тогда он снова стал поить брата мёдом, и тот выздоровел. Некоторые знатоки медицины считали, что у того мужчины было расстройство желудка. Когда его напоили мёдом, — а это жаркая пища, — это ещё больше расслабило его желудок. Организм стал быстро выводить вредные остатки пищи, и понос усилился. Бедуин решил, что больному стало хуже, хотя в действительности предпринимаемые меры пошли на пользу его брату. Он поил его мёдом, и понос усиливался. Когда же вредные остатки пищи были выведены из организма, понос прекратился, а состояние больного улучшилось. Его боли прекратились, и указания Пророка, мир ему и благословение Аллаха, пошли ему во благо. Передают со слов Ибн ‘Аббаса : «Три вещи приносят излечение: глоток мёда, надрез для кровопускания и прижигание огнём, но я запрещаю своим последователям делать прижигания» [Фатх Аль-Бари, т. 10, с. 136]. Он рассказывал это со слов Пророка, мир ему и благословение Аллаха. Могучий и Великий Аллах удивительным образом указывает верный путь этим маленьким пчёлам. Он помогает им добраться до цветочных лугов и найти дорогу обратно в ульи, которые пчёлы благоустраивают благодаря приобретённым от Аллаха знаниям и верному руководству. Из брюшков пчёл выделяется вкусный мёд, который бывает различных цветов и оттенков, зависящих от почвы и цветков. Это питьё приносит людям исцеление от многих болезней. Всё это свидетельствует о безупречной заботе, которую Всевышний Аллах проявляет о Своих творениях, и Его совершенной доброте по отношению к Своим рабам. А это значит, что рабы должны всецело посвящать свою любовь Ему одному и взывать о помощи только к Нему одному. Аллах сообщил о том, что Его раб, посланник и возлюбленный Ибрахим сказал о Нём: «Который сотворил меня и ведёт прямым путём, Который кор­мит меня и поит, Который исцеляет меня, когда я за­ болею» (сура 26 «Поэты», аяты 78–80). Комментируя слова Всевышнего «Который исце­ляет меня, когда я заболею», Ибн Кясир писал: «Ибрахим подчеркнул, что заболевает он сам, хотя всё происходит строго в соответствии с предопределением Аллаха и есть Его творение. Тем не менее, он связал болезнь со своим именем из уважения к Аллаху. Это означает: если я заболеваю, то никто не может излечить меня, и только Всеблагой и Всевышний Аллах создаёт причины, приводящие к исцелению» [Тафсир Ибн Кясир, т. 3, с. 339, с сокращениями]. Пророк, мир ему и благословение Аллаха, призвал своих последователей молить Аллаха об излечении, ибо только Он исцеляет, и никто другой не способен на это. Муслим и другие передали со слов ‘Усмана ибн аль-‘Аса , что он пожаловался Посланнику Аллаха, мир ему и благословение Аллаха, на то, что ощущал боль в теле с тех пор, как обратился в ислам. Посланник Аллаха, мир ему и благословение Аллаха, сказал ему: «Положи руку на то место, которое болит, и трижды скажи: “Во имя Аллаха!” Потом семь раз скажи: “Прибегаю к Аллаху и Его могуществу от зла того, что я ощущаю и чего опасаюсь”» [Муслим, т. 4, с. 1728]. Передают со слов Ибн ‘Аббаса , что Пророк, мир ему и благословение Аллаха, сказал: «Если кто-либо навестит больного, чей срок ещё не истёк, и семь раз скажет: “Я прошу Великого Аллаха, Господа великого Трона, исцелить тебя”, — то Аллах непременно излечит его от этой болезни» [Абу Давуд, т. 3, с. 187; Тирмизи, т. 2, с. 410; Ахмад, т. 1, с. 239; хадис достоверный согласно аль-Альбани: Сахих Тирмизи, т. 2, с. 210; Джами‘ ас-сагир, т. 5, с. 180]. Пророк, да благословит его Аллах и приветствует, обучал своих последователей полагаться на Господа, но прибегать к дозволенным средствам. Могучий и Великий Аллах — Единственный Целитель, и никто не способен излечить, кроме Него. Пророк, мир ему и благословения Аллаха, взывал к Нему с мольбами об излечении, ибо всякое излечение — в руке Всеблагого и Всевышнего Аллаха. Сообщается, что Пророк, мир ему и благословение Аллаха, сказал: «О Аллах, излечи Са‘да! О Аллах, излечи Са‘да! О Аллах, излечи Са‘да!» [Бухари, цит. по: Фатх Аль-Бари, т. 10, с. 120; Муслим, т. 3, с. 1253]. Иногда Посланник Аллаха, мир ему и благословение Аллаха, молился об исцелении сподвижников, читая им заклинания [Бухари, т. 7, с. 24; Муслим, т. 4, с. 1721]: Во имя Аллаха, наша земля вместе со слюной одного из нас, будет исцелён наш больной с дозволения нашего Господа - Би-смилляхи турбату ардына би-рикати ба‘дына йушфа сакымуна би-изни раббина. Посланник, мир ему и благословение Аллаха, разъяснил, что именно Аллах, Целитель, ниспосылает лекарства. В хадисе, переданном со слов Абу Хурайры , говорится: «Аллах не низвёл такой болезни, от которой бы не было ниспослано излечение» [Бухари, цит. по: Фатх Аль-Бари, т. 10, с. 134]. Муслим передал со слов Джабира , что Пророк, мир ему и благословение Аллаха, сказал: «От каждой болезни есть лечение, и если лечение и болезнь совпадают, то [больной] выздоравливает по воле Могучего и Великого Аллаха» [Муслим, т. 4, с. 1729]. Передают со слов Абу ад-Дарды , что Посланник Аллаха, мир ему и благословение Аллаха, сказал: «Поистине, Аллах низвёл болезнь и излечение, и установил излечение от каждой болезни. Посему лечитесь, но не лечитесь тем, что запрещено» [Абу Давуд, т. 4, с. 7]. Передают, что однажды к Пророку, мир ему и благословение Аллаха, пришли бедуины и спросили: «О Посланник Аллаха, стоит ли нам лечиться?» Он ответил: «Да, рабы Аллаха, лечитесь, ибо Аллах не низвёл такой болезни, от которой бы не было ниспослано излечение или лекарство, кроме одной болезни». Его спросили: «Что же это, Посланник Аллаха?» Он ответил: «Старость» [Абу Давуд, т. 4, с. 3; Тирмизи, т. 4, с. 383; хадис достоверный согласно аль-Альбани: Сахих Тирмизи, т. 2, с. 201; Сахих Ибн Маджа, т. 2, с. 252]. Передают со слов ‘Абдаллаха ибн Мас‘уда , что Пророк, мир ему и благословение Аллаха, сказал: «Аллах не низвёл такой болезни, от которой бы не было ниспослано излечение. Одним оно известно, а другим — нет» [Ахмад, № 3578; Ибн Маджа, № 3438; аль-Хаким, т. 4, с. 196; хадис достоверный согласно Ахма- ду Шакиру]. Ибн аль-Каййим писал [Зад аль-ма‘ад, т. 4, с. 14]: Эти хадисы доказывают существование причин и следствий, опровергая заявления тех, кто отрицает это. Возможно, слова «излечение от каждой болезни» имеют самый широкий смысл и относятся даже к смертельным болезням, которые врачи не могут лечить. Могучий и Великий Аллах создал лекарства, способные вылечить их, но знание об этом сокрыто от людей. Аллах не позволил им обрести его, потому что творения знают только то, чему их обучил Аллах. Таким образом, только Аллах исцеляет, кого пожелает, и скрывает знание о некоторых лекарствах от врачей, если не желает, чтобы болезнь излечивалась. Мы же молим Аллаха, Единственного, Кто достоин поклонения, посредством Его прекрасных имён и возвышенных качеств исцелить наши души и тела от всех недугов и уберечь нас благодаря исламу. Поистине, всё это в Его власти, и Он способен на это, и нет силы и мощи ни у кого, кроме Возвышенного и Великого Аллаха. Мир, благодать и благословение Аллаха Его рабу и посланнику, избранному среди Его творений, которому было доверено Откровение — нашему Пророку и имаму Мухаммаду, сыну ‘Абдаллаха, — его роду, сподвижникам и всем тем, кто следует его путём до наступления Дня воздаяния!" };

