using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;

namespace namesOfAllah.Models
{
    public class Names
    {
        public Dictionary<string, Name> ListOfNames = null;
        public Names()
        {
            ListOfNames = new Dictionary<string, Name>();

            ListOfNames["Аллах"] = new Name() { One_Name = "Аллах", Description = "Аллах — Тот, Кто заслуживает обожествления и поклонения. Все творения обязаны обожествлять Его и поклоняться Ему, потому что Он обладает божественными качествами, то есть качествами совершенства." };

            ListOfNames["Господь"] = new Name() { One_Name = "Господь", Description = "Всевышний сказал: «Скажи: “Неужели я стану искать другого господа помимо Аллаха, в то время как Он — Господь всякой вещи ?”» (сура 6 «Скот», аят 164). Господь — Тот, Кто заботится обо всех рабах, управляя их судьбами и одаряя их всевозможными благами.Особую заботу Аллах проявляет о Своих избранниках, приводя в порядок их сердца, души и нравственный облик.Поэтому в своих молитвах они часто называют Его этим славным именем, надеясь на Его особую заботу." };

            ListOfNames["Первый, Последний, Высочайший, Ближайший"] = new Name() { One_Name = "Первый, Последний, Высочайший, Ближайший", Description = "Всевышний Аллах сказал: Он — Первый и Послед­ний, Высочайший и Бли­жайший. Сура 57 «Железо»,аят 3. Эти четыре благословенных имени были ясно и всесторонне разъяснены Пророком(да благословит его Аллах и приветствует) в одной из молитв: «О Аллах, Ты — Первый, и нет ничего прежде Тебя, и Ты — Последний, и нет ничего после Тебя. Ты — Высочайший, и нет никого выше Тебя, и Ты — Ближайший, и нет никого ближе Тебя» [Муслим, т. 4, с. 2084]. Он указал на величественные значения каждого имени, отвергнув всё, что противоречит и противопоставляется им.Задумайся же над этими славными значениями, указывающими на то, что только Великий Господь обладает абсолютным совершенством и полностью объемлет время (первые два имени) и пространство (последниедва имени). Первый означает, что всё остальное некогда не существовало, а потом возникло. Это обязывает рабов замечать милость Господа во всех духовных и материальных благах, ибо только Он создаёт причины для их обретения. Последний означает, что Аллах — цель, к которой стремятся творения, обоготворяя Его, желая приблизиться к Нему и испытывая страх перед Ним. Высочайший указывает на величие божественных качеств и на то, что перед Его величием меркнет всё сущее и любые качества, какими бы славными они ни были. Ближайший указывает на то, что Ему ведомы любые тайны, сокровенные мысли и намерения. Он доподлинно знает обо всём в мельчайших подробностях. Это имя также свидетельствует о совершенстве Его близости к рабам. Имена Высочайший и Ближайший не противоречат друг другу, ибо ничьи качества не подобны качествам Аллаха." };

            ListOfNames["Возвышенный, Всевышний, Превознёсшийся"] = new Name() { One_Name = "Возвышенный, Всевышний, Превознёсшийся", Description = "Всевышний Аллах сказал: Не тяготит Его оберегание их. Он — Возвышенный, Великий. Сура 2 «Корова», аят 255. Славь имя Господа твоего Всевышнего. Сура 87 «Всевышний», аят 1. Он — Ведающий сокро­венное и явное, Большой, Превознёсшийся. Сура 13 «Гром», аят 9. Эти имена объединяют все проявления возвышенности, присущие Аллаху.Его высокое положение объясняется вознесением на Трон, и Он находится над рабами Своей божественной сущностью. Его высокое положение также объясняется тем, что Ему присущи возвышенные и величественные качества, которые никоим образом не похожи на качества творений. Более того, все творения вместе не способны всесторонне постичь даже одно из значений какого либо божественного качества. По этому поводу Всевышний сказал: «Они не способны объять Его своим знанием» (сура 20 «Та,ха »,аят 110), и это ещё раз доказывает, что ничьи качества не подобны качествам Всевышнего Аллаха. Его высокое положение проявляется в Его абсолютной власти, ибо Он — Единственный, Всемогущий. Своим могуществом и возвышенностью над всеми Он покоряет творения. Их судьбы — в Его Руках, и происходит только то, что Он пожелает, и ничто не способно помешать этому. А то, что Ему не угодно, не может произойти, и если все твари объединятся, чтобы сотворить то, что Ему не угодно, они не сумеют сделать этого. Если же они объединятся, чтобы помешать тому, что Он предрешил, то не сумеют предотвратить это. Его власть совершенна, и Его воля неизменно исполняется, и все творения испытывают в Нём всестороннюю крайнюю нужду." };

            ListOfNames["Великий"] = new Name() {One_Name = "Великий", Description = "Коран гласит: Не тяготит Его оберегание их.Он — Возвышенный, Великий. Сура 2 «Корова», аят 255. Аллах велик, и все божественные качества обязывают к почитанию и возвеличиванию Его.Ни одно творение не способно возвеличить Его должным образом, как Он того заслуживает.Он достоин похвалы, на которую способен только Он и которая недоступна Его рабам. Следует знать, что присущие Аллаху значения величия делятся на две категории. 1.Он обладает всеми качествами совершенства, и эти качества присущи Ему в самой совершенной, величественной и всеобъемлющей форме.Его знание безгранично, а могущество непререкаемо. Ему присущи гордость и величие,одним из проявлений которого является то, что небеса и земля в Пригоршне Милостивого не больше горчичного зёрнышка.Об этом говорили Ибн ‘Аббас и другие комментаторы, а Всевышний сказал: «Не ценили они Аллаха должным образом, а ведь вся земля в День воскресения будет всего лишь Пригорш­ней Его, а небеса будут свёрнуты Его Десницей» (сура 39 «Толпы», аят 67); «Воистину, Аллах удер­живает небеса и землю, чтобы они не сдвинулись. А если они сдвинутся, то никто другой после Не­го их уже не удержит» (сура 35 «Творец», аят 41); «Небеса готовы разверзнуться сверху[от величия Аллаха]» (сура 42 «Совет», аят 5). В «Сахихе» сообщается со слов Пророка , что Аллах сказал: «Гордость — Моя накидка, а величие — Мой изар, и Я подвергну мучениям того, кто попытается отнять их у Меня» [Муслим, т. 4, с. 2023; Абу Давуд, т. 4, с. 59; Ибн Маджа, т. 2, с. 1397; Ахмад, т. 2, с. 376]. Таким образом, гордость и величие — два божественных качества.Их невозможно оценить по достоинству, равно как и невозможно постичь всю глубину их смысла. 2. Ни одно из творений не достойно того, чтобы его возвеличивали и почитали так же, как Аллаха. Он заслуживает того, чтобы рабы возвеличивали Его в душе, языком и телом.Для этого следует проявить усердие в познании Его, возлюбить Его и унизиться перед Ним, смириться пред Его величавостью и убояться Его. Для этого надлежит устами прославлять Его, а телом — благодарить Его и поклоняться Ему.Чтобы почитать Аллаха, следует надлежащим образом бояться Его в делах, повиноваться Ему, не зная ослушания, поминать Его без устали, не забывать Его и быть признательным Ему, не допуская неблагодарности.Чтобы почитать Его, необходимо уважать Его запреты и предписания, касающиеся временных промежутков, мест и поступков. Всевышний сказал: «Вот так! И если кто почитает обрядовые знамения Аллаха, то это исходит от богобоязненности в сердцах» (сура 22 «Хадж», аят 32); «Вот так! Кто почитает святыни Аллаха, тот поступает во благо себе перед своим Господом» (сура 22 «Хадж», аят 30). Наконец, чтобы почитать Его, следует не возражать против того, что Он сотворил или узаконил." };

            ListOfNames["Славный"] = new Name() { One_Name = "Славный", Description = "Это — Тот, Кто обладает великой славой. Под славой подразумевается величие и необъятность качеств. Что же касается качеств Аллаха, то все они величественны и достославны. Он — Знающий, и Его знание совершенно. Он — Милосердный, и Его милосердие объемлет всё сущее. Он — Могущественный, и для Него нет ничего невозможного. Он — Выдержанный и Мудрый, а Его выдержка и мудрость безупречны. Такая характеристика справедлива по отношению ко всем божественным именам и качествам. Они достигли предела великолепия и славы, в них нет ни малейшего изъяна или недостатка. Всевышний Аллах сказал: Да пребудут над вами ми­лость и благословение Ал­лаха, о обитатели дома! Воистину, Он — Досто­хвальный, Славный. Сура 11 «Худ», аят 73" };

            ListOfNames["Большой"] = new Name() { One_Name = "Большой", Description = "Пречистому и Всевышнему Аллаху присущи слава, гордость и величие. Он больше, величавее, славнее и превыше всего сущего. Сердца Его возлюбленных и избранников преисполнены уважения и почтения к Нему. Они покорны Ему и смиренны перед Его величием, как сказал об этом Всевышний: Это потому, что, когда призывали одного Аллаха, вы не веровали. Если же к Нему приобщали сото­варищей, вы верили. Ре­шение принимает толь­ко Аллах, Возвышенный, Большой. Сура 40 «Прощающий», аят 12" };
            ListOfNames["Слышащий"] = new Name() { One_Name = "Слышащий", Description = "Всевышний сказал: Аллах — Слышащий, Ви­дящий. Сура 4 «Женщины», аят 134. Божественный слух и зрение часто упоминаются вместе. Каждое из этих качеств подразумевает обладание соответствующими внешними и внутренними способностями. Слышащий — Тот, Кто объемлет слухом всё, что можно услышать. Он слышит любые тайные и явные звуки, возникающие в высшем и низшем мирах. Он слышит их все без исключения, не запутываясь в них, несмотря на их многочисленность. Он слышит голоса на любых языках, близкие и отдалённые, приглушённые и громкие. Все они воспринимаются Им совершенно одинаково: Скрываете вы свои речи или произносите их вслух, прячетесь вы ночью или открыто передвигаетесь днём — все вы равны[перед Аллахом]. Сура 13 «Гром», аят 10. Аллах уже услышал слова женщины, которая всту­пила с тобой в пререкания относительно своего мужа и пожаловалась Аллаху. Аллах слышал ваш спор, ведь Аллах — Слышащий, Видящий. Сура 58 «Препирающаяся», аят 1 ‘Аиша говорила: «Благословен Тот, Чей слух объемлет любые звуки! Когда пререкавшаяся женщина пришла и стала жаловаться Посланнику Аллаха (да благословит его Аллах и приветствует), я находилась в углу комнаты и не могла расслышать некоторые из её слов. Аллах же ниспослал аят : “Аллах уже услышал слова женщины, которая вступила с тобой в пре­рекания относительно своего мужа”». Божественный слух бывает двух видов: а) имеется в виду, что Аллах слышит любые тайные и явные звуки, а также громкие и приглушённые голоса, различая их совершенным образом; б) имеется в виду, что Он внимает тем, кто просит Его, поклоняется Ему и обращается к Нему с молитвами. Он отвечает на их просьбы, вознаграждая их за это. Всевышний сказал: «Воистину, мой Господь внимает мольбе» (сура 14 «Ибрахим»,аят 39). Когда молящийся говорит: «Аллах внимает тому, кто восхваляет Его»[30], — имеется в виду, что Он отвечает на молитвы." };

            ListOfNames["Видящий"] = new Name() { One_Name = "Видящий", Description = "Это — Тот, Чьё зрение объемлет всё сущее в любых уголках небес и земли, даже самые крошечные творения. Он видит чёрного муравья на скале в тёмную ночь, различая все его внешние очертания и внутренние органы; от Него не скрыто даже движение пищи по его мелким органам. Он видит ток жидкости внутри ветвей деревьев и любых других растений, независимо от их размеров. Он различает сосуды муравьёв, пчёл, комаров и даже более мелких существ. Пречист Тот, Чьё величие изумляет умы, Чьи качества необъятны, а величие и проницательность совершенны. Он ведает сокровенное и явное, близкое и далёкое. Он замечает даже беглые взгляды и видит любые изменения, происходящие в сердцах. Всевышний сказал: «…Который видит те­бя, когда ты выстаиваешь намазы по ночам и двига­ешься среди падающих ниц. Воистину, Он — Слы­шащий, Знающий» (сура 26 «Поэты», аяты 218–220); «Он знает о предательском взгляде и том, что скрыва­ют сердца» (сура 40 «Прощающий», аят 19); «Аллах же — Свидетель всякой вещи!» (сура 85 «Созвездия Зодиака»,аят 9), то есть Он знает обо всём, что происходит во Вселенной, и объемлет зрением и слухом всё сущее." };

            ListOfNames["Знающий, Ведающий"] = new Name() { One_Name = "Знающий, Ведающий", Description = "Всевышний сказал: Он — Одолевающий и на­ ходится над Своими раба­ми.Он — Мудрый, Веда­ющий. Сура 6 «Скот», аят 18. Воистину, Аллах ведает о всякой вещи. Сура 8 «Трофеи», аят 75. Знающий — Тот, Чьё знание объемлет всё сущее: необходимое, невозможное и возможное. Ему известно о Себе Самом, Его пресвятых качествах и великих атрибутах. Все они есть необходимость, отсутствие которой невозможно и невероятно. Наряду с этим, Ему известно о том, что невозможно, и том, как бы это выглядело, если бы стало возможно. В Коране сказано: «Если бы на[небесах и земле] были иные божества на­ ряду с Аллахом, то[небеса и земля] разрушились бы» (сура 21 «Пророки», аят 22); «Аллах не взял Себе сына, и нет наряду с Ним другого бога. В противном случае каждый бог унёс бы с собой то, что сотворил, и одни из них возвысились бы над другими» (сура 23 «Верующие», аят 91). Эти и похожие аяты указывают на то, что Аллах ведает о том, что невозможно, и даже сообщает нам о том, что могло бы произойти, если бы это невозможное стало реальностью. Наконец, помимо всего прочего, Аллах ведает о возможном, то есть том, что существует во Вселенной, и том, чего пока нет, поскольку этого требовала божественная мудрость, но что в принципе может возникнуть. Его знания объемлют высший и низший миры, объемлют пространство и время. Ему известно всё сокрытое и явное, очевидное и сокровенное. Всевышний сказал: «Воистину, Аллах веда­ет о всякой вещи» (сура 8 «Трофеи», аят 75). Текстов о всеобъемлющем знании Аллаха очень много; всех их нельзя сосчитать и перечислить. Ничто не скроется от Него ни на земле, ни на небе, будь то крошечная крупица, или нечто большее, или нечто меньшее. Он ничего не оставляет без внимания и ничего не забывает. Все необъятные и разносторонние познания, которыми обладают творения, ничто в сравнении со знанием Аллаха. Точно так же всё могущество творений не идёт ни в какое сравнение с могуществом Господа. Он — Тот, Кто наделил их знаниями о том, чего они не ведали, и наделил их всевозможными способностями. Ему известно обо всём, что происходит на небесах и на земле, а также обо всех творениях, проживающих в этих мирах, — о них самих, их качествах, поступках и состояниях. Он ведает то, что произошло в прошлом, и то, чему предстоит произойти в далёком будущем. Он знает то, чего никогда не произойдёт, и то, как это выглядело бы, если бы всё - таки произошло. Ему известно обо всём, что происходит с людьми с того момента, как они появляются на свет, что постигает их после смерти и что ожидает их после того, как они будут воскрешены. Ни один из их добрых и злых поступков не сокрыт от Него, и в Обители вечности они получат воздаяние за свои деяния и узнают все мельчайшие подробности о самих себе. Одним словом, Аллах — Тот, Чьи знания охватывают всё зримое и незримое, тайное и явное, необходимое, невероятное и возможное. Он ведает о высшем и низшем мирах, знает о прошлом, настоящем и будущем и ничего не оставляет незамеченным." };

            ListOfNames["Достохвальный"] = new Name() { One_Name = "Достохвальный", Description = "  Всевышний сказал: О люди! Вы нуждаетесь в Аллахе, тогда как Ал­лах — Богатый, Досто­хвальный. Сура 35 «Творец», аят 15. Ибн аль-Каййим указывал на два проявления этого качества. Во-первых, все существа воздают Ему хвалу. Всевышний Аллах заслуживает всяческой похвалы, которую возносят Ему первые и последние обитатели небес и земли. Он достоин похвалы, которую возносят Ему в этом мире и в Последней жизни, и похвалы, которую только можно воздать на протяжении долгих веков. Она наполняет высший и низший миры, её невозможно ни сосчитать, ни перечислить. Аллах достоин её по праву, ведь именно Он создал творения, наделил их всяческими милостями и проливает на них зримые и незримые блага — как мирские, так и духовные. Он избавляет их от бед и несчастий. Любые милости, которыми пользуются рабы, ниспосланы от Аллаха, и только Он оберегает от зла и неприятностей. Посему Он достоин того, чтобы творения восхваляли и благодарили Его в любое время, каждое мгновение. Во-вторых, Господь достоин похвалы за то, что обладает прекрасными именами и совершенными, возвышенными качествами. Его атрибуты славны и прекрасны. Он обладает всеми качествами совершенства в их наилучших, самых величественных проявлениях. Каждое из Его качеств в отдельности заслуживает самой совершенной похвалы. Что же можно говорить о Том, Кто обладает всеми пречистыми качествами вместе?! Восхваления достойны Сам Аллах, Его атрибуты и деяния. Эти деяния преисполнены либо милости и добра, либо справедливости и мудрости, и потому их полагается хвалить наилучшим образом. Помимо того, Аллах заслуживает похвалы за то, что творит и издаёт законы, регулирующие всё происходящее во Вселенной, а также законы Шариата и справедливого возмездия в этом мире и в Последней жизни. Если говорить подробно о том, какой должна быть хвала Аллаху, и обо всём, за что Он заслуживает похвалы, то для этого не хватит ни слов, ни чернил." };

            ListOfNames["Могущественный, Всемогущий, Могучий, Способный, Всемогущий, Всесильный, Крепкий, Несокрушимый"] = new Name() { One_Name = "Могущественный, Всемогущий, Могучий, Способный, Всемогущий, Всесильный, Крепкий, Несокрушимый", Description = " Эти великие имена имеют похожий смысл, указывая на то, что Всевышний обладает совершенным могуществом, великой силой и безграничной мощью. В Коране сказано: «Могущество целиком принадлежит Аллаху» (сура 10 «Юнус», аят 65); «Воистину, твой Господь — Всесильный, Могущественный!» (сура 11 «Худ», аят 66). Таким образом, все три проявления могущества самым полным образом относятся к Великому Аллаху. Первое — сила. На это качество указывают такие имена, как Аль-Кави (Всесильный), Аль-Матин (Крепкий). Оно не может быть приписано творениям, какими бы сильными они ни были. Коран гласит: «Воистину, Аллах — Наделяющий уделом, Обладающий силой, Крепкий» (сура 51 «Рассеивающие прах», аят 58); «Ал­лах — Всемогущий. Аллах — Прощающий, Милосерд­ный» (сура 60 «Испытуемая», аят 7); «Скажи: “Он влас­тен наслать на вас мучения сверху или из-под ваших ног, или привести вас в замешательство, разделив на группировки и дав одним из вас вкусить жестокость других”» (сура 6 «Скот», аят 65); «Воистину, Аллах спо­собен на всякую вещь» (сура 18 «Пещера», аят 45); «Во­истину, богобоязненные пребудут в Райских садах и среди рек на седалище истины возле Всемогущего Властелина» (сура 54 «Луна», аяты 54–55). Второе — отсутствие всякой нужды. Аллах Самодостаточен, Он ни в ком и ни в чём не нуждается. Рабы не способны причинить Ему какой-либо вред или принести Ему какую-либо пользу. Напротив, только Он причиняет вред и приносит пользу, дарует блага одним и лишает других. Третье — власть. Аллах господствует над всеми творениями; все они подвластны Ему, подчинены Его величию, зависимы от Его воли. Он распоряжается судьбами всех существ, и они не могут ни шевельнутся, ни распорядиться тем, чем владеют, вопреки Его воле. Происходит только то, что пожелает Аллах, и то, что не угодно Ему, не может произойти. Нет силы и мощи ни у кого, кроме Него.Свидетельством Его силы и могущества было сотворение небес и земли, а также всего, что между ними, всего за шесть дней. Он дарует творениям жизнь, а потом умерщвляет их, а потом они будут воскрешены и вернутся к их Господу: «Сотворение и воскрешение всех вас подобно сотворению и воскрешению одного человека» (сура 31 «Лукман», аят 28); «Он — Тот, Кто создаёт тво­рения в первый раз, а затем воссоздаёт их, и сделать это для Него ещё легче» (сура 30 «Ромеи», аят 27). Другим свидетельством Его могущества является то, что ты видишь землю иссохшей, но стóит Ему излить с неба воду, как она приходит в движение и набухает, а потом на ней произрастают всевозможные пары растений. О Его могуществе свидетельствует участь неверующих и несправедливых народов, которые были подвергнуты различным наказаниям, ставшим поучительным уроком для остальных. Их ухищрения и уловки не принесли им пользы. Ни богатство, ни многочисленные армии, ни крепости нисколько не защитили их от Божьей кары, когда та постигла их. Напротив, всё это ещё больше отяготило их в тот момент, когда приблизилась их погибель. Колоссальная сила, которую обрели эти народы, и их изумительные изобретения были результатом того, что Аллах наделил их определёнными способностями и обучил тому, чего они не знали. Но вся эта сила, эти способности и достижения не принесли им никакой пользы, не уберегли их от наказания и погибели, и это стало ещё одним знамением Аллаха. Они пытались спастись от лютой кары, но свершилось Божье веление, ибо всё сущее на небесах и на земле подчинено Его воле. На совершенство могущества и власти Аллаха также указывает сотворение Им деяний рабов, их богоугодных поступков и грехов. Аллах сотворил не только рабов, но и их деяния. Таким образом, поступки и действия людей сотворены и предопределены Господом, но совершаются самими людьми. Между этими истинами нет противоречий, ибо Аллах создал их способности и волю. Тот, Кто создал причины, также является создателем того, что вызывает эти причины. В Коране сказано: «Аллах сотворил вас и то, что вы делаете» (сура 37 «Выстроившиеся в ряды», аят 96). Ещё одним проявлением Его могущества является упомянутое в Коране обещание помогать праведникам и приближённым рабам (аулийа), несмотря на их малочисленность и слабую оснащённость, в борьбе с врагами, превосходящими их числом и умением. Всевышний сказал: «Сколько малочисленных отрядов победило многочисленные отряды по воле Аллаха!» (сура 2 «Корова», аят 249). Благодаря Своему могуществу и по милости Своей Аллах приготовил для обитателей Ада и Рая разные виды наказания и вечного удовольствия, которые не прервутся и не прекратятся. Благодаря Своему могуществу Господь сотворил всё сущее, управляет Своими творениями, придаёт им соразмерность и совершенный облик, оживляет мёртвых и умерщвляет живых, а потом воскресит рабов для того, чтобы они получили воздаяние, вознаградит праведников за их благодеяния и покарает грешников за их прегрешения. Он изменяет состояние сердец и направляет их туда, куда пожелает: «Когда Он желает чего-либо, то стоит Ему сказать: “Будь!” — как это сбы­вается» (сура 36 «Йа, син», аят 82); «Где бы вы ни были, Аллах приведёт всех вас вместе. Воистину, Аллах спо­собен на всякую вещь» (сура 2 «Корова», аят 148)." };

            ListOfNames["Богатый"] = new Name() { One_Name = "Богатый", Description = "Всевышний сказал: Он дарует богатство и на­деляет собственностью. Сура 53 «Звезда», аят 48. О люди! Вы нуждаетесь в Аллахе, тогда как Ал­лах — Богатый, Досто­хвальный. Сура 35 «Творец», аят 15. Он богат и абсолютно ни в чём не нуждается, ибо Он обладает совершенством, и все Его качества совершенны и далеки от малейших недостатков. Бог не может нуждаться в чём-либо, ибо Его богатство и самодостаточность являются необходимостью. Таким же образом, Бог непременно должен быть Добродетельным, Щедрым, Добрым, Милосердным, Великодушным. Творения не способны прожить без Его заботы даже мгновение ока. Их появление на свет и существование невозможны без Его участия. Они нуждаются в Нём для удовлетворения своих нужд и потребностей. Проявлением Его безмерного богатства является обладание Им сокровищницами небес и земли, а также сокровищницами великой милости. Он неустанно оказывает милость Его творениям: каждый миг они пользуются Его благами. Он одаряет их днём и ночью, щедро и не скупясь. Совершенство богатства и щедрости Аллаха проявляется и в Его повелении взывать к Нему. Он обещал Своим рабам отвечать на их молитвы и удовлетворять их просьбы. Он наделяет их благами, которые они просят, и многим из того, чего они даже не просят. Его богатство настолько совершенно, что если все творения, от первого до последнего, соберутся в одном месте и попросят Его о любых благах, и Он одарит их тем, что они попросят, и всем, что только придёт им на ум, то от этого Его богатство не уменьшится даже на малую толику. Свидетельством безграничности Его богатства являются удовольствия и нескончаемые радости, которыми Он щедро одарит обитателей Рая. Это будут бесконечные дары, которых не видывал взор, о которых не слышали уши, которые даже не приходили на ум человеку. Ещё одним проявлением Его абсолютного богатства является отсутствие у Него жены и детей. Никто не разделяет с Ним божественной власти, и Он не нуж- дается в покровителе, избавляющем от унижения. Он богат и самодостаточен благодаря Своим совершенным качествам, и Он — Тот, Кто избавляет творения от нужды. Одним словом, Аллах самодостаточен и обладает всесторонним и абсолютным богатством, вследствие чего Он ни в чём не нуждается. Он дарует достаток всем творениям в самом широком смысле и одаряет особым достатком Своих избранников, наполняя их сердца божественным знанием и верой." };

            ListOfNames["Мудрый"] = new Name() { One_Name = "Мудрый", Description = "Всевышний сказал: Он — Одолевающий и нахо­дится над Своими рабами. Он — Мудрый, Ведающий. Сура 6 «Скот», аят 18. Это значит, что Он обладает безупречной мудростью и совершенной властью. Мудрый — Тот, Кто обладает всесторонними знаниями о сути событий и их исходе, последствиях, Кто заслуживает щедрой похвалы, обладает совершенным могуществом и необъятным милосердием. Он всё расставляет по местам, творит только то, что заслуживает быть, и отдаёт только подобающие приказы. В Его мудрости нельзя усомниться, и её нельзя подвергнуть критике. Следует знать, что божественная мудрость проявляется в двух формах. Первая — мудрость в творении. Аллах создал всё сущее с истиной и ради неё. Истина была целью появления творений на свет. Он установил во Вселенной прекрасный порядок и придал каждому существу надлежащий вид. Более того, Он позаботился о каждом органе живых существ и каждой части Его творений, и ни в одном из них нельзя усмотреть несовершенство или изъян. И если даже лучшие умы всех времён и народов объединятся, чтобы придумать нечто подобное творению Милостивого или хотя бы нечто близкое к этому прекрасному порядку и изумительному совершенству, у них ничего не выйдет. Они не способны на такое, и самые мудрые и рассудительные из мужей довольствуются лишь тем, что постигают некоторые из Божьих законов. Они могут видеть лишь часть того изящества и совершенства, которое заложено в творении Аллаха. Всё это вытекает с полной очевидностью из Его величия и совершенства Его качеств, из Его многочисленных творений и велений. Поэтому Он бросил вызов рабам, приказав им внимательно наблюдать за Его творением, выискивая в нём изъяны и недостатки. Он же сообщил, что человек скорее устанет наблюдать за Божьими созданиями, чем найдёт в них то, к чему можно придраться. Вторая — мудрость в законах и повелениях. Всевышний установил законы, ниспослал Писания и отправил посланников, чтобы рабы познали Его и поклонялись Ему. Может ли быть более мудрая цель? Может ли быть большая милость? Познание Всевышнего Аллаха, поклонение Ему одному, не приобщая сотоварищей к Нему, искреннее служение Ему, возможность благодарить и восхвалять Его — лучшее и самое славное из того, чем Аллах наделил Своих рабов. Это — наивысшее счастье и радость для человеческих сердец и душ. Вместе с тем это — единственный способ обрести вечное счастье и бесконечное блаженство. Эта великая цель — основа всякого благополучия и наивысшее удовольствие. Ради неё было создано всё сущее, ради неё были сотворены Рай и Ад, и ради неё будет вершиться справедливое возмездие. И если бы она была единственной целью ниспослания Божьих приказов и законов, то этого было бы вполне достаточно. Шариат Аллаха и Его религия преисполнены всякого блага. Его повествования наполняют сердца знаниями, убеждённостью и верой. Правильные убеждения выправляют кривизну человеческих душ, воплощаются в прекрасные нравственные качества и праведные деяния, помогают следовать прямым путём. Приказы и запреты Аллаха исполнены высшей мудрости, они служат исправлению духовного и материального мира, ибо Господь приказывает только то, что приносит одну только пользу или приносит больше пользы, чем вреда. А Его запреты распространяются только на то, что приносит один только вред либо приносит больше вреда, чем пользы. Мудрость мусульманского Шариата не ограничивается тем, что он нацелен на оздоровление душ, исправление нравов и поступков, а также следование прямым путём. Напротив, Шариат содержит законы, необходимые для приведения в порядок и мирских дел. Более того, подлинного благополучия на земле можно достичь, только следуя предписаниям истинной религии, принесённой Пророком Мухаммадом (да благословит его Аллах и приветствует). Это очевидно для каждого благоразумного человека, ибо последователи Мухаммада (да благословит его Аллах и приветствует) жили в полном благополучии, пока они руководствовались основными и второстепенными предписаниями этой религии, прислушивались ко всем её наставлениям и указаниям. Когда же они отклонились от этого пути и отказались от выполнения многих из этих канонов, когда они перестали руководствоваться высоким учением ислама, их мирская жизнь тоже пришла в упадок. Посмотри на другие народы, обладающие могучей силой и добившиеся изумительных успехов в цивилизационном развитии. Они лишены живительного духа веры, её милости и справедливости, и поэтому своими действиями они приносят больше вреда, чем пользы. Их учёные, правители и политики не способны предотвратить распространения зла, которое несёт с собой их развитие, и они не смогут справиться с такой задачей, пока не исправят своего положения. Вот почему божественная мудрость требовала, чтобы принесённая Мухаммадом (да благословит его Аллах и приветствует) религия и Священный Коран были ярчайшими свидетельствами его правдивости и справедливости его учения. Одним словом, Мудрый — Тот, Чья мудрость проявляется в Его творениях и велениях, каждое из которых совершенно и прекрасно. Он обладает тремя формами власти, которыми не обладает никто другой: вершит судьбу рабов по законам Шариата, предопределения и справедливого возмездия. Следует различать между законами предопределения и Шариата. Первые связаны с тем, что Господь предопределил и сотворил. В соответствии с ними происходит только то, что пожелал Аллах, а то, что не угодно Ему, не происходит. А законы Шариата связаны с тем, что Аллах приказал. Творения обязательно подчиняются либо одной группе этих законов, либо им обеим. Тот, кто совершает богоугодные поступки, подчиняется как законам предопределения, так и законам Шариата. Тот же, кто поступает иначе, попадает под законы предопределения. Его судьба была предопределена и предрешена, но он не следовал законам Шариата, отказавшись от того, что угодно Аллаху и нравится Ему. Таким образом, добро и зло, благодеяния и грехи — всё это происходит строго в соответствии с Божьим предопределением. А те из этих поступков, которые угодны Аллаху и нравятся Ему, также соответствуют законам Его Шариата и всему, что вытекает из них." };

            ListOfNames["Выдержанный"] = new Name() { One_Name = "Выдержанный", Description = "Всевышний сказал: Знайте, что Аллаху из­вестно о том, что в ва­ших душах. Остерегайтесь Его и знайте, что Аллах — Прощающий, Выдержан­ный. Сура 2 «Корова», аят 235. Выдержанный — Тот, Кто одаряет творения зримыми и незримыми благами, несмотря на их ослушание и многочисленные ошибки. Он не спешит наказывать грешников за ослушание и предоставляет им отсрочку, чтобы они могли раскаяться и вернуться на прямой путь. Аллах обладает совершенной выдержкой, которая распространяется даже на неверующих, нечестивцев и грешников. Он предоставляет им шанс покаяться и не торопится с их наказанием. Если бы Он захотел, то подверг бы их лютой каре сразу после того, как они совершили свой грех, потому что грехи навлекают на человека скорое возмездие в его разных проявлениях. Однако выдержка Пречистого Господа требует того, чтобы им была предоставлена отсрочка. В Коране сказано: Если бы Аллах стал на­казывать людей за то, что они приобрета­ют, то Он не оставил бы на поверхности [земли] ни единого живого сущест­ва, но Он предоставля­ ет им отсрочку до назна­ченного срока. Их срок настанет, и Аллах видит Своих рабов. Сура 35 «Творец», аят 45. Если бы Аллах стал нака­зывать людей за их несправедливость, то не оста­вил бы на земле ни одного живого существа. Одна­ко Он предоставляет им отсрочку до назначенного срока. Когда же наступит их срок, они не смогут от­далить или приблизить его даже на час. Сура 16 «Пчёлы», аят 61" };

            ListOfNames["Снисходительный, Прощающий, Всепрощающий"] = new Name() { One_Name = "Снисходительный, Прощающий, Всепрощающий", Description = "Всевышний сказал: Воистину, Аллах — Снисходительный, Прощаю­щий. Сура 22 «Хадж», аят 60. Снисходительный, Прощающий — Тот, Чьим неотъемлемым качеством во все времена остаётся снисходительность и всепрощение. Все существа нуждаются в Его снисходительности и прощении, милости и великодушии. Он обещал простить и помиловать тех, кто своими деяниями заслужит такое отношение. Всевышний сказал: Воистину, Я прощаю тех, кто раскаялся, уверовал, стал поступать праведно, а потом последовал пря­мым путём. Сура 20 «Та, ха», аят 82. Снисходительный — Тот, Кто обладает необъятным прощением. Он отпускает рабам грехи, особенно если они совершают поступки, помогающие им заслужить прощение, — например, просят об этом и раскаиваются, укрепляют веру и совершают праведные дела. Пречистый Аллах принимает покаяния и прощает прегрешения. Он — Снисходительный. Он любит прощение и любит, когда люди стремятся заслужить Его прощение. Он любит, когда они совершают богоугодные поступки и делают добро Его рабам. Его прощение настолько совершенно, что Он милует даже тех, кто совершил великие преступления, если они покаялись и вернулись на прямой путь. Проявлением Его всепрощения является то, что обращение в ислам смывает любые большие и малые грехи, совершённые прежде. Всевышний сказал: Скажи Моим рабам, ко­торые излишествовали во вред самим себе: «Не от­чаивайтесь в милости Ал­лаха. Воистину, Аллах про­ щает грехи полностью, ибо Он — Прощающий, Милосердный». Сура 39 «Толпы», аят 53. В хадисе сообщается, что Аллах сказал: «О сын Адама, если ты явишься ко Мне с грехами величиной с Землю и предстанешь предо Мной, никого не приобщая в сотоварищи ко Мне, то Я дарую тебе столько же прощения» [Тирмизи, т. 4, с. 122; хадис хороший согласно аль-Альбани: Джами‘ ас-сагир, т. 5, с. 548]. В Коране также сказано: «Воистину, твой Господь обладает необъятным прощением» (сура 53 «Звезда», аят 32). Аллах распахнул врата для обретения прощения путём покаяния и молитв о прощении, укрепления веры и совершения праведных деяний, доброго и снисходительного отношения к рабам Аллаха, стремления к Его милости и надежды на Него. Всё это — лишь некоторые из поступков, приближающих нас к Его великому прощению." };

            ListOfNames["Принимающий покаяния"] = new Name() { One_Name = "Принимающий покаяния", Description = "Всевышний сказал: Неужели они не знают, что Аллах принимает по­каяния от Своих рабов и принимает пожертвова­ния, что Аллах — Прини­мающий покаяния, Милосердный? Сура 9 «Покаяние», аят 104. Принимающий покаяния — Тот, Кто не перестаёт принимать покаяния и прощать грехи кающимся. Если человек обращается к Аллаху с искренним покаянием, то Он непременно принимает его покаяние. Он прощает кающихся вначале, когда помогает им принести покаяние и направляет их сердца к Нему. Он прощает их после их покаяния, когда принимает его и отпускает им прегрешения. Таким образом, данное качество Господа проявляется в двух формах: а) Он вселяет в сердца рабов желание вернуться к Нему и покаяться, выполнив все обязательные условия, то есть прекратив грешить, искренне пожалев о содеянном, твёрдо решив не возвращаться к этому и совершив вместо него добрые дела; б) Он принимает искренние покаяния рабов, внимая их просьбам, и стирает совершённые ими ранее грехи" };

            ListOfNames["Наблюдающий"] = new Name() { One_Name = "Наблюдающий", Description = "  Он — Тот, Кто ведает обо всём, что кроется в сердцах, и сохраняет всё, что приобретает человек: «Воистину, Аллах наблюдает за вами» (сура 4 «Женщины», аят 1). Наблюдающий — Тот, Кто оберегает Свои творения, располагает их в самом прекрасном порядке и управляет ими совершенным образом." };

            ListOfNames["Свидетель"] = new Name() { One_Name = "Свидетель", Description = "Свидетель — Тот, Кто видит всё сущее, слышит любые громкие и тихие звуки, видит все большие и маленькие существа; Кто объемлет знанием всё сущее и свидетельствует за и против Своих рабов обо всём, что они совершили. Шейх ‘Абд-ар-Рахман ас-Са‘ди писал [Хакк аль-вадых аль-мубин, с. 58–59]: Ар-Ракиб (Наблюдающий) и Аш-Шахид (Свидетель) — имена близкие по смыслу, каждое из них указывает на то, что Аллах слышит любые звуки и видит всё сущее. Ему известно всё явное и сокрытое. Он читает мысли творений и следит за их взорами, не говоря уже об их очевидных поступках. Всевышний сказал: «Воистину, Аллах наблюда­ет за вами» (4:1); «Аллах — Свидетель всякой ве­щи» (58:6). Вот почему осознание того, что Аллах наблюдает за человеком, является одним из самых достойных поступков, совершаемых сердцем. Это есть форма поклонения Аллаху посредством Его имён Ар-Ракиб (Наблюдающий) и Аш-Шахид (Свидетель). Если раб понимает, что Аллах знает о каждом из его явных и сокрытых поступков, если он вспоминает об этом в любом состоянии, то он оберегает свою душу от любых мыслей и раздумий, ненавистных Аллаху, а также избегает любых слов и поступков, вызывающих Его гнев. Он достигает ступени ихсана, поклоняясь Аллаху так, словно он видит Его, и если даже он не видит Его, то Аллах видит его. Таким образом, если Аллах наблюдает за любыми мелкими и сокрытыми вещами, ведая о сокровенных тайнах и намерениях, то Ему тем более известно обо всём явном и очевидном, то есть о поступках, которые рабы совершают телом." };


            ListOfNames["Хранитель"] = new Name() { One_Name = "Хранитель", Description = "Всевышний сказал: Воистину, Господь мой — Хранитель всякой вещи. Сура 11 «Худ», аят 57. Это прекрасное имя имеет два значения. Первое говорит о том, что Аллах сохраняет все добрые и злые деяния, праведные поступки и грехи, совершённые Его рабами. Ему известно обо всех поступках, совершённых ими явно и тайно, и все они записаны в Хранимой скрижали. Кроме того, Он приставил к людям ангелов — благородных писцов, которым ведомо об их поступках. Таким образом, из этого прекрасного имени следует, что Аллах обладает всесторонним знанием о внешнем и духовном состоянии Его рабов. Всё это записано в Хранимой скрижали и свитках, находящихся в руках ангелов. Он ведает о степени тяжести и превосходства человеческих поступков, их совершенстве и недостатках, а также размерах той награды и того наказания, которое они заслужили. А потом, когда придёт время, Он воздаст каждому из них либо по милости, либо по справедливости. Второе свидетельствует о том, что Аллах оберегает рабов от всего, что им ненавистно. Подобная забота бывает общей и особой. Общая забота распространяется на все творения: Аллах облегчает им поиски пропитания, оберегает их жилища, наставляет их на путь, ведущий к Нему, и указывает на то, что приносит им пользу. По этому поводу сказано: «Господь наш — Тот, Кто при­дал облик всякой вещи, а затем указал всему путь» (сура 20 «Та, ха», аят 50), то есть указал путь каждому творению к тому, что предписано для него, в чём оно нуждается и без чего оно не в состоянии прожить. Он научил рабов тому, как раздобыть пропитание и как удовлетворять половое влечение. Он оберегает их от многочисленных несчастий и злоключений, и подобная забота распространяется не только на праведников, но и на грешников, а также на животных и прочие творения. Аллах удерживает небеса и землю от сотрясений, поддерживает творения благодаря Своим дарам. К людям Он приставил ангелов- хранителей, которые оберегают их от несчастных случаев по воле Аллаха, и если бы не божественная опека, то с ними непременно приключилось бы много несчастий. Особую заботу Аллах проявляет о Его любимых рабах. Он оберегает их от любых сомнений, искушений и страстей, которые могут навредить их вере и пошатнуть их убеждённость. Он помогает им благополучно избежать всего этого и защищает их от врагов из числа людей и джиннов. Он одаряет их победой над ними, отводя от них козни неверующих. Всевышний сказал: «Во­истину, Аллах оберегает тех, кто уверовал» (сура 22 «Хадж», аят 38). Это касается всего, что может навредить их духовному и мирскому благополучию. И чем сильнее вера раба, тем лучше оберегает Его Аллах по милости Своей. В хадисе сказано: «Помни об Аллахе, и Он защитит тебя» [Тирмизи, т. 4, с. 667; хадис достоверный согласно аль- Альбани: Джами‘ ас-сагир, т. 6, с. 300]. Это значит: выполняй Его приказы, остерегайся нарушать Его запреты, не преступай Его ограничения, и Он непременно защитит тебя и твою веру, твоё имущество и твоих детей, сохранив для тебя все дарованные Им блага." };

            ListOfNames["Проницательный, Добрый"] = new Name() { One_Name = "Проницательный, Добрый", Description = "Всевышний сказал: Аллах добр к Своим рабам и наделяет уделом, кого пожелает. Он — Всесиль­ный, Могущественный. Сура 42 «Совет», аят 19. Взоры не могут постичь Его, а Он постигает взоры. Он — Проницательный [или Добрый], Сведущий. Сура 6 «Скот», аят 103. Проницательный, Добрый — Тот, Кто нежно обращается со Своими рабами во всех вопросах, касающихся их духовного мира и связанных с ними внешне. Он ведёт их и приближает к ним то, что приносит им пользу, хотя они даже не ощущают Его руководства. Всё это — следствие Его знаний, великодушия и милосердия, и поэтому прекрасное имя Аль-Лятыф (Проницательный, Добрый) имеет два значения. Первое указывает на то, что Аллах объемлет знанием всё сущее, ведая о сокровенных тайнах и утаённых помыслах. Ему известно то, что кроется в сердцах, и то, что сокрыто от взоров. Он знает обо всём сущем в мельчайших подробностях. Второе значение свидетельствует о том, что Аллах добр к Его любимым рабам. Он желает довести до конца Своё благодеяние по отношению к ним, одарить их по Своей щедрости, поселить их в вышних горницах, облегчить им путь к наилегчайшему и отдалить их от всего обременительного. Он подвергает их всевозможным нелёгким испытаниям, которые ненавистны им, но даже они приносят им одну только пользу. Это — путь к счастью, ибо даже пророки подвергались испытаниям: их народы причиняли им страдания, и им было велено бороться с ними на пути Аллаха. В истории пророка Юсуфа сообщается о том, какие трудности выпали на его долю, но Аллах был добр к нему и проявил изысканную заботу о нём: исход предписанных ему испытаний оказался благим как в этом мире, так и в Последней жизни. Таким же образом Он подвергает испытанию Своих любимых рабов, чтобы, в конечном итоге, они добились исполнения своих заветных желаний. Люди не способны прочувствовать доброту и великодушие Аллаха, равно как и не способны представить себе их. Сколько желанных земных благ, таких как власть или правление, Аллах отдаляет от человека из сострадания к нему, чтобы они не навредили его религии. Но это огорчает людей, потому что они невежественны и не познали своего Господа. Если бы они знали, что припасено для них, но сокрыто от их взоров, то воздали бы хвалу Аллаху и принялись благодарить Его. Поистине, Он снисходителен и милосерден к Своим рабам, добр и обходителен со Своими любимцами. В одной из дошедших до нас молитв говорится: «О Аллах, пусть то, чем Ты одарил меня и что нравится мне, придаст мне силы для совершения того, что Ты любишь, и пусть то, чего Ты лишил меня и что нравится мне, не мешает мне совершать то, что Ты любишь» [Тирмизи, т. 5, с. 523; хадис хороший согласно ‘Абд-аль-Кадиру аль-Арнауту: Ибн аль-Асир 2, т. 4, с. 341]." };

            ListOfNames["Близкий"] = new Name() { One_Name = "Близкий", Description = "Всевышний сказал: Он сотворил вас из земли и поселил вас на ней. Про­сите прощения у Него, а за­ тем покайтесь перед Ним. Воистину, мой Господь — Близкий, Отзывчивый. Сура 11 «Худ», аят 61. Близкий — одно из имён Аллаха, и Его близость к творениям бывает двух видов. Близость в общем смысле проявляется в Его знании обо всём сущем. В этом смысле Он ближе к человеку, чем его яремная вена. Так же трактуется присутствие Аллаха рядом со всеми творениями. Особая близость проявляется по отношению к тем, кто поклоняется Аллаху, взывает к Нему и любит Его. Из такой близости вытекает любовь к этим людям и поддержка их: Аллах оказывает им поддержку, когда они трудятся и отдыхают, внимает их молитвам, принимает их поклонение и вознаграждает за него. В Благородном Коране сказано: «Если Мои рабы спросят тебя обо Мне, то ведь Я близок и отвечаю на зов молящего­ся, когда он взывает ко Мне» (сура 2 «Корова», аят 186). Если человек осознает истинный смысл всеобщей и особой близости Аллаха, то ему станет ясно, что Его близость к творениям не противоречит тому, что Он находится над Троном. Пречистый Аллах — Тот, Кто возвышен, несмотря на Его близость, и близок, несмотря на Его высокое положение." };

            ListOfNames["Отзывчивый"] = new Name() { One_Name = "Отзывчивый", Description = "Аллах отзывается на молитвы рабов, внимает их просьбам и принимает их поклонение. Это божественное качество проявляется в двух формах. В широком смысле слова Аллах внимает всем, кто обращается к Нему с просьбами или поклоняется Ему. Ваш Господь сказал: «Взы­вайте ко Мне, и Я отвечу вам». Сура 40 «Прощающий», аят 60. Обращаясь к Аллаху с просьбами, люди молят Его одарить их благами и уберечь от зла. Так поступают не только благочестивые праведники, но и грешники, и Аллах внимает молитвам каждого из них, принимая во внимание их положение и руководствуясь Своей мудростью. Это указывает на щедрость нашего Покровителя и то, что Его доброго отношения не лишены ни праведники, ни грешники. Поэтому, если молитва была принята, это не означает, что сам человек ведёт праведный образ жизни, за исключением тех случаев, когда просьбе сопутствуют праведные деяния, свидетельствующие об искренности человека и том, что правда на его стороне. Так было, например, когда пророки просили Аллаха за своих соплеменников или же, наоборот, проклинали их. Аллах внимал их молитвам, и это было подтверждением правдивости того, к чему они призывали, и указывало на их высокое положение перед Господом. Именно поэтому мусульмане и прочие современники Пророка (да благословит его Аллах и приветствует) неоднократно становились свидетелями того, как принимаются его молитвы. Это доказывало истинность его миссии и лишний раз подтверждало его правдивость. То же самое можно сказать о том, как Аллах принимает мольбы многих праведников и приближённых рабов — всё это указывает на их высокое положение перед Ним. Существуют и особые проявления того, как Аллах отзывается на мольбы рабов. Причины на это могут быть самые разные. Например, Аллах внимает мольбе раба, который попал в тяжёлое положение и испытывает сильную печаль: «Кто отвечает на мольбу нуждающегося, ког­да он взывает к Нему?» (сура 27 «Муравьи», аят 62). Че- ловек, попавший в сложную ситуацию, остро нуждается в помощи Аллаха, смиряется пред Ним и не возлагает никаких надежд на творения. В то же время милость Аллаха велика и объемлет все творения в зависимости от того, насколько они нуждаются в ней. И, конечно же, Он не лишает Своей милости того, кто испытывает крайнюю нужду. К обстоятельствам, способствующим скорейшему принятию молитв, также относятся долгое путешествие и попытки приблизиться к Нему посредством того, что угодно Ему, то есть посредством Его имён, качеств и дарованных людям благ. Помимо того, Аллах внимает мольбам больных и угнетённых, постящихся и родителей, молящихся за своих детей. Есть и определённые благословенные часы и обстоятельства, когда Господь не отвергает мольбу, например, по окончании обязательных намазов, перед наступлением рассвета, между азаном и икаматом, во время азана или дождя, в разгар битвы и т.д. Воистину, мой Господь — Близкий, Отзывчивый. Сура 11 «Худ», аят 61" };

            ListOfNames["Любящий, Любимый"] = new Name() { One_Name = "Любящий, Любимый", Description = "Всевышний сказал: Попросите прощения у своего Господа, а затем покайтесь перед Ним. Воистину, мой Господь — Милосердный, Любящий (или Любимый). Сура 11 «Худ», аят 90. Он — Прощающий, Любящий (или Любимый). Сура 85 «Созвездия Зодиака», аят 14. Это прекрасное имя образовано от слова вудд — ود — ‘чистая любовь’. Аллах любит пророков, ангелов, верующих рабов, и они тоже любят Его. Более того, они никого и ничто не любят так сильно, как Аллаха. Любовь избранных рабов к Нему не сравнима с любой другой любовью — ни в основе своей, ни в своих проявлениях, ни по силе любви. Любовь к Аллаху в сердце каждого раба должна опережать и превосходить любовь ко всему остальному. Более того, любовь ко всему остальному должна находиться в зависимости от любви к Нему. Любовь к Аллаху — основа всех праведных поступков, из неё исходят все формы поклонения душой и телом. Это чувство возникает по милости Господа. Человек не приобретает его благодаря своим способностям или своей силе. Аллах помещает любовь к Нему в сердце того раба, которого Он возлюбил. Потом Он помогает ему проникнуться любовью к Нему, после чего вознаграждает его особой любовью. Всё это — лишь результат доброго отношения Аллаха. Он создаёт причины и следствия. Он не ждёт отдачи, а любит бескорыстно — любит тех из Своих рабов, которые благодарны, за то, что они благодарны. Таким образом, пользу от всего этого получают только сами рабы. Благословен же Тот, Кто поместил любовь в сердца правоверных, а потом увеличил и усилил её настолько, что любовь ко всему остальному рядом с ней померкла, любые несчастья стали казаться лёгкими, а самые трудные богоугодные поступки стали доставлять удовольствие. Всё это принесло им множество разных благ, величайшим из которых является любовь к Аллаху, обретение Его довольства и наслаждение близостью к Нему. Таким образом, любовь раба к Господу окружена двумя проявлениями любви Самого Господа. Благодаря первоначальной любви Аллаха в человеке просыпается ответная любовь к Нему. За это Аллах благодарит Своего раба, Его любовь к нему усиливается, и тот попадает в число Божьих избранников. Иными словами, любовь к Господу — величайшая цель существования человека, и лучшим способом обрести её являются частое поминание и восхваление Аллаха, раскаяние перед Ним и сильное упование на Него. Помимо того, необходимо стремление приблизиться к Нему посредством выполнения обязательных и добровольных предписаний, подлинная искренность во всех словах и поступках, а также повиновение Пророку (да благословит его Аллах и приветствует) душой и телом. Всевышний сказал: Скажи: «Если вы лю­бите Аллаха, то следуй­те за мной, и тогда Аллах возлюбит вас». Сура 3 «Семейство ‘Имрана», аят 31" };

            ListOfNames["Признательный, Благодарный"] = new Name() { One_Name = "Признательный, Благодарный", Description = "Всевышний сказал: А если кто доброволь­но совершает доброе дело, то ведь Аллах — Призна­тельный, Знающий. Сура 2 «Корова», аят 158. Если вы одолжите Ал­лаху прекрасный заём, то Он приумножит его для вас и простит вас. Ал­лах — Благодарный, Вы­держанный. Сура 64 «Взаимное обделение», аят 17. Аллах — Признательный, Знающий. Сура 4 «Женщины», аят 147. Всевышний Аллах не даёт пропасть стараниям тех, кто трудится искренне ради Него. Напротив, Он многократно приумножает награду за такие благодеяния. Он не теряет награды тех, кто поступает хорошо. В Коране и Сунне содержатся указания на то, что награда за одно доброе дело приумножается многократно — в десять раз, в семьсот раз, и даже больше. Так Аллах благодарит Своих рабов! Он видит всё, что испытывают ради Него праведники, и с лихвой вознаграждает тех, кто трудится или отказывается от чего-нибудь ради Него. Он вдохновляет верующих на богоугодные поступки, а потом благодарит их за это, одаряя щедрыми благами, которых не видывал взор, о которых не слышали уши и которые даже не приходили на ум человеку. Он никому не обязан всё это делать, но по милости и щедрости Своей Аллах обязал Себя поступать именно так. Никто сверху не приказывает Ему относится к творениям таким образом: Его не спросят за то, что Он совершает, а они будут спрошены. Сура 21 «Пророки», аят 23. Он не обязан вознаграждать покорных рабов и наказывать ослушников. Награда — всего лишь проявление Его милости и добродетели, а наказание — проявление Его справедливости и мудрости. Однако Пречистый Аллах вменил Себе в обязанность определённое отношение к рабам, которое стало Его долгом. Таково обещание Того, Кто не нарушает обещаний. В Коране сказано: Ваш Господь предписал Себе быть Милосердным, и если кто из вас сотво­рит зло по своему неве­жеству, а затем раскается и станет совершать пра­ведные деяния, то ведь Он — Прощающий, Ми­лосердный. Сура 6 «Скот», аят 54. Нашим долгом было помогать верующим. Сура 30 «Ромеи», аят 47. Приверженцы Сунны считают, что Аллах ничего не должен рабам, и всё, что названо долгом Аллаха, было вменено Им Себе в обязанность. Вот почему ни один поступок, совершённый искренне ради Аллаха и в соответствии с предписаниями Пророка (да благословит его Аллах и приветствует), не пропадает у Него. Это и есть два основных условия принятия деяний. Таким образом, если на долю раба выпадает благо или ему удаётся избежать какого-либо несчастья, то это есть милость и великодушный дар Всевышнего Аллаха. Если Он оказывает людям милость, то делает им добро; если же Он подвергает их наказанию, то поступает мудро и справедливо. В любом из этих случаев Он заслуживает всяческой похвалы за Своё решение!" };

            ListOfNames["Господин, Самодостаточный"] = new Name() { One_Name = "Господин, Самодостаточный", Description = "Всевышний сказал: Скажи: «Он — Аллах Еди­ный, Аллах Самодостаточный. Сура 112 «Очищение веры», аяты 1–2. Пророк (да благословит его Аллах и приветствует) сказал: «Господин — это Всеблагой и Всевышний Аллах» [Абу Давуд, т. 4, с. 254; Ахмад, т. 3, с. 241; там же, т. 4, с. 25; хадис достоверный согласно аль-Арнауту: Фатх Аль-Маджид, с. 613]. Слово سيد�ال’ господин’ означает также ‘хозяин’, ‘владелец’, ‘глава’, ‘муж’. Так называют славных, достойных, благородных, выдержанных людей, помогающих другим в трудную минуту. Великий и Могучий Аллах — Господин, правящий творениями и покровительствующий им. Его господство истинно и пóлно, а все творения — всего лишь Его рабы. Это не означает того, что отдельных людей нельзя назвать господами, однако господство Всеблагого и Всевышнего Творца отличается от господства слабых творений. Слово الصمد’ самодостаточный’ обладает множеством значений, соответствующих различным интерпретациям этого прекрасного имени. Это — Тот, к Которому устремляются все творения, унижаясь перед Ним и испытывая бесконечную нужду в Нём. Это — Тот, Кто обладает совершенными знаниями и мудростью, сдержанностью и могуществом, величием и милосердием. Одним словом, Тот, Чьи качества совершенны и безупречны, к Кому устремляются творения для удовлетворения всех своих нужд. Он — Господин, Чьё главенство совершенно; Зна- ющий, Чьё знание совершенно; Выдержанный, Чья выдержка совершенна; Богатый, Чьё богатство совершенно; Могущественный, Чьё могущество совершенно; Славный, Чья слава совершенна; Великий, Чьё величие совершенно; Мудрый, Чья мудрость совершенна. Он — Тот, Чьи проявления славы и главенства безупречны. Он — Великий и Могучий Аллах. Этим качеством не обладает никто другой. Нет никого равного Ему, и нет никого подобного Ему. Пречист Аллах, Единственный, Всемогущий!" };

            ListOfNames["Одолевающий, Всемогущий"] = new Name() { One_Name = "Одолевающий, Всемогущий", Description = "Всевышний сказал: Скажи: «Аллах — Творец всякой вещи. Он — Единст­венный, Всемогущий». Сура 13 «Гром», аят 16. В тот день, когда они поя­вятся [на поверхности земли после воскрешения], ничто о них не скроется от Аллаха. Кому же при­надлежит власть сегодня?  Аллаху, Единственному, Всемогущему. Сура 40 «Прощающий», аят 16. Он — Одолевающий и нахо­дится над Своими рабами. Он — Мудрый, Ведающий. Сура 6 «Скот», аят 18. Одолевающий, Всемогущий — Тот, Кому подвластно всё сущее и подчинены все творения. Пред Его могуществом и волей преклоняется всё, что есть на небесах и земле. Всё движется и покоится только с Его дозволения. Происходит только то, что угодно Ему, а то, что не угодно Ему, не может произойти. Все творения беспомощны и нуждаются в Аллахе. Они не способны ни помочь себе, ни навредить. Способность подчинять подразумевает обладание Им такими атрибутами, как жизнь, могущество и способность на всё, поскольку подчинение творений и власть над ними не были бы полными, ес- ли бы Творец не обладал этими атрибутами. Таким образом, именно эти три качества делают власть полной и совершенной" };

            ListOfNames["Могучий, Выправляющий, Подчиняющий"] = new Name() { One_Name = "Могучий, Выправляющий, Подчиняющий", Description = "Всевышний сказал: Он — Аллах, и нет бо­жества, кроме Него, Ца­ря, Святого, Пречистого, Оберегающего, Храните­ля, Могущественного, Мо­гучего. Сура 59 «Сбор», аят 23. Это прекрасное имя несёт в себе три значения. 1. Аллах поддерживает слабых и выправляет сердца тех, кто унижается ради Него. Он помогает тем, кто надломился, избавляет от нужды нуждающихся и облегчает участь тех, кто сильно стеснён обстоятельствами. Он оказывает поддержку тем, кого постигло несчастье, помогая им проявить стойкость и терпение, и, если они справляются со своими обязанностями, Он возмещает их потери величайшей наградой. Его особой поддержки удостаиваются те, кто покорился Его величию и могуществу. Он выправляет сердца любящих Его, щедро одаряя их всевозможными благами, знаниями и верой. Если человек смиряется пред Ним, то помощь и поддержка Аллаха близка. О ней просит молящийся, когда говорит: «О Аллах, выправи моё положение», то есть приведи в порядок мои дела и убереги меня от всего неприятного. 2. Ему подчинено всё сущее, и все творения смиряются пред Ним, покоряются Его воле. 3. Он возвышен над всеми творениями. Таким образом, это прекрасное имя включает в себя значения имён Снисходительный, Всемогущий и Возвышенный. Иногда оно может указывать на четвёртое значение — возвышающийся над любыми пороками и недостатками, находящийся превыше того, чтобы иметь равных, соперников или партнёров в обладании божественными особенностями и правами." };

            ListOfNames["Требующий отчёта"] = new Name() { One_Name = "Требующий отчёта", Description = "Всевышний сказал: Но довольно того, что Ал­лах ведёт счёт. Сура 4 «Женщины», аят 6 Он один принимает реше­ние, и Он — Самый быст­рый в расчёте. Сура 6 «Скот», аят 62. Это прекрасное имя включает в себя несколько значений. Во-первых, оно указывает на то, что Аллах обеспе- чивает рабов всем, в чём они нуждаются для обретения мирских и духовных благ, а также избавления от вреда. Во-вторых, в более узком смысле оно означает, что Аллах проявляет особую заботу о тех рабах, которые блюдут благочестие и уповают на Него, приводя в порядок их духовный мир и земные дела. В-третьих, Аллах сохраняет добрые и злые деяния Его рабов, чтобы призвать их к ответу. И тогда праведники получат добро, а грешникам будет воздано злом за зло. Всевышний сказал: «О Пророк! Тебе и твоим верующим последователям довольно Аллаха» (сура 8 «Трофеи», аят 64), то есть Он защитит и тебя, и твоих последователей. Степень такой защиты зависит от того, насколько исправно он душой и телом выполняет приказы Посланника (да благословит его Аллах и приветствует) и поклоняется Всевышнему" };

            ListOfNames["Наставляющий"] = new Name() { One_Name = "Наставляющий", Description = "Коран гласит: Довольно того, что твой Господь наставляет на пря­мой путь и помогает. Сура 25 «Различение», аят 31. Воистину, Аллах настав­ляет верующих на прямой путь. Сура 22 «Хадж», аят 54. Наставляющий — Тот, Кто указывает Своим рабам на всё, что может принести им пользу и уберечь их от вреда; Кто обучает их тому, чего они не знают, и наставляет их на прямой путь; Кто внушает им богобоязненность и делает их сердца стремящимися к Нему и покорными Его воле. Под наставлением подразумевается доброе руководство, поучение. Руководство Аллаха для людей имеет четыре формы. 1. Руководство, данное всем, кто несёт ответственность за свои поступки. Здесь имеются в виду разум, естество (фитра) и необходимые знания, которые Аллах даровал всем людям в той или иной степени. Всевышний сказал: «Господь наш — Тот, Кто придал облик всякой вещи, а затем указал всему путь» (сура 20 «Та, ха», аят 50). 2. Руководство, данное людям через пророков, ниспосланное в Коране и т. д. Оно имеется в виду в аяте: «Мы создали среди них имамов, которые вели остальных по Нашему повелению прямым путём» (сура 32 «Земной поклон», аят 24). 3. Особое руководство (хидайат ат-тауфик), посредством которого Аллах наставляет тех, кто следует прямым путём. Оно упоминается в Его словах: «Тем, кто следует прямым путём, Он уве­личивает их приверженность прямому пути» (сура 47 «Мухаммад», аят 17); «Сердце того, кто уве­ровал в Аллаха, Он наставляет на прямой путь» (сура 64 «Взаимное обделение», аят 11); «Воис­тину, тех, которые уверовали и совершали пра­ведные деяния, Господь поведёт верным путём за их веру» (сура 10 «Юнус», аят 9); «А тех, кото­рые борются ради Нас, Мы непременно поведём Нашими путями» (сура 29 «Паук», аят 69). 4. Руководство в Последней жизни, указывающее путь к Раю. Оно подчёркивается в аятах: «Он ни­когда не сделает тщетными деяния тех, кто был убит на пути Аллаха. Он поведёт их прямым путём, исправит их положение» (сура 47 «Мухаммад», аяты 4–5); «Мы исторгнем из их сердец зло­бу, и под ними будут протекать реки. Они скажут: “Хвала Аллаху, Который привёл нас к этому”» (сура 7 «Ограды», аят 43). Эти четыре формы связаны друг с другом, и если человек лишён первого руководства, то он не сможет получить второе. Более того, он даже не несёт ответственности за свои поступки. Кто лишён второго руководства, тот не сумеет получить третье и четвёртое руководства. Следовательно, четвёртое руководство получит только тот, кто получил предыдущие три, а третье — только тот, кому были дарованы первые два. С другой стороны, если человек получил первое руководство, это не значит, что он обязательно получит последующие. Человек не в состоянии наставить других людей на прямой путь, но он может помолиться за них и указать им на него. Об этом говорится в словах Всевышнего: «Воистину, ты указываешь на прямой путь» (сура 42 «Совет», аят 52); «Мы созда­ли среди них предводителей, которые вели остальных по Нашему повелению прямым путём» (сура 32 «Земной поклон», аят 24); «У каждого народа есть наставник» (сура 13 «Гром», аят 7), то есть тот, кто призывает на прямой путь. Об остальных формах наставления говорится в аяте: «Воистину, ты не сможешь наставить на прямой путь тех, кого возлюбил» (сура 28 «Рассказ», аят 56). Наставляющий — Тот, Чьи слова и деяния есть верное руководство. Он выводит на прямой путь тех, кто пребывает в растерянности и заблуждении. Он разъясняет им правую стезю, обучает их тому, как нужно идти по ней, и вдохновляет на это. Изданные им вселенские законы, посредством которых создаются творения и уп- равляется Вселенная, есть сущая истина, ибо они преисполнены мудрости, красоты и совершенства. Его слова, произнесённые в Писаниях и устами посланников, содержат правдивые повествования и абсолютно справедливые предписания (веления и запреты). Ничья речь не может быть правдивее и прекраснее слов Аллаха! Коран гласит: «Слово твоего Господа исполнено правды и справедливости!» (сура 6 «Скот», аят 115), и это относится к Его повелениям и запретам. Это — величайшие и самые славные наставления рабам. Более того, ничто иное не может сделать человека праведным, и если кто либо пытается встать на прямой путь, обходя их, то Аллах вводит его в заблуждение. Не будет праведным тот, кто не следует Божьему руководству. Оно позволяет обрести правильные познания (рушд ‘ильми), когда человеку становится ясна истина, когда он узнаёт ответы на основные и второстепенные вопросы, а также понимает, что приносит ему пользу, а что вредит его мирскому и духовному благополучию. Оно же позволяет творить праведные дела (рушд ‘амали), ибо оно облагораживает души, очищает сердца и указывает на наилучшие поступки и нравственные качества. Оно вдохновляет на всё прекрасное, а также предостерегает от всего низкого и скверного. Тот, кто прислушивается к этому руководству, следует прямым путём; а тот, кто отворачивается от него, впадает в заблуждение. Аллах отправил к людям посланников и ниспослал Писания, содержащее абсолютное руководство, и они не смогут оправдаться перед Аллахом собственным неведением. Скольких же заблудших Господь вывел на прямой путь по милости Своей! Скольким пребывавшим в растерянности рабам Он указал на правую стезю! И в первую очередь — тем, кто уповал на Него и от всей души молил Его о верном руководстве, зная, что никто другой не способен вывести на прямой путь. Таким образом, руководство, которого Великий и Могучий Аллах лишил нечестивцев и неверующих, является третьим и четвёртым руководствами. Иными словами, это — вдохновение, благодаря которому люди следуют прямым путём, и награда в Последней жизни, то есть в Раю. По этому поводу в Коране сказано: «Ал­лах не ведёт прямым путём несправедливых людей» (сура 2 «Корова», аят 258); «Это потому, что они пред­почли мирскую жизнь Последней жизни. Воистину, Аллах не ведёт прямым путём неверующих людей» (сура 16 «Пчёлы», аят 107). В аятах также сообщается, что Пророк (да благословит его Аллах и приветствует) и остальные люди не способны наставлять на прямой путь. Здесь имеется в виду, что они не способны наделить рабов разумом, вдохновить их на следование прямым путём или ввести в Рай. Однако это не относится к мольбе за людей и указанию на правую стезю. Всевышний сказал: «Вести их прямым путём — не твоя обязанность, ибо Аллах ведёт прямым путём, кого пожелает» (сура 2 «Корова», аят 272). Я молю Аллаха вдохновить нас на совершение того, что Он любит и что угодно Ему. Только Он заслуживает обращения за помощью и упования, и нет силы и могущества ни у кого, кроме Аллаха." };

